-- Batch 10: Questions 181-200
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES
('0a36d25f-43a9-41c2-ae5c-fa8fdd30a656', 'What is the browser support for React applications?', 'React supports IE9+ with polyfills like `es5-shim` for older browsers.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"Only Chrome and Firefox","isCorrect":false},{"id":"b","text":"IE9+ with polyfills","isCorrect":true},{"id":"c","text":"No IE support at all","isCorrect":false},{"id":"d","text":"Requires ES2020","isCorrect":false}]', NULL, 'Modern React apps require ES5+; polyfills bridge gaps in older browsers.', NULL, ARRAY[]::text[], ARRAY['react','browser-support','polyfills','ie9'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q180","original_type":"multiple-choice","topic":"Browser Compatibility","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('cade2580-7337-46fe-81ed-63578c07756b', 'What is code-splitting?', 'Code-splitting divides your app into smaller bundles loaded on demand, improving initial load time.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It reduces the initial bundle size by lazy-loading features only when needed.', NULL, ARRAY[]::text[], ARRAY['code-splitting','webpack','performance','bundling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q181","original_type":"open-ended","topic":"Code Splitting","subcategory":"Performance Optimization","sample_answers":["Code-splitting uses dynamic `import()` to create separate bundles that load at runtime, reducing initial payload.","In React, it’s commonly used with `React.lazy` and `Suspense` for route or component-based splitting."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('0940e9ea-be66-4cf2-bb76-afcb9b5ef716', 'What are Keyed Fragments?', '`<React.Fragment key={...}>` allows fragments to be used in lists by providing a unique key.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'When mapping to multiple elements, each must have a key—even if wrapped in a fragment.', NULL, ARRAY[]::text[], ARRAY['react','fragments','keys','lists'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q182","original_type":"open-ended","topic":"Fragments","subcategory":"React Basics","sample_answers":["When returning multiple elements from a `.map()`, wrap them in `<React.Fragment key={item.id}>` to satisfy React’s key requirement.","Keyed fragments avoid unnecessary wrapper divs while enabling stable reconciliation in lists."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('49d53999-49db-4f8e-8fb7-ef98dd055031', 'Does React support all HTML attributes?', 'Yes, React 16+ supports all standard and custom DOM attributes using camelCase (e.g., `className`, `tabIndex`).', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'React uses camelCase to match DOM API property names, not HTML attribute names.', NULL, ARRAY[]::text[], ARRAY['react','jsx','attributes','dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q183","original_type":"true-false","topic":"JSX","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('1152b366-9a81-4cfd-bad6-7254d9f4ad3e', 'When do component props default to true?', 'When a prop is passed without a value (e.g., `<MyInput autocomplete />`), it defaults to `true`.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'This mimics HTML behavior (e.g., `<button disabled>`), but can be confusing—explicit `={true}` is clearer.', NULL, ARRAY[]::text[], ARRAY['react','jsx','props','booleans'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q184","original_type":"open-ended","topic":"JSX","subcategory":"React Basics","sample_answers":["In JSX, `<Component hidden />` is equivalent to `<Component hidden={true} />`—the prop becomes `true`.","This matches HTML’s boolean attribute behavior but is discouraged in favor of explicit values."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('13889f8a-c025-4ce8-8a18-239487f1d7a6', 'What is Next.js and its major features?', 'Next.js is a React framework for SSR, SSG, and static apps with features like automatic code splitting and file-based routing.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"A state management library","isCorrect":false},{"id":"b","text":"A React framework for SSR/SSG with file-based routing and code splitting","isCorrect":true},{"id":"c","text":"A testing framework","isCorrect":false},{"id":"d","text":"Only for static sites","isCorrect":false}]', NULL, 'Next.js simplifies building production-ready React apps with built-in SSR, routing, and optimization.', NULL, ARRAY[]::text[], ARRAY['nextjs','ssr','react','framework'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q185","original_type":"multiple-choice","topic":"Next.js Basics","subcategory":"Next.js Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('d3542a1d-def1-48bd-8b64-76b428b644a0', 'How do you pass an event handler to a component?', 'Pass the function as a prop: `<Button onClick={handleClick} />`.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Event handlers are functions passed as props and called in response to user interactions.', NULL, ARRAY[]::text[], ARRAY['react','events','handlers','props'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q186","original_type":"open-ended","topic":"Event Handling","subcategory":"React Basics","sample_answers":["Define a function in the parent and pass it as a prop: `<Child onClick={this.handleClick} />`.","In the child, call it via `onClick={props.onClick}` on a DOM element."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"2a5901fd-e2ca-4fe3-a914-447622008d02"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('20eb08c4-b21a-41fe-85dd-0c14ada69d91', 'How to prevent a function from being called multiple times?', 'Use throttling, debouncing, or `requestAnimationFrame` to limit execution rate.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Call it inside a `setTimeout` with 0 delay","isCorrect":false},{"id":"b","text":"Use throttling, debouncing, or requestAnimationFrame","isCorrect":true},{"id":"c","text":"Disable the button permanently","isCorrect":false},{"id":"d","text":"Use `preventDefault()`","isCorrect":false}]', NULL, 'These techniques control how often a function runs in response to rapid events like scrolling or typing.', NULL, ARRAY[]::text[], ARRAY['throttle','debounce','performance','events'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q187","original_type":"multiple-choice","topic":"Debounce & Throttle","subcategory":"Debounce & Throttle","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('e7954414-4133-4c2d-9311-9f4e794cac20', 'How does JSX prevent Injection Attacks?', 'React escapes all values embedded in JSX, converting them to strings before rendering.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'This automatic escaping prevents XSS by ensuring user input is never executed as code.', NULL, ARRAY[]::text[], ARRAY['react','jsx','xss','security'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q188","original_type":"open-ended","topic":"XSS Prevention","subcategory":"XSS (Cross-Site Scripting)","sample_answers":["JSX automatically escapes `{userInput}` by converting it to a string, so `<h1>{name}</h1>` can’t inject scripts.","This built-in protection makes React apps resistant to XSS without manual sanitization."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('4c122cbd-0437-4125-9823-c9805ed758d7', 'How do you update rendered elements?', 'Call `ReactDOM.render()` again with the new element to update the UI.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'In modern React, state-driven re-renders replace manual `render()` calls, but this is how it worked in early examples.', NULL, ARRAY[]::text[], ARRAY['react','render','dom','updates'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q189","original_type":"open-ended","topic":"Rendering","subcategory":"React Basics","sample_answers":["In legacy React (pre-hooks), you could update the UI by calling `ReactDOM.render(newElement, container)` repeatedly.","Today, we use state and let React handle re-renders automatically via the reconciliation process."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('f3dbc035-db59-4ae5-8ec1-7cd288777f81', 'How do you say that props are readonly?', 'React components must treat props as immutable—never modify them directly.', 'multiple-choice', 'beginner', 3, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'Components should act like pure functions with respect to props—same input, same output.', NULL, ARRAY[]::text[], ARRAY['react','props','immutable','pure-functions'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q190","original_type":"true-false","topic":"Props","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('bc412540-5fc2-445d-b54c-88851d9539d1', 'What are the conditions to safely use the index as a key?', '1. List is static
2. Items have no IDs
3. List is never reordered or filtered', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Always safe to use index","isCorrect":false},{"id":"b","text":"Only if list is static, no IDs, and never reordered","isCorrect":true},{"id":"c","text":"Index is preferred over IDs","isCorrect":false},{"id":"d","text":"Keys are optional in static lists","isCorrect":false}]', NULL, 'Using index as key can cause bugs if the list changes order or items are added/removed.', NULL, ARRAY[]::text[], ARRAY['react','keys','lists','index'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q191","original_type":"multiple-choice","topic":"Lists and Keys","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"540c20e9-c800-48de-9185-e611c10d37d5"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('375e933b-dcce-48bf-9122-35917a6f50a2', 'Should keys be globally unique?', 'No. Keys only need to be unique among siblings, not globally.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Keys help React identify which items have changed within the same parent—global uniqueness isn’t required.', NULL, ARRAY[]::text[], ARRAY['react','keys','lists','uniqueness'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q192","original_type":"true-false","topic":"Lists and Keys","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"540c20e9-c800-48de-9185-e611c10d37d5"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('a489aebb-f52c-4791-8f9f-b087d10304b4', 'What is the popular choice for form handling?', '`Formik` is a popular React form library for validation, state management, and submission handling.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Redux Form is the only option","isCorrect":false},{"id":"b","text":"Formik is a popular lightweight form library","isCorrect":true},{"id":"c","text":"HTML forms are sufficient","isCorrect":false},{"id":"d","text":"Forms don’t need libraries","isCorrect":false}]', NULL, 'Formik provides a minimal API to handle common form tasks without Redux.', NULL, ARRAY[]::text[], ARRAY['formik','forms','react','validation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q193","original_type":"multiple-choice","topic":"Formik","subcategory":"Form Libraries","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('3e77ba6a-af6d-4da5-bb7e-ec725f228eb8', 'What are the advantages of Formik over Redux Form?', '1. Form state is local, not global
2. Better performance (no Redux reducer calls per keystroke)
3. Smaller bundle size', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"Redux Form is faster","isCorrect":false},{"id":"b","text":"Formik: local state, better perf, smaller size","isCorrect":true},{"id":"c","text":"Redux Form has fewer features","isCorrect":false},{"id":"d","text":"Formik requires Redux","isCorrect":false}]', NULL, 'Formik keeps form state local, avoiding unnecessary Redux overhead.', NULL, ARRAY[]::text[], ARRAY['formik','redux-form','comparison','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q194","original_type":"multiple-choice","topic":"Formik","subcategory":"Form Libraries","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('4ca82933-5709-47a0-aebb-af4f4a324ea0', 'Why are you not required to use inheritance in React?', 'React recommends composition over inheritance for code reuse—via props and custom hooks.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Composition is more flexible and explicit—components receive behavior via props or hooks.', NULL, ARRAY[]::text[], ARRAY['react','composition','inheritance','design'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q195","original_type":"open-ended","topic":"Composition vs Inheritance","subcategory":"React Basics","sample_answers":["React uses composition: components receive functionality via props, children, or custom hooks—not class inheritance.","For non-UI logic, extract functions/modules instead of extending classes."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('cce44caf-97cc-496c-9a11-506804f9576f', 'Can I use web components in a React application?', 'Yes. React can render web components by using their custom element tags in JSX.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'React treats web components as regular DOM elements—just use `<my-element />` in JSX.', NULL, ARRAY[]::text[], ARRAY['react','web-components','custom-elements','integration'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q196","original_type":"true-false","topic":"Web Components","subcategory":"Integration","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('b7e1e848-2af2-41ac-a7ae-dbcd71f38e99', 'What is dynamic import?', 'Dynamic `import()` loads modules at runtime, enabling code-splitting and lazy loading.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Unlike static `import`, dynamic `import()` returns a promise and loads code on demand.', NULL, ARRAY[]::text[], ARRAY['dynamic-import','code-splitting','esm','webpack'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q197","original_type":"open-ended","topic":"Dynamic Imports","subcategory":"Code Splitting","sample_answers":["`import(''./module'')` returns a Promise that resolves to the module, allowing conditional or lazy loading.","It’s the foundation for route-based or feature-based code splitting in modern apps."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('53f218c3-ca9c-4b7d-9161-9ca9a001f1ab', 'What are loadable components?', '`@loadable/component` is a library for code-splitting with SSR support, used before React 18’s lazy/Suspense SSR.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'It provided SSR-compatible dynamic imports before React.lazy supported server rendering.', NULL, ARRAY[]::text[], ARRAY['loadable','code-splitting','ssr','react'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q198","original_type":"open-ended","topic":"Loadable Components","subcategory":"Code Splitting","sample_answers":["`loadable(() => import(''./Component''))` creates a component that loads its bundle dynamically, with SSR support.","Now largely superseded by React 18’s built-in SSR support for `lazy` and `Suspense`."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('9cd2839f-db82-4678-9ce4-66aaaf351406', 'What is Suspense component?', '`Suspense` lets you show a fallback UI while waiting for asynchronous operations (code or data) to complete.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It’s used with `React.lazy` for code-splitting and with data-fetching libraries that integrate with Suspense.', NULL, ARRAY[]::text[], ARRAY['react','suspense','lazy','loading'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q199","original_type":"open-ended","topic":"Suspense","subcategory":"React Basics","sample_answers":["`<Suspense fallback={<Spinner />}><LazyComponent /></Suspense>` shows a spinner while LazyComponent loads.","Suspense provides a declarative way to handle loading states without manual boolean flags."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
