INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES
('c8326a01-fa71-4d25-b7a1-6b92a4e8fe6b', 'What is route-based code splitting?', 'Splitting code by route—each page loads only its required bundle—improves initial load performance.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Since users navigate to one route at a time, splitting by route ensures they only download what they need.', NULL, ARRAY[]::text[], ARRAY['code-splitting','routing','performance','react-router'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q200","original_type":"open-ended","topic":"Code Splitting","subcategory":"Code Splitting","sample_answers":["Use `React.lazy` with React Router to load each route’s component only when visited: `const Home = lazy(() => import(''./Home''));`","This reduces the main bundle size and speeds up time-to-interactive for the initial route."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('47a27c3d-7b3e-4367-9884-4913a63b3c7a', 'What is the purpose of default value in context?', 'The `defaultValue` argument in `React.createContext(defaultValue)` is only used when a component does not have a matching Provider above it in the tree. This is helpful for testing components in isolation.', 'multiple-choice', 'intermediate', 5, NULL, NULL, 'The default value allows components to work without a Provider, which is useful in unit tests or when the Provider is conditionally rendered.', NULL, ARRAY[]::text[], ARRAY['react','context','defaultvalue','testing'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q201","original_type":"open-ended","topic":"Context API","subcategory":"Context API","sample_answers":["The default value in `createContext()` is used only when no Provider is present in the ancestry—ideal for testing or fallback behavior.","Example: `const ThemeContext = React.createContext(''light'');` ensures components render with ''light'' theme if no Provider wraps them."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('6e78d746-5153-469c-859c-d6374ff310e3', 'What is diffing algorithm?', 'React uses a heuristic O(n) diffing algorithm to efficiently update the UI by minimizing DOM operations, based on two assumptions: (1) different element types produce different trees, (2) keys help identify stable children.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'Instead of O(n³) tree comparison, React assumes stable identity via keys and type changes imply full subtree replacement.', NULL, ARRAY[]::text[], ARRAY['react','diffing','reconciliation','virtual-dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q202","original_type":"open-ended","topic":"Diffing Algorithm","subcategory":"Virtual DOM","sample_answers":["React’s diffing algorithm is an O(n) heuristic that avoids expensive full-tree comparisons by assuming element type changes mean new subtrees and using keys to track child identity.","This optimization enables fast UI updates even with large component trees."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('eb71ba47-3aca-4e62-8a7f-054ec82fd813', 'What are the rules covered by diffing algorithm?', 'Rules: (1) Different root types → full rebuild, (2) Same DOM type → update attributes only, (3) Same component type → update props and recurse, (4) Children diffed by index unless keys are used.', 'multiple-choice', 'advanced', 9, '[{"id":"a","text":"React compares every possible node pair in O(n³) time","isCorrect":false},{"id":"b","text":"Different types rebuild tree; same DOM updates attrs; same component updates props; keys optimize children diffing","isCorrect":true},{"id":"c","text":"Keys are optional and have no performance impact","isCorrect":false},{"id":"d","text":"Component instances are recreated on every render","isCorrect":false}]', NULL, 'These rules ensure minimal DOM mutations while maintaining correctness through key-based child reconciliation.', NULL, ARRAY[]::text[], ARRAY['react','reconciliation','diffing','keys'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q203","original_type":"multiple-choice","topic":"Diffing Algorithm","subcategory":"Virtual DOM","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('f43cffb6-2253-4b86-a651-c80390bd0946', 'When do you need to use refs?', 'Use refs for: (1) managing focus/text selection/media playback, (2) triggering imperative animations, (3) integrating with third-party DOM libraries.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"For all state management","isCorrect":false},{"id":"b","text":"For focus, animations, and third-party DOM integrations","isCorrect":true},{"id":"c","text":"To replace useState","isCorrect":false},{"id":"d","text":"Only in class components","isCorrect":false}]', NULL, 'Refs provide a way to access DOM nodes or React elements created in the render method, bypassing typical data flow.', NULL, ARRAY[]::text[], ARRAY['react','refs','imperative','dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q204","original_type":"multiple-choice","topic":"Refs","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('aa8b5c62-5608-41e5-b443-ce1188c35f60', 'Must prop be named as render for render props?', 'No. Any prop that is a function used to determine what to render is a render prop—including the `children` prop.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'The pattern is defined by behavior (a function that returns JSX), not the prop name. `children` as a function is common.', NULL, ARRAY[]::text[], ARRAY['react','render-props','children','patterns'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q205","original_type":"true-false","topic":"Render Props","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('6a87f1d4-028a-4d81-8460-c26a05977c0d', 'What are the problems of using render props with pure components?', 'Creating a new function inside render negates PureComponent’s shallow comparison, causing unnecessary re-renders.', 'multiple-choice', 'advanced', 7, NULL, NULL, 'Each render creates a new function instance, so shallow prop comparison fails, and the component re-renders every time.', NULL, ARRAY[]::text[], ARRAY['react','purecomponent','render-props','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q206","original_type":"open-ended","topic":"Render Props","subcategory":"Performance Optimization","sample_answers":["Defining the render function inline in render creates a new reference each time, breaking PureComponent’s optimization. Define it as an instance method or use useCallback.","Solution: Move the render function outside render or memoize it to preserve referential identity."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('29d6058a-34ed-423d-9b57-c5962a10b603', 'What is windowing technique?', 'Windowing renders only the visible subset of a large list, reducing DOM nodes and improving performance. Libraries: react-window, react-virtualized.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Instead of rendering 10,000 list items, windowing renders only the 20 currently visible, drastically improving memory and speed.', NULL, ARRAY[]::text[], ARRAY['react','windowing','virtualization','performance','lists'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q207","original_type":"open-ended","topic":"Windowing","subcategory":"Performance Optimization","sample_answers":["Windowing (or virtualization) renders only the items in the viewport, recycling DOM nodes as the user scrolls—ideal for long lists or tables.","Libraries like `react-window` provide `FixedSizeList` and `VariableSizeList` components to implement this pattern easily."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('f517ea89-ca3b-4919-b848-032dbc8c6e12', 'How do you print falsy values in JSX?', 'Falsy values (false, null, undefined) don’t render in JSX. Convert them to strings using `String()` or template literals.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'React ignores falsy children for convenience (e.g., `{condition && <Component />}`), but explicit conversion shows the value.', NULL, ARRAY[]::text[], ARRAY['react','jsx','falsy','rendering'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q208","original_type":"open-ended","topic":"JSX","subcategory":"JSX","sample_answers":["Use `{String(myVar)}` or `{`${myVar}`}` to display falsy values like `null` or `undefined` in JSX.","Example: `<div>Value: {String(user.age)}</div>` will show ''Value: null'' if age is null."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('e5161598-591f-41cf-a907-6838eeeb47b0', 'What is the typical use case of portals?', 'Portals render modals, tooltips, dropdowns, and notifications outside the parent DOM tree to avoid CSS issues like `overflow: hidden` or `z-index` stacking contexts.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"To improve component reusability","isCorrect":false},{"id":"b","text":"To render modals/tooltips outside parent DOM to avoid CSS clipping and z-index issues","isCorrect":true},{"id":"c","text":"To replace Redux","isCorrect":false},{"id":"d","text":"Only for server-side rendering","isCorrect":false}]', NULL, 'By rendering into a separate DOM node (e.g., `document.body`), portals escape parent styling constraints.', NULL, ARRAY[]::text[], ARRAY['react','portals','modals','tooltips','css'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q209","original_type":"multiple-choice","topic":"React Portals","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('eefda22c-b9e2-49e2-b929-a8bdd9874eff', 'How do you set default value for uncontrolled component?', 'Use `defaultValue` for text inputs and `defaultChecked` for checkboxes/radios to set initial values without controlling subsequent updates.', 'multiple-choice', 'beginner', 5, NULL, NULL, '`defaultValue` sets the initial DOM value but doesn’t override user changes, unlike `value` which fully controls the input.', NULL, ARRAY[]::text[], ARRAY['react','forms','uncontrolled','defaultvalue'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q210","original_type":"open-ended","topic":"Uncontrolled Components","subcategory":"Forms","sample_answers":["Use `<input defaultValue=\\"John\\" />` to set the initial value of an uncontrolled input. For checkboxes, use `defaultChecked={true}`.","This is useful when you need to initialize a form but don’t want to manage every keystroke with state."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('6044f053-ed68-4ddf-8caa-59e106d54e28', 'What is your favorite React stack?', 'A popular stack includes Redux/redux-saga for state, react-router for routing, styled-components for styling, axios for API calls, and webpack/Babel for tooling.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This stack balances maturity, performance, and developer experience for large-scale applications.', NULL, ARRAY[]::text[], ARRAY['react','boilerplate','redux','saga','tooling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q211","original_type":"open-ended","topic":"React Ecosystem","subcategory":"React Ecosystem","sample_answers":["A robust React stack: Redux + redux-saga for state and side effects, React Router for navigation, styled-components for styling, axios for HTTP, and Webpack/Babel for builds.","Projects like react-boilerplate provide this setup out of the box for production-ready apps."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('e484b446-f60f-4f51-a221-1f1f5b82c6bc', 'What is the difference between Real DOM and Virtual DOM?', 'Real DOM updates are slow and expensive; Virtual DOM is a lightweight JS representation that enables fast diffing and minimal real DOM mutations.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"Real DOM is faster than Virtual DOM","isCorrect":false},{"id":"b","text":"Real DOM updates are slow/expensive; Virtual DOM enables fast, minimal updates","isCorrect":true},{"id":"c","text":"Virtual DOM directly manipulates HTML","isCorrect":false},{"id":"d","text":"They are the same","isCorrect":false}]', NULL, 'Virtual DOM allows React to compute the minimal set of changes before touching the real DOM, optimizing performance.', NULL, ARRAY[]::text[], ARRAY['react','virtual-dom','real-dom','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q212","original_type":"multiple-choice","topic":"Virtual DOM","subcategory":"Virtual DOM","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"0ee220a5-c50b-4c32-afc5-a654931c9fcd"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('9ead77a8-58a1-4151-892b-42a6a105bb74', 'How to add Bootstrap to a React application?', 'Three ways: (1) CDN, (2) npm install bootstrap + import CSS, (3) React-specific libraries like react-bootstrap or reactstrap.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"Only via CDN","isCorrect":false},{"id":"b","text":"CDN, npm install, or React wrappers like react-bootstrap","isCorrect":true},{"id":"c","text":"Bootstrap is built into React","isCorrect":false},{"id":"d","text":"Only with Sass","isCorrect":false}]', NULL, 'React-specific libraries (react-bootstrap) wrap Bootstrap components as React components, avoiding jQuery dependency.', NULL, ARRAY[]::text[], ARRAY['react','bootstrap','styling','css'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q213","original_type":"multiple-choice","topic":"Third-Party Libraries","subcategory":"Styling","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('95808455-40e1-4b1f-8631-943d861135e8', 'Can you list top websites using React?', 'Top sites: Facebook, Instagram, WhatsApp, Uber, Airbnb, Netflix, PayPal, Khan Academy, Dropbox, Flipboard.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'React’s component model and performance make it ideal for large, dynamic user interfaces.', NULL, ARRAY[]::text[], ARRAY['react','companies','adoption','real-world'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q214","original_type":"open-ended","topic":"React Adoption","subcategory":"React Ecosystem","sample_answers":["Major companies using React include Facebook (creator), Instagram, WhatsApp, Airbnb, Netflix, Uber, and PayPal.","These companies rely on React for its declarative UI, performance, and rich ecosystem."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('76697799-4602-408a-a7fc-6cde21a827a4', 'Is it recommended to use CSS In JS in React?', 'React has no opinion, but beginners should start with CSS files and className. CSS-in-JS (e.g., styled-components) is a valid alternative for scoped styles.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'CSS-in-JS offers benefits like dynamic styling and scoping, but traditional CSS is simpler for beginners.', NULL, ARRAY[]::text[], ARRAY['react','css-in-js','styled-components','styling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q215","original_type":"true-false","topic":"CSS-in-JS","subcategory":"CSS-in-JS","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('a9526b71-5a4a-468e-97bc-ed615076f1ed', 'Do I need to rewrite all my class components with hooks?', 'No. Hooks are additive; you can use them in new components without rewriting existing class components.', 'multiple-choice', 'beginner', 3, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'React has no plans to remove class components. Hooks and classes can coexist in the same app.', NULL, ARRAY[]::text[], ARRAY['react','hooks','class-components','migration'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q216","original_type":"true-false","topic":"React Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('76cbec41-8125-4c76-a9df-c58e90ad3d92', 'What is useEffect hook? How to fetch data with React Hooks?', '`useEffect` handles side effects in function components. Use it with `useState` to fetch data on mount by passing an empty dependency array.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It replaces componentDidMount, componentDidUpdate, and componentWillUnmount with a unified API.', NULL, ARRAY[]::text[], ARRAY['react','useeffect','data-fetching','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q217","original_type":"open-ended","topic":"useEffect","subcategory":"React Hooks","sample_answers":["`useEffect(() => { fetch(url).then(setData); }, []);` fetches data once on mount. The empty array ensures it doesn’t re-run on updates.","Always include cleanup for subscriptions or async operations to avoid memory leaks or state updates on unmounted components."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('135c9929-945f-4c00-98d4-dca49aefb76f', 'Is Hooks cover all use cases for classes?', 'Not yet. Hooks don’t cover `getSnapshotBeforeUpdate` and `componentDidCatch` (error boundaries).', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Error boundaries and snapshot reading are still class-only features, though the React team plans to add Hook equivalents.', NULL, ARRAY[]::text[], ARRAY['react','hooks','class-lifecycle','limitations'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q218","original_type":"true-false","topic":"React Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('516ca754-e44b-48c7-a0f8-4df0fe2fe59f', 'What is the stable release for hooks support?', 'React 16.8 introduced stable Hooks support for React DOM, React DOM Server, React Test Renderer, and React Shallow Renderer.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'React 16.8 (February 2019) was the first version to include Hooks as a stable feature.', NULL, ARRAY[]::text[], ARRAY['react','hooks','16.8','release'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q219","original_type":"open-ended","topic":"React Hooks","subcategory":"React Hooks","sample_answers":["React 16.8, released in February 2019, was the first stable version to include Hooks across all official React packages.","This allowed function components to use state and side effects without classes."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
