INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES
('26711762-813d-4486-8379-1def21184f06', 'How to structure Redux top-level directories?', 'Common structure: `components/`, `containers/`, `actions/`, `reducers/`, `store/`.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"All files in one folder","isCorrect":false},{"id":"b","text":"`components/`, `containers/`, `actions/`, `reducers/`, `store/`","isCorrect":true},{"id":"c","text":"Only `src/` and `test/`","isCorrect":false},{"id":"d","text":"No standard structure exists","isCorrect":false}]', NULL, 'This structure separates concerns: dumb components, smart containers, action creators, reducers, and store setup.', NULL, ARRAY[]::text[], ARRAY['redux','structure','project-organization'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q121","original_type":"multiple-choice","topic":"Redux Project Structure","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('37f8864d-8ee6-4a1b-9fe8-3912e567f509', 'What is redux-saga?', '`redux-saga` is a middleware for handling side effects in Redux using generator functions.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It uses ES6 generators to manage complex async flows (e.g., cancellable requests, race conditions) in a declarative way.', NULL, ARRAY[]::text[], ARRAY['redux','saga','middleware','side-effects'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q122","original_type":"open-ended","topic":"Redux Saga","subcategory":"State Management","sample_answers":["`redux-saga` is a Redux middleware that uses generator functions to handle side effects like API calls, allowing complex async logic to be testable and cancellable.","It treats side effects as separate ''sagas'' that listen for actions and perform tasks using effects like `call()` and `put()`."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('cb0571b4-9200-4770-b226-87dc47496c6d', 'What is the mental model of redux-saga?', 'A saga is like a background thread that handles side effects, started/paused/cancelled via Redux actions.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'Sagas run in the background, watch for actions, and can dispatch new actions—all while being testable and cancellable.', NULL, ARRAY[]::text[], ARRAY['redux-saga','generators','side-effects'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q123","original_type":"open-ended","topic":"Redux Saga","subcategory":"State Management","sample_answers":["Think of a saga as a daemon process that lives alongside your app, listening for dispatched actions and performing side effects in response.","It’s a separate thread of execution managed by Redux middleware, using generators for async control flow."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('a3cfbcde-c5ac-4baa-879e-4a0cfd0bf4e7', 'What are the differences between `call()` and `put()` in redux-saga?', '`call()` invokes a function (e.g., API call); `put()` dispatches an action to the store.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"`call()` dispatches actions; `put()` makes API calls","isCorrect":false},{"id":"b","text":"`call()` invokes functions; `put()` dispatches actions","isCorrect":true},{"id":"c","text":"Both are used only for logging","isCorrect":false},{"id":"d","text":"`put()` is for database queries","isCorrect":false}]', NULL, '`call(fn, ...args)` is for invoking functions; `put(action)` is for dispatching actions.', NULL, ARRAY[]::text[], ARRAY['redux-saga','call','put','effects'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q124","original_type":"multiple-choice","topic":"Redux Saga","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('b0827951-8b18-49f8-8fb4-2837edf4a4c2', 'What is Redux Thunk?', 'Redux Thunk is middleware that allows action creators to return a function instead of an action object, enabling async logic.', 'multiple-choice', 'beginner', 6, NULL, NULL, 'The thunk function receives `dispatch` and `getState`, allowing conditional or delayed dispatch.', NULL, ARRAY[]::text[], ARRAY['redux','thunk','middleware','async'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q125","original_type":"open-ended","topic":"Redux Thunk","subcategory":"State Management","sample_answers":["Redux Thunk lets you write action creators that return a function, which can perform async operations and dispatch actions when needed.","It’s the simplest way to handle async logic in Redux, using functions instead of plain action objects."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('2e46c55e-d8b5-4a96-8f65-86cf9a7b094a', 'What are the differences between `redux-saga` and `redux-thunk`?', 'Thunk uses Promises and is simple; Saga uses Generators and is powerful for complex flows.', 'multiple-choice', 'intermediate', 8, '[{"id":"a","text":"Saga uses Promises; Thunk uses Generators","isCorrect":false},{"id":"b","text":"Thunk uses Promises; Saga uses Generators","isCorrect":true},{"id":"c","text":"Both are identical","isCorrect":false},{"id":"d","text":"Saga is deprecated","isCorrect":false}]', NULL, 'Thunk is easier for simple cases; Saga is better for complex async logic (cancellation, race conditions, etc.).', NULL, ARRAY[]::text[], ARRAY['redux','thunk','saga','middleware'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q126","original_type":"multiple-choice","topic":"Redux Middleware","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('797274c2-0566-4f0c-877f-51e3f16494e9', 'What is Redux DevTools?', 'Redux DevTools is a time-travel debugging environment for Redux with action replay and hot reloading.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'It lets you inspect every action and state change, rewind, and replay actions—great for debugging.', NULL, ARRAY[]::text[], ARRAY['redux','devtools','debugging','time-travel'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q127","original_type":"open-ended","topic":"Redux DevTools","subcategory":"State Management","sample_answers":["Redux DevTools is a browser extension or library that provides a UI to inspect Redux store changes, dispatch actions manually, and travel back in time.","It shows a timeline of actions, lets you cancel past actions, and re-evaluates state when reducer code changes."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('729bd65d-4109-4967-9d3d-cf9f5f3c1f44', 'What are the features of Redux DevTools?', 'Features include state/action inspection, time travel, reducer hot reloading, error tracking, and state persistence.', 'multiple-choice', 'beginner', 6, '[{"id":"a","text":"Only shows console logs","isCorrect":false},{"id":"b","text":"State inspection, time travel, hot reloading, error tracking, persistence","isCorrect":true},{"id":"c","text":"Replaces the need for unit tests","isCorrect":false},{"id":"d","text":"Only works in production","isCorrect":false}]', NULL, 'Key features: inspect state/actions, cancel actions (time travel), hot reload reducers, see errors, persist debug sessions.', NULL, ARRAY[]::text[], ARRAY['redux-devtools','debugging','features'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q128","original_type":"multiple-choice","topic":"Redux DevTools","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('2516fa54-bc4c-422d-bc0a-b56b734fb659', 'What are Redux selectors and why use them?', 'Selectors are functions that compute derived data from Redux state. They enable memoization and reduce redundant calculations.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"They directly mutate the Redux state","isCorrect":false},{"id":"b","text":"They compute derived data and memoize results for performance","isCorrect":true},{"id":"c","text":"They replace reducers","isCorrect":false},{"id":"d","text":"They are only for async logic","isCorrect":false}]', NULL, 'Selectors compute derived data (e.g., filtered lists) and memoize results to avoid recomputation unless inputs change.', NULL, ARRAY[]::text[], ARRAY['redux','selectors','reselect','memoization'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q129","original_type":"multiple-choice","topic":"Reselect","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('7ae91233-db24-4726-9010-2b47214b4b1b', 'What is Redux Form?', 'Redux Form is a library that manages form state in Redux, handling values, validation, and submission.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'It stores all form state (values, errors, touched fields) in Redux, enabling complex form logic and persistence.', NULL, ARRAY[]::text[], ARRAY['redux-form','forms','validation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q130","original_type":"open-ended","topic":"Redux Form","subcategory":"State Management","sample_answers":["Redux Form integrates with React and Redux to keep all form state (input values, validation errors, submission status) in the Redux store.","It provides higher-order components and action creators to manage form lifecycle and validation."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('fb5efe73-81c5-42c7-85a3-627b0b1b58d5', 'What are the main features of Redux Form?', 'Features include field value persistence, sync/async validation, and formatting/parsing of field values.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Only basic input binding","isCorrect":false},{"id":"b","text":"Field persistence, validation, formatting/parsing","isCorrect":true},{"id":"c","text":"Replaces React components","isCorrect":false},{"id":"d","text":"Only works with class components","isCorrect":false}]', NULL, 'It handles form state, validation (sync and async), and data transformation (formatting, parsing, normalization).', NULL, ARRAY[]::text[], ARRAY['redux-form','features','validation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q131","original_type":"multiple-choice","topic":"Redux Form","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('b6b6147d-8d72-4b62-81b0-ae7a133ee734', 'How to add multiple middlewares to Redux?', 'Use `applyMiddleware()` from Redux, passing all middleware functions as arguments.', 'multiple-choice', 'intermediate', 6, NULL, NULL, '`applyMiddleware(thunk, logger)(createStore)` chains multiple middleware functions.', NULL, ARRAY[]::text[], ARRAY['redux','middleware','applymiddleware'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q132","original_type":"open-ended","topic":"Redux Middleware","subcategory":"State Management","sample_answers":["Use `applyMiddleware(middleware1, middleware2)(createStore)` to compose multiple middleware functions when creating the store.","Example: `const store = createStore(reducer, applyMiddleware(thunk, logger));`"],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('442fca48-86da-44c3-bc6f-1e9cf0853ade', 'How to set initial state in Redux?', 'Pass the initial state as the second argument to `createStore()`.', 'multiple-choice', 'beginner', 5, NULL, NULL, '`createStore(reducer, initialState)` sets the starting state of the store.', NULL, ARRAY[]::text[], ARRAY['redux','initial-state','createstore'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q133","original_type":"open-ended","topic":"Redux Store","subcategory":"State Management","sample_answers":["Call `createStore(rootReducer, initialState)` where `initialState` is a plain object matching your state shape.","This is useful for server-side rendering or preloading data from localStorage."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('7b821201-8a3a-4fe5-90c4-bf5081492897', 'How is Relay different from Redux?', 'Relay manages only server-originated state via GraphQL; Redux manages all application state.', 'multiple-choice', 'advanced', 8, '[{"id":"a","text":"Relay uses REST APIs","isCorrect":false},{"id":"b","text":"Relay manages only server state via GraphQL; Redux manages all state","isCorrect":true},{"id":"c","text":"Redux requires GraphQL","isCorrect":false},{"id":"d","text":"They are identical","isCorrect":false}]', NULL, 'Relay is GraphQL-specific and handles caching/optimization automatically; Redux is general-purpose.', NULL, ARRAY[]::text[], ARRAY['relay','redux','graphql','state-management'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q134","original_type":"multiple-choice","topic":"Relay vs Redux","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('ef68b361-f8ef-4343-aa1a-bcfb0eccdd0e', 'What is an action in Redux?', 'An action is a plain JavaScript object with a `type` property that describes what happened.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"A function that mutates state","isCorrect":false},{"id":"b","text":"A plain object with a `type` property","isCorrect":true},{"id":"c","text":"A class instance","isCorrect":false},{"id":"d","text":"A Promise","isCorrect":false}]', NULL, 'Actions are payloads of information sent to the store. They must have a `type` string.', NULL, ARRAY[]::text[], ARRAY['redux','actions','flux-standard-action'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q135","original_type":"multiple-choice","topic":"Redux Actions","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('c059dbac-af0d-4752-8d99-a67abceab2eb', 'What is the difference between React Native and React?', 'React is for web UIs; React Native compiles to native mobile components for iOS/Android.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"React Native uses HTML","isCorrect":false},{"id":"b","text":"React is for web; React Native is for native mobile apps","isCorrect":true},{"id":"c","text":"React Native cannot use JavaScript","isCorrect":false},{"id":"d","text":"They are the same library","isCorrect":false}]', NULL, 'React targets the DOM; React Native targets native mobile APIs using platform-specific components.', NULL, ARRAY[]::text[], ARRAY['react','react-native','mobile'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q136","original_type":"multiple-choice","topic":"React Native Basics","subcategory":"Core Concepts","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('453a1a82-0b76-4d5e-9174-ae6399d31651', 'How to test React Native apps?', 'Test using iOS/Android simulators or real devices via Expo, which syncs using QR codes over the same network.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'Expo simplifies testing by allowing you to scan a QR code to run the app on a physical device connected to the same Wi-Fi.', NULL, ARRAY[]::text[], ARRAY['react-native','expo','testing','simulator'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q137","original_type":"open-ended","topic":"React Native Testing","subcategory":"Testing & Debugging","sample_answers":["Use Expo to run your app on a real device by scanning a QR code, or use iOS/Android simulators from Xcode or Android Studio.","Ensure your computer and mobile device are on the same network when using Expo for live reloading."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('40cb95de-713c-4588-b8fc-91c389fe7e68', 'How to do logging in React Native?', 'Use `console.log()`, and view logs with `react-native log-ios` or `react-native log-android`.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Standard `console` methods work, and logs can be viewed via CLI commands or Chrome DevTools.', NULL, ARRAY[]::text[], ARRAY['react-native','logging','console'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q138","original_type":"open-ended","topic":"React Native Debugging","subcategory":"Testing & Debugging","sample_answers":["Use `console.log()` in your code, then run `react-native log-ios` or `react-native log-android` to see output in the terminal.","You can also debug in Chrome DevTools by enabling remote debugging in the developer menu."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('3d832544-ead9-494a-a78a-831d91c0ab7a', 'How to debug your React Native?', 'Use the in-app developer menu (Cmd+D on iOS simulator), then open Chrome DevTools at `http://localhost:8081/debugger-ui`.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"Only use Xcode debugger","isCorrect":false},{"id":"b","text":"Use Cmd+D in simulator, then Chrome DevTools at localhost:8081/debugger-ui","isCorrect":true},{"id":"c","text":"Debugging is not supported","isCorrect":false},{"id":"d","text":"Use Firefox only","isCorrect":false}]', NULL, 'The debug workflow involves opening the dev menu, enabling remote JS debugging, and using Chrome DevTools.', NULL, ARRAY[]::text[], ARRAY['react-native','debugging','devtools'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q139","original_type":"multiple-choice","topic":"React Native Debugging","subcategory":"Testing & Debugging","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('b3265f22-0070-4977-b7e6-fc72a10ac222', 'What is Reselect and how it works?', 'Reselect is a selector library that uses memoization to avoid recomputing derived data unless inputs change.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It caches the last result and arguments; if inputs are the same, it returns the cached output without recomputing.', NULL, ARRAY[]::text[], ARRAY['reselect','selectors','memoization','redux'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q140","original_type":"open-ended","topic":"Reselect","subcategory":"State Management","sample_answers":["Reselect creates memoized selectors that only recompute when their input arguments change, improving performance in Redux apps.","It uses a `createSelector` function that takes input selectors and a result function, caching the output for identical inputs."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
