-- Batch 9: Questions 161-180
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES
('65eb8671-8b77-47a5-980e-2a625e8cc71b', 'What is render hijacking in React?', 'Render hijacking is controlling a component’s output by wrapping it in a Higher-Order Component (HOC) to inject props or logic.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'It’s not true hijacking—just composition via HOCs to alter rendering behavior conditionally.', NULL, ARRAY[]::text[], ARRAY['react','hoc','render-hijacking','composition'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q160","original_type":"open-ended","topic":"Higher-Order Components","subcategory":"React Basics","sample_answers":["Render hijacking uses HOCs to wrap a component and modify its output by injecting props or conditional logic.","Example: A HOC that wraps a component and only renders it if the user is authenticated."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('3f975595-33a9-4418-97e0-756fd7acfbb9', 'How to pass numbers to React component?', 'Pass numbers using curly braces: `<Child age={30} />`. Strings use quotes: `<Child name="Chetan" />`.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'JSX expressions inside `{}` are evaluated as JavaScript, so numbers, booleans, and objects must be wrapped in braces.', NULL, ARRAY[]::text[], ARRAY['react','props','javascript','jsx'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q161","original_type":"open-ended","topic":"Props","subcategory":"React Basics","sample_answers":["Use `<Component count={42} />` to pass the number 42. The braces tell JSX to evaluate the expression as JavaScript.","Never use quotes for numbers: `<Component count=\\"42\\" />` passes the string ''42'', not the number."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('cd688643-5153-45fc-85ac-340187fc9a2a', 'Do I need to keep all my state in Redux? Should I use React internal state?', 'No. Use Redux for global/shared state; use React component state for local/UI state (e.g., dropdown open/closed).', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Ask: Is this state shared? Needed for debugging? Used in multiple components? If not, keep it local.', NULL, ARRAY[]::text[], ARRAY['redux','react-state','best-practices','state-management'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q162","original_type":"open-ended","topic":"Redux Best Practices","subcategory":"State Management","sample_answers":["Keep global, shared, or persistent state in Redux. Keep transient UI state (e.g., form input, modal open) in component state.","Rules of thumb: if only one component cares, or it’s ephemeral, use useState. If multiple components or time-travel matters, use Redux."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('6cb233aa-bc85-4ea1-8e05-a382594775e3', 'What is the purpose of registerServiceWorker in React?', 'It registers a service worker to cache assets, enabling offline functionality and improved performance.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'The service worker caches static assets so the app works offline or on slow networks.', NULL, ARRAY[]::text[], ARRAY['react','service-worker','pwa','offline'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q163","original_type":"open-ended","topic":"PWAs (Progressive Web Apps)","subcategory":"Performance & Monitoring","sample_answers":["`registerServiceWorker()` sets up a service worker that caches the app shell and assets, enabling offline use and faster repeat visits.","It’s part of Create React App’s PWA support—users can install the app and use it without internet."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('80746b72-bc11-41d5-b63f-df8bcb073ef0', 'What is React memo function?', '`React.memo` is a higher-order component that prevents re-renders of function components when props haven’t changed.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'It’s the functional component equivalent of `PureComponent` for class components.', NULL, ARRAY[]::text[], ARRAY['react','memo','performance','optimization'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q164","original_type":"open-ended","topic":"Performance Optimization","subcategory":"React Basics","sample_answers":["`React.memo(MyComponent)` returns a memoized version that only re-renders if props change (shallow comparison by default).","Use it to optimize expensive components that receive the same props frequently."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('89119923-b513-4514-ad6a-3e5275d457cd', 'What is React lazy function?', '`React.lazy` lets you load components dynamically via `import()` for code-splitting.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It returns a promise that resolves to a component, which must be rendered inside `Suspense`.', NULL, ARRAY[]::text[], ARRAY['react','lazy','code-splitting','dynamic-import'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q165","original_type":"open-ended","topic":"Code Splitting","subcategory":"React Basics","sample_answers":["`const LazyComp = React.lazy(() => import(''./LazyComp''));` loads the component only when needed, reducing initial bundle size.","Must be used with `<Suspense fallback={...}>` to show a loading state while the chunk loads."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('1ecd5692-496d-466f-a6b5-bb0aabb8d166', 'How to prevent unnecessary updates using setState?', 'In functional `setState`, return `null` if the new state equals the current state to skip re-rendering.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Returning `null` from functional `setState` tells React to skip the update and re-render.', NULL, ARRAY[]::text[], ARRAY['react','setstate','optimization','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q166","original_type":"open-ended","topic":"State Management","subcategory":"React Basics","sample_answers":["In `setState((state) => { if (state.x === newX) return null; else return { x: newX }; })`, returning `null` prevents re-render.","This is useful for avoiding redundant updates when new data matches current state."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('6c6ddc42-adc9-4fcd-8624-a7d252eae51e', 'How do you render Arrays, Strings, and Numbers in React 16+?', 'React 16+ allows returning arrays, strings, and numbers directly from `render()` without a wrapper element.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"You must always wrap in a `<div>`","isCorrect":false},{"id":"b","text":"You can return arrays, strings, and numbers directly","isCorrect":true},{"id":"c","text":"Only objects are allowed","isCorrect":false},{"id":"d","text":"Strings cause hydration errors","isCorrect":false}]', NULL, 'Before React 16, `render()` had to return a single root element. Now it can return arrays, strings, or numbers.', NULL, ARRAY[]::text[], ARRAY['react','fragments','rendering','arrays'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q167","original_type":"multiple-choice","topic":"Fragments & Rendering","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('a8967b3f-11bb-40db-aac5-262aaf18b387', 'What are hooks?', 'Hooks are functions that let you use state and other React features in function components without classes.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'Hooks like `useState` and `useEffect` enable stateful logic in functional components.', NULL, ARRAY[]::text[], ARRAY['react','hooks','useState','function-components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q168","original_type":"open-ended","topic":"React Hooks","subcategory":"React Hooks","sample_answers":["Hooks are functions like `useState` and `useEffect` that let function components manage state and side effects.","They allow extracting stateful logic into reusable custom hooks, improving code organization."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('2ea1b197-440c-4695-bc3b-eb5bdd5df0c3', 'What rules need to be followed for hooks?', '1. Call hooks only at the top level
2. Call hooks only from React functions (components or custom hooks)', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"Call hooks inside loops and conditions","isCorrect":false},{"id":"b","text":"Call hooks only at top level and from React functions","isCorrect":true},{"id":"c","text":"Hooks can be called from any JavaScript function","isCorrect":false},{"id":"d","text":"Order of hook calls doesn’t matter","isCorrect":false}]', NULL, 'These rules ensure hooks are called in the same order every render, preserving state consistency.', NULL, ARRAY[]::text[], ARRAY['react','hooks','rules-of-hooks','eslint'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q169","original_type":"multiple-choice","topic":"Rules of Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('4913419f-91ed-43f7-8b72-981ed6d9ffac', 'How to ensure hooks follow the rules in your project?', 'Use the `eslint-plugin-react-hooks` ESLint plugin with the `react-hooks/rules-of-hooks` rule enabled.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This plugin enforces the two rules of hooks and is included by default in Create React App.', NULL, ARRAY[]::text[], ARRAY['react','hooks','eslint','linting'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q170","original_type":"open-ended","topic":"Rules of Hooks","subcategory":"React Hooks","sample_answers":["Install `eslint-plugin-react-hooks` and enable `react-hooks/rules-of-hooks` in ESLint config to catch violations at build time.","The plugin also includes `exhaustive-deps` to validate effect dependencies."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('a5af9a12-d936-4f3a-b7e5-e298de071a57', 'What are the differences between Flux and Redux?', 'Flux has multiple mutable stores and a dispatcher; Redux has a single immutable store and no dispatcher.', 'multiple-choice', 'intermediate', 8, '[{"id":"a","text":"Flux uses immutable state; Redux uses mutable state","isCorrect":false},{"id":"b","text":"Flux: multiple mutable stores, dispatcher; Redux: single immutable store, no dispatcher","isCorrect":true},{"id":"c","text":"Redux has multiple dispatchers","isCorrect":false},{"id":"d","text":"They are identical","isCorrect":false}]', NULL, 'Redux simplifies Flux by using a single store, pure reducers, and no dispatcher.', NULL, ARRAY[]::text[], ARRAY['flux','redux','comparison','architecture'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q171","original_type":"multiple-choice","topic":"Flux vs Redux","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('5e483da9-d5e0-424e-b675-3bbc5ab6632e', 'What are the benefits of React Router V4?', '1. Component-based API
2. Automatic history management
3. Smaller bundle size via modular imports', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Requires manual history setup","isCorrect":false},{"id":"b","text":"Component-based, auto history, smaller bundles","isCorrect":true},{"id":"c","text":"Only works with class components","isCorrect":false},{"id":"d","text":"No support for dynamic routes","isCorrect":false}]', NULL, 'React Router v4 treats routes as components, simplifying composition and reducing boilerplate.', NULL, ARRAY[]::text[], ARRAY['react-router','routing','v4','components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q172","original_type":"multiple-choice","topic":"React Router","subcategory":"React Router","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('9ac84133-0429-492b-9538-53700ed853fb', 'Describe the componentDidCatch lifecycle method signature', '`componentDidCatch(error, info)` receives the error object and an info object with a componentStack.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This method is called after an error is thrown in a child component tree.', NULL, ARRAY[]::text[], ARRAY['react','error-boundaries','componentdidcatch','lifecycle'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q173","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["`componentDidCatch(error, info)` where `error` is the thrown error and `info.componentStack` shows the component hierarchy.","Used in error boundary components to log errors and display fallback UI."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('1c4223be-1c64-4b4f-92aa-5ad53abaee21', 'In which scenarios do error boundaries not catch errors?', '1. Event handlers
2. Async code (setTimeout)
3. Server-side rendering
4. Errors in the error boundary itself', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"They catch all errors everywhere","isCorrect":false},{"id":"b","text":"Not in event handlers, async code, SSR, or self-errors","isCorrect":true},{"id":"c","text":"They work in setTimeout callbacks","isCorrect":false},{"id":"d","text":"They catch syntax errors in modules","isCorrect":false}]', NULL, 'Error boundaries only catch errors during rendering, in lifecycle methods, and in constructors.', NULL, ARRAY[]::text[], ARRAY['react','error-boundaries','limitations','debugging'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q174","original_type":"multiple-choice","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('2f1f3188-3f46-4678-bc73-762ab7da15cc', 'What is the behavior of uncaught errors in React 16?', 'Uncaught errors cause React to unmount the entire component tree to prevent corrupted UI.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This prevents displaying inconsistent or dangerous UI (e.g., wrong payment amounts).', NULL, ARRAY[]::text[], ARRAY['react','error-handling','react-16','unmount'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q175","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["React 16 unmounts the whole app on uncaught errors to avoid showing corrupted state—better to show nothing than wrong data.","This behavior emphasizes the need for error boundaries to gracefully handle failures."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('3cec31d1-7874-4496-af50-e93bef40a446', 'What is the proper placement for error boundaries?', 'Place them at the top level for app-wide errors, or around individual components to isolate failures.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Granularity depends on UX needs—protect critical sections or provide global fallbacks.', NULL, ARRAY[]::text[], ARRAY['react','error-boundaries','best-practices','ui'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q176","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["Wrap top-level routes for a generic error page, or wrap individual widgets (e.g., comment section) to keep the rest of the app functional.","Balance between user experience and implementation complexity when deciding placement."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('f2c7ea86-080a-47b9-87e9-e91b726f121e', 'What is the benefit of component stack trace from error boundary?', 'It shows the component hierarchy with file names and line numbers, making debugging easier.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'Unlike generic JS stack traces, React’s component stack shows which component failed.', NULL, ARRAY[]::text[], ARRAY['react','debugging','stack-trace','error-boundaries'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q177","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["Error boundaries provide a component stack trace showing which component threw the error, including file and line number in development.","This makes it much easier to locate the source of rendering errors compared to raw JavaScript stack traces."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('ba04110e-318a-4826-a595-131028169391', 'What are default props?', '`defaultProps` sets fallback values for props when they are `undefined` (but not `null` or `0`).', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Default props only apply when a prop is not passed or is explicitly `undefined`.', NULL, ARRAY[]::text[], ARRAY['react','defaultprops','props','components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q178","original_type":"open-ended","topic":"Props","subcategory":"React Basics","sample_answers":["`MyButton.defaultProps = { color: ''red'' };` means if `<MyButton />` is used without a `color` prop, it defaults to ''red''.","Note: `null` and `0` are valid values and will not trigger defaultProps."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('dfda1526-8041-43fa-a511-110d7ee2325d', 'What is the purpose of displayName class property?', '`displayName` sets a custom name for a component in React DevTools and error messages.', 'multiple-choice', 'intermediate', 5, NULL, NULL, 'It’s especially useful for HOCs to show meaningful names like `withAuth(Button)`.', NULL, ARRAY[]::text[], ARRAY['react','displayname','devtools','hoc'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q179","original_type":"open-ended","topic":"Debugging","subcategory":"React Basics","sample_answers":["`displayName` helps identify components in React DevTools—critical for HOCs that would otherwise appear as ''Anonymous''.","Example: `WithSubscription.displayName = ''WithSubscription(UserList)'';`"],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
