-- Batch 18: Questions 171-180
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES
('06052c78-b258-4dd3-83e9-7abef7617a2c', 'How to ensure hooks follow the rules in your project?', 'Use the `eslint-plugin-react-hooks` ESLint plugin with the `react-hooks/rules-of-hooks` rule enabled.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This plugin enforces the two rules of hooks and is included by default in Create React App.', NULL, ARRAY[], ARRAY['react','hooks','eslint','linting'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q170","original_type":"open-ended","topic":"Rules of Hooks","subcategory":"React Hooks","sample_answers":["Install `eslint-plugin-react-hooks` and enable `react-hooks/rules-of-hooks` in ESLint config to catch violations at build time.","The plugin also includes `exhaustive-deps` to validate effect dependencies."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('464acfa0-ebdf-470a-9a10-84fb432cf088', 'What are the differences between Flux and Redux?', 'Flux has multiple mutable stores and a dispatcher; Redux has a single immutable store and no dispatcher.', 'multiple-choice', 'intermediate', 8, '[{"id":"a","text":"Flux uses immutable state; Redux uses mutable state","isCorrect":false},{"id":"b","text":"Flux: multiple mutable stores, dispatcher; Redux: single immutable store, no dispatcher","isCorrect":true},{"id":"c","text":"Redux has multiple dispatchers","isCorrect":false},{"id":"d","text":"They are identical","isCorrect":false}]', NULL, 'Redux simplifies Flux by using a single store, pure reducers, and no dispatcher.', NULL, ARRAY[], ARRAY['flux','redux','comparison','architecture'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q171","original_type":"multiple-choice","topic":"Flux vs Redux","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('e9c111fe-7bb2-44c6-9329-ae935ba55e9e', 'What are the benefits of React Router V4?', '1. Component-based API
2. Automatic history management
3. Smaller bundle size via modular imports', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Requires manual history setup","isCorrect":false},{"id":"b","text":"Component-based, auto history, smaller bundles","isCorrect":true},{"id":"c","text":"Only works with class components","isCorrect":false},{"id":"d","text":"No support for dynamic routes","isCorrect":false}]', NULL, 'React Router v4 treats routes as components, simplifying composition and reducing boilerplate.', NULL, ARRAY[], ARRAY['react-router','routing','v4','components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q172","original_type":"multiple-choice","topic":"React Router","subcategory":"React Router","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('07de42f2-8044-4cc4-b6a5-e68e2fe61489', 'Describe the componentDidCatch lifecycle method signature', '`componentDidCatch(error, info)` receives the error object and an info object with a componentStack.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This method is called after an error is thrown in a child component tree.', NULL, ARRAY[], ARRAY['react','error-boundaries','componentdidcatch','lifecycle'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q173","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["`componentDidCatch(error, info)` where `error` is the thrown error and `info.componentStack` shows the component hierarchy.","Used in error boundary components to log errors and display fallback UI."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('aae42ccb-e576-44a8-83f6-70b62a9ee587', 'In which scenarios do error boundaries not catch errors?', '1. Event handlers
2. Async code (setTimeout)
3. Server-side rendering
4. Errors in the error boundary itself', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"They catch all errors everywhere","isCorrect":false},{"id":"b","text":"Not in event handlers, async code, SSR, or self-errors","isCorrect":true},{"id":"c","text":"They work in setTimeout callbacks","isCorrect":false},{"id":"d","text":"They catch syntax errors in modules","isCorrect":false}]', NULL, 'Error boundaries only catch errors during rendering, in lifecycle methods, and in constructors.', NULL, ARRAY[], ARRAY['react','error-boundaries','limitations','debugging'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q174","original_type":"multiple-choice","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('c3ca2cfd-98e3-45fb-b6bb-84fccea8a2cd', 'What is the behavior of uncaught errors in React 16?', 'Uncaught errors cause React to unmount the entire component tree to prevent corrupted UI.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This prevents displaying inconsistent or dangerous UI (e.g., wrong payment amounts).', NULL, ARRAY[], ARRAY['react','error-handling','react-16','unmount'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q175","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["React 16 unmounts the whole app on uncaught errors to avoid showing corrupted state—better to show nothing than wrong data.","This behavior emphasizes the need for error boundaries to gracefully handle failures."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('618c7212-c1ac-4034-a80e-d685f8fcf026', 'What is the proper placement for error boundaries?', 'Place them at the top level for app-wide errors, or around individual components to isolate failures.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Granularity depends on UX needs—protect critical sections or provide global fallbacks.', NULL, ARRAY[], ARRAY['react','error-boundaries','best-practices','ui'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q176","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["Wrap top-level routes for a generic error page, or wrap individual widgets (e.g., comment section) to keep the rest of the app functional.","Balance between user experience and implementation complexity when deciding placement."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('47062a21-79dc-43a0-905a-f8efcc2ce886', 'What is the benefit of component stack trace from error boundary?', 'It shows the component hierarchy with file names and line numbers, making debugging easier.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'Unlike generic JS stack traces, React’s component stack shows which component failed.', NULL, ARRAY[], ARRAY['react','debugging','stack-trace','error-boundaries'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q177","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["Error boundaries provide a component stack trace showing which component threw the error, including file and line number in development.","This makes it much easier to locate the source of rendering errors compared to raw JavaScript stack traces."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('5fc3b540-e466-431d-aff8-c9c18f700fe6', 'What are default props?', '`defaultProps` sets fallback values for props when they are `undefined` (but not `null` or `0`).', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Default props only apply when a prop is not passed or is explicitly `undefined`.', NULL, ARRAY[], ARRAY['react','defaultprops','props','components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q178","original_type":"open-ended","topic":"Props","subcategory":"React Basics","sample_answers":["`MyButton.defaultProps = { color: ''red'' };` means if `<MyButton />` is used without a `color` prop, it defaults to ''red''.","Note: `null` and `0` are valid values and will not trigger defaultProps."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('b56f4fc2-f703-43e2-beda-62e3a84098aa', 'What is the purpose of displayName class property?', '`displayName` sets a custom name for a component in React DevTools and error messages.', 'multiple-choice', 'intermediate', 5, NULL, NULL, 'It’s especially useful for HOCs to show meaningful names like `withAuth(Button)`.', NULL, ARRAY[], ARRAY['react','displayname','devtools','hoc'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q179","original_type":"open-ended","topic":"Debugging","subcategory":"React Basics","sample_answers":["`displayName` helps identify components in React DevTools—critical for HOCs that would otherwise appear as ''Anonymous''.","Example: `WithSubscription.displayName = ''WithSubscription(UserList)'';`"],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
