       subtotalSelector,
       taxPercentSelector,
       (subtotal, taxPercent) =&gt; subtotal * (taxPercent / 100)
     );

     export const totalSelector = createSelector(
       subtotalSelector,
       taxSelector,
       (subtotal, tax) =&gt; ({ total: subtotal + tax })
     );

