**Proper Form Structure:**

<form>
<fieldset>
<legend>Personal Information</legend>

<div class="field">
<label for="first-name">First Name *</label>
<input
type="text"
id="first-name"
name="firstName"
required
aria-describedby="first-name-help"
/>
<div id="first-name-help" class="help-text">
Enter your legal first name
</div>
</div>

<div class="field">
<label for="email">Email Address *</label>
<input
type="email"
id="email"
name="email"
required
aria-describedby="email-help email-error"
aria-invalid="false"
/>
<div id="email-help" class="help-text">We'll never share your email</div>
<div id="email-error" class="error-text" role="alert" aria-live="polite">
<!-- Error messages appear here -->
</div>
</div>

<fieldset>
<legend>Newsletter Preferences</legend>
<div class="checkbox-group">
<input type="checkbox" id="newsletter" name="newsletter" />
<label for="newsletter">Subscribe to newsletter</label>
</div>
</fieldset>

<button type="submit">Submit</button>
</fieldset>
</form>

**Form Validation:**

class AccessibleForm {
constructor(form) {
this.form = form;
this.setupValidation();
}

setupValidation() {
this.form.addEventListener('submit', e => {
e.preventDefault();
this.validateForm();
});

// Real-time validation
this.form.addEventListener(
'blur',
e => {
if (e.target.matches('input, select, textarea')) {
this.validateField(e.target);
}
},
true
);
}

validateField(field) {
const errorElement = document.getElementById(`${field.id}-error`);
const isValid = field.checkValidity();

field.setAttribute('aria-invalid', !isValid);

if (!isValid) {
const errorMessage = this.getErrorMessage(field);
errorElement.textContent = errorMessage;
errorElement.setAttribute('role', 'alert');
} else {
errorElement.textContent = '';
errorElement.removeAttribute('role');
}
}

getErrorMessage(field) {
if (field.validity.valueMissing) {
return `${field.labels[0].textContent} is required`;
}
if (field.validity.typeMismatch) {
return `Please enter a valid ${field.type}`;
}
if (field.validity.patternMismatch) {
return `Please match the requested format`;
}
return 'Please correct this field';
}

validateForm() {
const fields = this.form.querySelectorAll('input, select, textarea');
let isValid = true;

fields.forEach(field => {
this.validateField(field);
if (!field.checkValidity()) {
isValid = false;
}
});

if (isValid) {
this.submitForm();
} else {
// Focus first invalid field
const firstInvalid = this.form.querySelector('[aria-invalid="true"]');
if (firstInvalid) {
firstInvalid.focus();
}
}
}
}

