return value;
}

console.log('Cache miss for key:', key);
// Cache miss - get from database
value = await this.db.get(key);
if (value) {
// Update cache
await this.cache.set(key, value, 3600); // 1 hour TTL
}
return value;
}

async set(key, value) {
// Write to database
await this.db.set(key, value);
// Update cache
await this.cache.set(key, value, 3600);
}

async delete(key) {
