**Focus Management:**

// Focus trap for modals
class FocusTrap {
  constructor(element) {
    this.element = element;
    this.focusableElements = this.getFocusableElements();
    this.firstFocusable = this.focusableElements[0];
    this.lastFocusable =
      this.focusableElements[this.focusableElements.length - 1];
  }

  getFocusableElements() {
    const selector =
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])';
    return Array.from(this.element.querySelectorAll(selector));
  }

  trapFocus(e) {
    if (e.key === 'Tab') {
      if (e.shiftKey) {
        if (document.activeElement === this.firstFocusable) {
          e.preventDefault();
          this.lastFocusable.focus();
        }
      } else {
        if (document.activeElement === this.lastFocusable) {
          e.preventDefault();
          this.firstFocusable.focus();
        }
      }
    }
  }

  activate() {
    this.element.addEventListener('keydown', this.trapFocus.bind(this));
    this.firstFocusable.focus();
  }

  deactivate() {
    this.element.removeEventListener('keydown', this.trapFocus.bind(this));
  }
}

// Usage
const modal = document.getElementById('modal');
const focusTrap = new FocusTrap(modal);
focusTrap.activate();

**Custom Keyboard Navigation:**

// Arrow key navigation for custom components
class CustomSelect {
  constructor(element) {
    this.element = element;
    this.options = Array.from(element.querySelectorAll('[role="option"]'));
    this.selectedIndex = 0;
    this.setupKeyboardNavigation();
  }

  setupKeyboardNavigation() {
    this.element.addEventListener('keydown', e => {
      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          this.selectNext();
          break;
        case 'ArrowUp':
          e.preventDefault();
          this.selectPrevious();
          break;
        case 'Enter':
        case ' ':
          e.preventDefault();
          this.activateOption();
          break;
        case 'Escape':
          this.close();
          break;
        case 'Home':
          e.preventDefault();
          this.selectFirst();
          break;
        case 'End':
          e.preventDefault();
          this.selectLast();
          break;
      }
    });
  }

  selectNext() {
    this.selectedIndex = Math.min(
      this.selectedIndex + 1,
      this.options.length - 1
    );
    this.updateSelection();
  }

  selectPrevious() {
    this.selectedIndex = Math.max(this.selectedIndex - 1, 0);
    this.updateSelection();
  }

  updateSelection() {
    this.options.forEach((option, index) => {
      option.setAttribute('aria-selected', index === this.selectedIndex);
      if (index === this.selectedIndex) {
        option.focus();
      }
    });
  }
}

