-- Batch 8: Questions 141-160
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES
('395e4b98-8b49-48d9-a000-25aeb4ecf67c', 'What is Flow?', 'Flow is a static type checker for JavaScript that catches type errors at compile time.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'Flow analyzes code with type annotations to find bugs like null dereferences before runtime.', NULL, ARRAY[]::text[], ARRAY['flow','typescript','static-typing','javascript'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q141","original_type":"open-ended","topic":"Static Type Checking","subcategory":"TypeScript Basics","sample_answers":["Flow is a static analysis tool that adds type annotations to JavaScript and checks for type errors during development.","It helps catch common bugs like calling methods on null or passing wrong argument types."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('91e331b2-c27e-4112-a599-9c0641caf757', 'What is the difference between Flow and PropTypes?', 'Flow is a static type checker (compile-time); PropTypes is a runtime type checker for React component props.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"PropTypes is static; Flow is runtime","isCorrect":false},{"id":"b","text":"Flow is static (compile-time); PropTypes is runtime (only for props)","isCorrect":true},{"id":"c","text":"Both are identical","isCorrect":false},{"id":"d","text":"PropTypes can check entire codebase","isCorrect":false}]', NULL, 'Flow checks all code at build time; PropTypes only checks component props at runtime and is less comprehensive.', NULL, ARRAY[]::text[], ARRAY['flow','proptypes','typing','react'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q142","original_type":"multiple-choice","topic":"Static vs Runtime Typing","subcategory":"TypeScript Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('3497298d-9bb8-4fdc-a0eb-e7cfc10b659f', 'How to use Font Awesome icons in React?', 'Install `font-awesome`, import CSS in `index.js`, and use `<i className="fa fa-icon-name" />`.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'Font Awesome provides icon fonts; you include the CSS and use class names in JSX.', NULL, ARRAY[]::text[], ARRAY['react','font-awesome','icons','css'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q143","original_type":"open-ended","topic":"Third-Party Libraries","subcategory":"React Basics","sample_answers":["Install `font-awesome` via npm, import the CSS file in your entry point, then use `<i className=\\"fa fa-spinner\\" />` in components.","Note: Modern apps often use SVG-based icon libraries like `react-icons` instead of font-based ones."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('ddc3ff5e-82d3-460b-a7e7-c958dbb6415b', 'What is React Dev Tools?', 'React DevTools is a browser extension or standalone app for inspecting React component hierarchy, props, and state.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"A code formatter","isCorrect":false},{"id":"b","text":"A tool to inspect React components, props, and state","isCorrect":true},{"id":"c","text":"Only works with Angular","isCorrect":false},{"id":"d","text":"A testing framework","isCorrect":false}]', NULL, 'It lets you explore the component tree, view props/state, and profile performance.', NULL, ARRAY[]::text[], ARRAY['react-devtools','debugging','chrome-extension'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q144","original_type":"multiple-choice","topic":"React Developer Tools","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('80cfd1b5-bca3-489e-8d46-10b4ae5adb02', 'Why is DevTools not loading in Chrome for local files?', 'Because Chrome blocks extensions from accessing `file://` URLs by default. You must enable ''Allow access to file URLs'' in extension settings.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Chrome extensions are sandboxed; you must manually allow file URL access in `chrome://extensions`.', NULL, ARRAY[]::text[], ARRAY['react-devtools','chrome','file-urls'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q145","original_type":"open-ended","topic":"React Developer Tools","subcategory":"React Basics","sample_answers":["Open `chrome://extensions`, find React DevTools, and check ''Allow access to file URLs'' to enable it for local HTML files.","Alternatively, serve your app via a local server (e.g., `npx serve`) instead of opening `file://` directly."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('f4a42f41-99f2-4bca-b903-1ad7253e1546', 'How to use Polymer in React?', 'Create a Polymer element, import it in HTML, and use the custom element tag in JSX.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'React can render custom elements defined by Polymer by treating them as regular HTML tags.', NULL, ARRAY[]::text[], ARRAY['react','polymer','web-components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q146","original_type":"open-ended","topic":"Web Components","subcategory":"Integration","sample_answers":["Define a Polymer element, import its HTML file in your main `index.html`, then use `<calendar-element />` in your React JSX.","Note: You may need to handle event naming differences (e.g., `onMyEvent` vs `onmyevent`) and property binding carefully."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('48d210d9-fcc6-4a9e-8b57-f1f80633b370', 'What are the advantages of React over Vue.js?', 'React offers more flexibility in large apps, easier testing, better mobile support (React Native), and a larger ecosystem.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Vue has better mobile support","isCorrect":false},{"id":"b","text":"React offers more flexibility, easier testing, React Native, and larger ecosystem","isCorrect":true},{"id":"c","text":"Vue is harder to learn","isCorrect":false},{"id":"d","text":"React has no advantages","isCorrect":false}]', NULL, 'These are common perceived advantages, though both frameworks are capable.', NULL, ARRAY[]::text[], ARRAY['react','vue','comparison','ecosystem'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q147","original_type":"multiple-choice","topic":"React Ecosystem","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('593cd126-61cc-4589-a766-21a017d799a6', 'What is the difference between React and Angular?', 'React is a UI library with JSX and unidirectional data flow; Angular is a full framework with templates and two-way binding.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"React is a full framework like Angular","isCorrect":false},{"id":"b","text":"React is a library (View only); Angular is a full framework (MVC)","isCorrect":true},{"id":"c","text":"Angular uses JSX","isCorrect":false},{"id":"d","text":"React has two-way data binding","isCorrect":false}]', NULL, 'React is view-only, uses JavaScript syntax (JSX), and has one-way data flow. Angular is a full MVC framework with HTML templates and two-way binding.', NULL, ARRAY[]::text[], ARRAY['react','angular','comparison','frameworks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q148","original_type":"multiple-choice","topic":"Framework Comparison","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('6f784a67-ac7b-4f61-9444-dcca9a3cb28c', 'Why React tab is not showing up in DevTools?', 'Because the page isn’t using React, or React failed to initialize and communicate with DevTools.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'DevTools detects React via a global hook set during React initialization. If React isn’t loaded or is in production mode, the tab won’t appear.', NULL, ARRAY[]::text[], ARRAY['react-devtools','debugging','setup'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q149","original_type":"open-ended","topic":"React Developer Tools","subcategory":"React Basics","sample_answers":["The React DevTools tab appears only if React is loaded on the page and not in production mode. Check that you’re using a development build of React.","If using a bundler, ensure React is included and the app is running in development mode."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('6dcd4e7b-9501-4024-adf7-a329a2a86e8e', 'What are Styled Components?', '`styled-components` is a CSS-in-JS library that lets you write actual CSS in JavaScript to style React components.', 'multiple-choice', 'beginner', 6, NULL, NULL, 'It removes the mapping between styles and components by generating unique class names and injecting styles automatically.', NULL, ARRAY[]::text[], ARRAY['styled-components','css-in-js','react','styling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q150","original_type":"open-ended","topic":"Styled Components","subcategory":"CSS-in-JS","sample_answers":["`styled-components` allows you to write CSS inside JavaScript template literals, creating styled React components with automatic scoping and vendor prefixing.","Example: `const Title = styled.h1\\\\`color: red;\\\\`;` creates a component that renders an `<h1>` with red text."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('f3335ac3-cff7-4598-b717-547b997f109b', 'Give an example of Styled Components', 'Create styled `Title` and `Wrapper` components using `styled.h1` and `styled.section` with CSS inside backticks.', 'multiple-choice', 'beginner', 6, NULL, NULL, 'Styled Components uses tagged template literals to define styles, creating components that apply those styles when rendered.', NULL, ARRAY[]::text[], ARRAY['styled-components','example','react','css'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q151","original_type":"open-ended","topic":"Styled Components","subcategory":"CSS-in-JS","sample_answers":["```javascript\\nimport styled from ''styled-components'';\\nconst Title = styled.h1`\\n  font-size: 1.5em;\\n  color: palevioletred;\\n`;\\nconst Wrapper = styled.section`\\n  padding: 4em;\\n  background: papayawhip;\\n`;\\n// Usage: <Wrapper><Title>Hello</Title></Wrapper>\\n```","The `styled` function takes a tag name and returns a component that renders that tag with the specified styles."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('be73ece6-cc1e-482d-815c-85375a4bde49', 'Give an example of Styled Components?', 'Create styled `<Title>` and `<Wrapper>` components using `styled.h1` and `styled.section` with CSS inside backticks.

```javascript
import React from "react";
import styled from "styled-components";

const Title = styled.h1`
  font-size: 1.5em;
  text-align: center;
  color: palevioletred;
`;

const Wrapper = styled.section`
  padding: 4em;
  background: papayawhip;
`;
```

Usage:
```jsx
<Wrapper>
  <Title>{"Lets start first styled component!"}</Title>
</Wrapper>
```', 'multiple-choice', 'beginner', 6, NULL, NULL, 'Styled Components uses tagged template literals to define scoped CSS that is automatically injected and applied to React components.', NULL, ARRAY[]::text[], ARRAY['styled-components','css-in-js','react','styling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q151","original_type":"open-ended","topic":"Styled Components","subcategory":"CSS-in-JS","sample_answers":["The `styled` function creates a new React component with encapsulated styles. Example: `const Title = styled.h1\\\\`color: red;\\\\`;` renders an `<h1>` with red text.","Styled Components generates unique class names to avoid style collisions and supports dynamic styling via props."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('6660b0c7-cb5d-421e-8818-e895e3a7f08b', 'What is Relay?', 'Relay is a JavaScript framework for providing a data layer and client-server communication to web applications using the React view layer.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'Relay is a GraphQL client that manages server-originated state, handles caching, and optimizes data fetching for React applications.', NULL, ARRAY[]::text[], ARRAY['relay','graphql','react','data-fetching'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q152","original_type":"open-ended","topic":"Relay","subcategory":"State Management","sample_answers":["Relay is a GraphQL-based data-fetching framework for React that automatically manages caching, pagination, and mutations with strong typing.","Unlike Redux, Relay only stores data fetched from a GraphQL server and uses declarative queries to specify component data needs."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('0aa190eb-21b7-46aa-84d9-bfbf1ae31466', 'What are the main features of Reselect library?', '1. Selectors compute derived data
2. Selectors are efficient (memoized)
3. Selectors are composable', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"It replaces reducers","isCorrect":false},{"id":"b","text":"It provides memoized, composable selectors for derived data","isCorrect":true},{"id":"c","text":"It handles async actions","isCorrect":false},{"id":"d","text":"It manages global state outside Redux","isCorrect":false}]', NULL, 'Reselect enables performant derived data computation in Redux apps through memoized, composable selector functions.', NULL, ARRAY[]::text[], ARRAY['reselect','selectors','redux','memoization'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q153","original_type":"multiple-choice","topic":"Reselect","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('33a83310-4581-4202-8489-7bb58cdbeac0', 'Give an example of Reselect usage?', 'Use `createSelector` to build memoized selectors for subtotal, tax, and total from shop state.', 'multiple-choice', 'intermediate', 8, NULL, NULL, 'Reselect’s `createSelector` takes input selectors and a result function, caching output until inputs change.', NULL, ARRAY[]::text[], ARRAY['reselect','redux','selectors','example'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q154","original_type":"open-ended","topic":"Reselect","subcategory":"State Management","sample_answers":["```javascript\\nimport { createSelector } from ''reselect'';\\nconst items = state => state.shop.items;\\nconst subtotal = createSelector(items, i => i.reduce((a, b) => a + b.value, 0));\\n```","Selectors can be composed: tax depends on subtotal, total depends on both—each recomputes only when its dependencies change."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('42d4d74f-36ed-40ac-ac30-b90320f1f8e5', 'Can Redux only be used with React?', 'No. Redux is a standalone state container that can be used with any UI layer (Angular, Vue, etc.) or even without a UI.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Redux provides a subscription mechanism that any library or framework can use to listen to state changes.', NULL, ARRAY[]::text[], ARRAY['redux','framework-agnostic','state-management'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q155","original_type":"true-false","topic":"Redux","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('973c380c-f1a5-4dfe-806a-b198b8c4a33e', 'Do you need a particular build tool to use Redux?', 'No. Redux offers ES5 UMD builds that work without any build process, though it’s commonly used with modern tooling.', 'multiple-choice', 'beginner', 3, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Redux provides multiple builds (ESM, CJS, UMD) and works with or without Webpack, Babel, etc.', NULL, ARRAY[]::text[], ARRAY['redux','build-tools','umd','bundling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q156","original_type":"true-false","topic":"Redux","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('1547e4c2-f382-45be-8736-d06897b97149', 'How does Redux Form `initialValues` get updated from state?', 'Set `enableReinitialize: true` in the `reduxForm` config to allow `initialValues` prop updates to reset the form.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'By default, Redux Form initializes only once. `enableReinitialize: true` makes it responsive to `initialValues` prop changes.', NULL, ARRAY[]::text[], ARRAY['redux-form','initialvalues','react','forms'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q157","original_type":"open-ended","topic":"Redux Form","subcategory":"State Management","sample_answers":["Pass `enableReinitialize: true` to the `reduxForm()` HOC to allow the form to reinitialize when the `initialValues` prop changes.","Example: `const Form = reduxForm({ form: ''user'', enableReinitialize: true })(UserForm);`"],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('76152284-01a6-4f93-8a2a-a649fbfd4c6c', 'How do React PropTypes allow different types for one prop?', 'Use `PropTypes.oneOfType([PropTypes.string, PropTypes.number])` to accept multiple types for a single prop.', 'multiple-choice', 'beginner', 5, NULL, NULL, '`PropTypes.oneOfType` accepts an array of type validators, allowing flexible prop definitions.', NULL, ARRAY[]::text[], ARRAY['react','proptypes','validation','typing'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q158","original_type":"open-ended","topic":"PropTypes","subcategory":"React Basics","sample_answers":["Use `PropTypes.oneOfType([PropTypes.string, PropTypes.number])` to allow a prop to be either a string or a number.","This is useful for props like `size` that might accept ''large'' (string) or 24 (number)."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('a55dd1e4-b2f8-4efa-9459-4c71c3cbf50e', 'Can I import an SVG file as a React component?', 'Yes, with `react-scripts@2.0.0+`, use `import { ReactComponent as Logo } from ''./logo.svg'';` to treat SVGs as components.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'CRA supports importing SVGs as React components, enabling props, styling, and tree-shaking.', NULL, ARRAY[]::text[], ARRAY['react','svg','assets','create-react-app'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q159","original_type":"true-false","topic":"Asset Handling","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
