INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES
('1dc6798e-63bd-4884-9552-6ca01fecc23b', 'How do you set default value for uncontrolled component?', 'Use `defaultValue` for text inputs and `defaultChecked` for checkboxes/radios to set initial values without controlling subsequent updates.', 'multiple-choice', 'beginner', 5, NULL, NULL, '`defaultValue` sets the initial DOM value but doesn’t override user changes, unlike `value` which fully controls the input.', NULL, ARRAY[]::text[], ARRAY['react','forms','uncontrolled','defaultvalue'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q210","original_type":"open-ended","topic":"Uncontrolled Components","subcategory":"Forms","sample_answers":["Use `<input defaultValue=\"John\" />` to set the initial value of an uncontrolled input. For checkboxes, use `defaultChecked={true}`.","This is useful when you need to initialize a form but don’t want to manage every keystroke with state."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('08bc25fe-5922-4e9a-af13-6f2b9b5c0804', 'What is your favorite React stack?', 'A popular stack includes Redux/redux-saga for state, react-router for routing, styled-components for styling, axios for API calls, and webpack/Babel for tooling.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This stack balances maturity, performance, and developer experience for large-scale applications.', NULL, ARRAY[]::text[], ARRAY['react','boilerplate','redux','saga','tooling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q211","original_type":"open-ended","topic":"React Ecosystem","subcategory":"React Ecosystem","sample_answers":["A robust React stack: Redux + redux-saga for state and side effects, React Router for navigation, styled-components for styling, axios for HTTP, and Webpack/Babel for builds.","Projects like react-boilerplate provide this setup out of the box for production-ready apps."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('0f366d04-1b32-43c6-9c09-6fbc446de0e8', 'What is the difference between Real DOM and Virtual DOM?', 'Real DOM updates are slow and expensive; Virtual DOM is a lightweight JS representation that enables fast diffing and minimal real DOM mutations.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"Real DOM is faster than Virtual DOM","isCorrect":false},{"id":"b","text":"Real DOM updates are slow/expensive; Virtual DOM enables fast, minimal updates","isCorrect":true},{"id":"c","text":"Virtual DOM directly manipulates HTML","isCorrect":false},{"id":"d","text":"They are the same","isCorrect":false}]', NULL, 'Virtual DOM allows React to compute the minimal set of changes before touching the real DOM, optimizing performance.', NULL, ARRAY[]::text[], ARRAY['react','virtual-dom','real-dom','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q212","original_type":"multiple-choice","topic":"Virtual DOM","subcategory":"Virtual DOM","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"0ee220a5-c50b-4c32-afc5-a654931c9fcd"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('93e04b17-ed4c-4bb1-bb46-ca72764b26e5', 'How to add Bootstrap to a React application?', 'Three ways: (1) CDN, (2) npm install bootstrap + import CSS, (3) React-specific libraries like react-bootstrap or reactstrap.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"Only via CDN","isCorrect":false},{"id":"b","text":"CDN, npm install, or React wrappers like react-bootstrap","isCorrect":true},{"id":"c","text":"Bootstrap is built into React","isCorrect":false},{"id":"d","text":"Only with Sass","isCorrect":false}]', NULL, 'React-specific libraries (react-bootstrap) wrap Bootstrap components as React components, avoiding jQuery dependency.', NULL, ARRAY[]::text[], ARRAY['react','bootstrap','styling','css'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q213","original_type":"multiple-choice","topic":"Third-Party Libraries","subcategory":"Styling","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('beac778a-6383-4604-bbf8-c17dc2bebd49', 'Can you list top websites using React?', 'Top sites: Facebook, Instagram, WhatsApp, Uber, Airbnb, Netflix, PayPal, Khan Academy, Dropbox, Flipboard.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'React’s component model and performance make it ideal for large, dynamic user interfaces.', NULL, ARRAY[]::text[], ARRAY['react','companies','adoption','real-world'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q214","original_type":"open-ended","topic":"React Adoption","subcategory":"React Ecosystem","sample_answers":["Major companies using React include Facebook (creator), Instagram, WhatsApp, Airbnb, Netflix, Uber, and PayPal.","These companies rely on React for its declarative UI, performance, and rich ecosystem."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('5413d289-313a-4b3d-8acd-8515332f6353', 'Is it recommended to use CSS In JS in React?', 'React has no opinion, but beginners should start with CSS files and className. CSS-in-JS (e.g., styled-components) is a valid alternative for scoped styles.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'CSS-in-JS offers benefits like dynamic styling and scoping, but traditional CSS is simpler for beginners.', NULL, ARRAY[]::text[], ARRAY['react','css-in-js','styled-components','styling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q215","original_type":"true-false","topic":"CSS-in-JS","subcategory":"CSS-in-JS","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('f3bc2f03-db05-4b88-b23c-c2df9bb69d8c', 'Do I need to rewrite all my class components with hooks?', 'No. Hooks are additive; you can use them in new components without rewriting existing class components.', 'multiple-choice', 'beginner', 3, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'React has no plans to remove class components. Hooks and classes can coexist in the same app.', NULL, ARRAY[]::text[], ARRAY['react','hooks','class-components','migration'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q216","original_type":"true-false","topic":"React Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('acfeb77c-c7f8-40b7-9b8c-8cdb2322c28f', 'What is useEffect hook? How to fetch data with React Hooks?', '`useEffect` handles side effects in function components. Use it with `useState` to fetch data on mount by passing an empty dependency array.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It replaces componentDidMount, componentDidUpdate, and componentWillUnmount with a unified API.', NULL, ARRAY[]::text[], ARRAY['react','useeffect','data-fetching','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q217","original_type":"open-ended","topic":"useEffect","subcategory":"React Hooks","sample_answers":["`useEffect(() => { fetch(url).then(setData); }, []);` fetches data once on mount. The empty array ensures it doesn’t re-run on updates.","Always include cleanup for subscriptions or async operations to avoid memory leaks or state updates on unmounted components."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('ddef7b75-e532-4c1d-8875-b73627e895a3', 'Is Hooks cover all use cases for classes?', 'Not yet. Hooks don’t cover `getSnapshotBeforeUpdate` and `componentDidCatch` (error boundaries).', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Error boundaries and snapshot reading are still class-only features, though the React team plans to add Hook equivalents.', NULL, ARRAY[]::text[], ARRAY['react','hooks','class-lifecycle','limitations'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q218","original_type":"true-false","topic":"React Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('ae3efa8c-c82e-4b57-97a1-b5417e4b378f', 'What is the stable release for hooks support?', 'React 16.8 introduced stable Hooks support for React DOM, React DOM Server, React Test Renderer, and React Shallow Renderer.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'React 16.8 (February 2019) was the first version to include Hooks as a stable feature.', NULL, ARRAY[]::text[], ARRAY['react','hooks','16.8','release'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q219","original_type":"open-ended","topic":"React Hooks","subcategory":"React Hooks","sample_answers":["React 16.8, released in February 2019, was the first stable version to include Hooks across all official React packages.","This allowed function components to use state and side effects without classes."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
