INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES
('2acfad17-083c-470b-8167-edec4162e386', 'How to structure Redux top-level directories?', 'Common structure: `components/`, `containers/`, `actions/`, `reducers/`, `store/`.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"All files in one folder","isCorrect":false},{"id":"b","text":"`components/`, `containers/`, `actions/`, `reducers/`, `store/`","isCorrect":true},{"id":"c","text":"Only `src/` and `test/`","isCorrect":false},{"id":"d","text":"No standard structure exists","isCorrect":false}]', NULL, 'This structure separates concerns: dumb components, smart containers, action creators, reducers, and store setup.', NULL, ARRAY[], ARRAY['redux','structure','project-organization'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q121","original_type":"multiple-choice","topic":"Redux Project Structure","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('21a9ec15-3423-4101-a571-91002d2a2326', 'What is redux-saga?', '`redux-saga` is a middleware for handling side effects in Redux using generator functions.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It uses ES6 generators to manage complex async flows (e.g., cancellable requests, race conditions) in a declarative way.', NULL, ARRAY[], ARRAY['redux','saga','middleware','side-effects'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q122","original_type":"open-ended","topic":"Redux Saga","subcategory":"State Management","sample_answers":["`redux-saga` is a Redux middleware that uses generator functions to handle side effects like API calls, allowing complex async logic to be testable and cancellable.","It treats side effects as separate ''sagas'' that listen for actions and perform tasks using effects like `call()` and `put()`."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('5c96d04f-16d9-48fb-9f76-73ade3bf24a2', 'What is the mental model of redux-saga?', 'A saga is like a background thread that handles side effects, started/paused/cancelled via Redux actions.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'Sagas run in the background, watch for actions, and can dispatch new actions—all while being testable and cancellable.', NULL, ARRAY[], ARRAY['redux-saga','generators','side-effects'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q123","original_type":"open-ended","topic":"Redux Saga","subcategory":"State Management","sample_answers":["Think of a saga as a daemon process that lives alongside your app, listening for dispatched actions and performing side effects in response.","It’s a separate thread of execution managed by Redux middleware, using generators for async control flow."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('2893d996-098d-412a-bb92-18740ed11bd9', 'What are the differences between `call()` and `put()` in redux-saga?', '`call()` invokes a function (e.g., API call); `put()` dispatches an action to the store.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"`call()` dispatches actions; `put()` makes API calls","isCorrect":false},{"id":"b","text":"`call()` invokes functions; `put()` dispatches actions","isCorrect":true},{"id":"c","text":"Both are used only for logging","isCorrect":false},{"id":"d","text":"`put()` is for database queries","isCorrect":false}]', NULL, '`call(fn, ...args)` is for invoking functions; `put(action)` is for dispatching actions.', NULL, ARRAY[], ARRAY['redux-saga','call','put','effects'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q124","original_type":"multiple-choice","topic":"Redux Saga","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('d871e147-661d-44da-a2d2-04174e334ca1', 'What is Redux Thunk?', 'Redux Thunk is middleware that allows action creators to return a function instead of an action object, enabling async logic.', 'multiple-choice', 'beginner', 6, NULL, NULL, 'The thunk function receives `dispatch` and `getState`, allowing conditional or delayed dispatch.', NULL, ARRAY[], ARRAY['redux','thunk','middleware','async'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q125","original_type":"open-ended","topic":"Redux Thunk","subcategory":"State Management","sample_answers":["Redux Thunk lets you write action creators that return a function, which can perform async operations and dispatch actions when needed.","It’s the simplest way to handle async logic in Redux, using functions instead of plain action objects."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('54019b7b-035d-4a87-942b-3e551146b3bf', 'What are the differences between `redux-saga` and `redux-thunk`?', 'Thunk uses Promises and is simple; Saga uses Generators and is powerful for complex flows.', 'multiple-choice', 'intermediate', 8, '[{"id":"a","text":"Saga uses Promises; Thunk uses Generators","isCorrect":false},{"id":"b","text":"Thunk uses Promises; Saga uses Generators","isCorrect":true},{"id":"c","text":"Both are identical","isCorrect":false},{"id":"d","text":"Saga is deprecated","isCorrect":false}]', NULL, 'Thunk is easier for simple cases; Saga is better for complex async logic (cancellation, race conditions, etc.).', NULL, ARRAY[], ARRAY['redux','thunk','saga','middleware'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q126","original_type":"multiple-choice","topic":"Redux Middleware","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('4338e70b-9f1d-471e-9d93-dc7191322486', 'What is Redux DevTools?', 'Redux DevTools is a time-travel debugging environment for Redux with action replay and hot reloading.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'It lets you inspect every action and state change, rewind, and replay actions—great for debugging.', NULL, ARRAY[], ARRAY['redux','devtools','debugging','time-travel'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q127","original_type":"open-ended","topic":"Redux DevTools","subcategory":"State Management","sample_answers":["Redux DevTools is a browser extension or library that provides a UI to inspect Redux store changes, dispatch actions manually, and travel back in time.","It shows a timeline of actions, lets you cancel past actions, and re-evaluates state when reducer code changes."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('58001d7b-4caf-4b74-a817-04872edf5144', 'What are the features of Redux DevTools?', 'Features include state/action inspection, time travel, reducer hot reloading, error tracking, and state persistence.', 'multiple-choice', 'beginner', 6, '[{"id":"a","text":"Only shows console logs","isCorrect":false},{"id":"b","text":"State inspection, time travel, hot reloading, error tracking, persistence","isCorrect":true},{"id":"c","text":"Replaces the need for unit tests","isCorrect":false},{"id":"d","text":"Only works in production","isCorrect":false}]', NULL, 'Key features: inspect state/actions, cancel actions (time travel), hot reload reducers, see errors, persist debug sessions.', NULL, ARRAY[], ARRAY['redux-devtools','debugging','features'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q128","original_type":"multiple-choice","topic":"Redux DevTools","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('1ccbcaf8-2c82-4180-b167-9f96ee72ba0d', 'What are Redux selectors and why use them?', 'Selectors are functions that compute derived data from Redux state. They enable memoization and reduce redundant calculations.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"They directly mutate the Redux state","isCorrect":false},{"id":"b","text":"They compute derived data and memoize results for performance","isCorrect":true},{"id":"c","text":"They replace reducers","isCorrect":false},{"id":"d","text":"They are only for async logic","isCorrect":false}]', NULL, 'Selectors compute derived data (e.g., filtered lists) and memoize results to avoid recomputation unless inputs change.', NULL, ARRAY[], ARRAY['redux','selectors','reselect','memoization'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q129","original_type":"multiple-choice","topic":"Reselect","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('2c54085b-d3f7-4f45-80fc-222e062ad275', 'What is Redux Form?', 'Redux Form is a library that manages form state in Redux, handling values, validation, and submission.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'It stores all form state (values, errors, touched fields) in Redux, enabling complex form logic and persistence.', NULL, ARRAY[], ARRAY['redux-form','forms','validation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q130","original_type":"open-ended","topic":"Redux Form","subcategory":"State Management","sample_answers":["Redux Form integrates with React and Redux to keep all form state (input values, validation errors, submission status) in the Redux store.","It provides higher-order components and action creators to manage form lifecycle and validation."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
