INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES
('d697bc03-296c-4bf5-9bac-01faa493f3be', 'What is route-based code splitting?', 'Splitting code by route—each page loads only its required bundle—improves initial load performance.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Since users navigate to one route at a time, splitting by route ensures they only download what they need.', NULL, ARRAY[], ARRAY['code-splitting','routing','performance','react-router'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q200","original_type":"open-ended","topic":"Code Splitting","subcategory":"Code Splitting","sample_answers":["Use `React.lazy` with React Router to load each route’s component only when visited: `const Home = lazy(() => import(''./Home''));`","This reduces the main bundle size and speeds up time-to-interactive for the initial route."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('6e9f9138-b490-48a7-bb96-70339f5757a8', 'What is the purpose of default value in context?', 'The `defaultValue` argument in `React.createContext(defaultValue)` is only used when a component does not have a matching Provider above it in the tree. This is helpful for testing components in isolation.', 'multiple-choice', 'intermediate', 5, NULL, NULL, 'The default value allows components to work without a Provider, which is useful in unit tests or when the Provider is conditionally rendered.', NULL, ARRAY[], ARRAY['react','context','defaultvalue','testing'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q201","original_type":"open-ended","topic":"Context API","subcategory":"Context API","sample_answers":["The default value in `createContext()` is used only when no Provider is present in the ancestry—ideal for testing or fallback behavior.","Example: `const ThemeContext = React.createContext(''light'');` ensures components render with ''light'' theme if no Provider wraps them."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('76bf791e-d044-4cf6-acd0-65955b0a5b6d', 'What is diffing algorithm?', 'React uses a heuristic O(n) diffing algorithm to efficiently update the UI by minimizing DOM operations, based on two assumptions: (1) different element types produce different trees, (2) keys help identify stable children.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'Instead of O(n³) tree comparison, React assumes stable identity via keys and type changes imply full subtree replacement.', NULL, ARRAY[], ARRAY['react','diffing','reconciliation','virtual-dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q202","original_type":"open-ended","topic":"Diffing Algorithm","subcategory":"Virtual DOM","sample_answers":["React’s diffing algorithm is an O(n) heuristic that avoids expensive full-tree comparisons by assuming element type changes mean new subtrees and using keys to track child identity.","This optimization enables fast UI updates even with large component trees."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('f85c99fa-4955-48a7-b8d5-8e963c716b20', 'What are the rules covered by diffing algorithm?', 'Rules: (1) Different root types → full rebuild, (2) Same DOM type → update attributes only, (3) Same component type → update props and recurse, (4) Children diffed by index unless keys are used.', 'multiple-choice', 'advanced', 9, '[{"id":"a","text":"React compares every possible node pair in O(n³) time","isCorrect":false},{"id":"b","text":"Different types rebuild tree; same DOM updates attrs; same component updates props; keys optimize children diffing","isCorrect":true},{"id":"c","text":"Keys are optional and have no performance impact","isCorrect":false},{"id":"d","text":"Component instances are recreated on every render","isCorrect":false}]', NULL, 'These rules ensure minimal DOM mutations while maintaining correctness through key-based child reconciliation.', NULL, ARRAY[], ARRAY['react','reconciliation','diffing','keys'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q203","original_type":"multiple-choice","topic":"Diffing Algorithm","subcategory":"Virtual DOM","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('42781e68-d795-4cf9-b650-66ff965374d8', 'When do you need to use refs?', 'Use refs for: (1) managing focus/text selection/media playback, (2) triggering imperative animations, (3) integrating with third-party DOM libraries.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"For all state management","isCorrect":false},{"id":"b","text":"For focus, animations, and third-party DOM integrations","isCorrect":true},{"id":"c","text":"To replace useState","isCorrect":false},{"id":"d","text":"Only in class components","isCorrect":false}]', NULL, 'Refs provide a way to access DOM nodes or React elements created in the render method, bypassing typical data flow.', NULL, ARRAY[], ARRAY['react','refs','imperative','dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q204","original_type":"multiple-choice","topic":"Refs","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('ac5e0325-5197-484f-9096-dc41b8eafe18', 'Must prop be named as render for render props?', 'No. Any prop that is a function used to determine what to render is a render prop—including the `children` prop.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'The pattern is defined by behavior (a function that returns JSX), not the prop name. `children` as a function is common.', NULL, ARRAY[], ARRAY['react','render-props','children','patterns'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q205","original_type":"true-false","topic":"Render Props","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('259c1444-2290-485c-8534-9eb315932cd5', 'What are the problems of using render props with pure components?', 'Creating a new function inside render negates PureComponent’s shallow comparison, causing unnecessary re-renders.', 'multiple-choice', 'advanced', 7, NULL, NULL, 'Each render creates a new function instance, so shallow prop comparison fails, and the component re-renders every time.', NULL, ARRAY[], ARRAY['react','purecomponent','render-props','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q206","original_type":"open-ended","topic":"Render Props","subcategory":"Performance Optimization","sample_answers":["Defining the render function inline in render creates a new reference each time, breaking PureComponent’s optimization. Define it as an instance method or use useCallback.","Solution: Move the render function outside render or memoize it to preserve referential identity."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('48528f51-02a7-44cd-8bb4-edaf2b580d94', 'What is windowing technique?', 'Windowing renders only the visible subset of a large list, reducing DOM nodes and improving performance. Libraries: react-window, react-virtualized.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Instead of rendering 10,000 list items, windowing renders only the 20 currently visible, drastically improving memory and speed.', NULL, ARRAY[], ARRAY['react','windowing','virtualization','performance','lists'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q207","original_type":"open-ended","topic":"Windowing","subcategory":"Performance Optimization","sample_answers":["Windowing (or virtualization) renders only the items in the viewport, recycling DOM nodes as the user scrolls—ideal for long lists or tables.","Libraries like `react-window` provide `FixedSizeList` and `VariableSizeList` components to implement this pattern easily."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('2ecf6ed8-d4b1-443d-a4b1-01bf67e61707', 'How do you print falsy values in JSX?', 'Falsy values (false, null, undefined) don’t render in JSX. Convert them to strings using `String()` or template literals.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'React ignores falsy children for convenience (e.g., `{condition && <Component />}`), but explicit conversion shows the value.', NULL, ARRAY[], ARRAY['react','jsx','falsy','rendering'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q208","original_type":"open-ended","topic":"JSX","subcategory":"JSX","sample_answers":["Use `{String(myVar)}` or `{`${myVar}`}` to display falsy values like `null` or `undefined` in JSX.","Example: `<div>Value: {String(user.age)}</div>` will show ''Value: null'' if age is null."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z'),
('d15a98c5-b689-440c-8b8c-40c7efbaa093', 'What is the typical use case of portals?', 'Portals render modals, tooltips, dropdowns, and notifications outside the parent DOM tree to avoid CSS issues like `overflow: hidden` or `z-index` stacking contexts.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"To improve component reusability","isCorrect":false},{"id":"b","text":"To render modals/tooltips outside parent DOM to avoid CSS clipping and z-index issues","isCorrect":true},{"id":"c","text":"To replace Redux","isCorrect":false},{"id":"d","text":"Only for server-side rendering","isCorrect":false}]', NULL, 'By rendering into a separate DOM node (e.g., `document.body`), portals escape parent styling constraints.', NULL, ARRAY[], ARRAY['react','portals','modals','tooltips','css'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q209","original_type":"multiple-choice","topic":"React Portals","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
