-- Question 80: How is React Router different from the history library?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('facf1978-8fb4-461d-b643-57a9683dc452', 'How is React Router different from the history library?', 'React Router is a wrapper around the `history` library. The `history` library handles interaction with the browser’s `window.history` (via HTML5 history API, hash, or memory). React Router builds on this to provide declarative routing components like `<Route>` and `<Link>`.', 'multiple-choice', 'intermediate', 3, '[{"id":"a","text":"React Router is a standalone browser API","isCorrect":false},{"id":"b","text":"React Router wraps the `history` library to provide declarative React components for routing","isCorrect":true},{"id":"c","text":"The `history` library is only for server-side rendering","isCorrect":false},{"id":"d","text":"They are the same thing","isCorrect":false}]', NULL, 'React Router uses the `history` library under the hood but provides a React-friendly, component-based API for routing.', NULL, ARRAY[]::text[], ARRAY['react','react-router','history','routing','browser-api'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"react-q-080","original_type":"multiple-choice","topic":"React Router","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"system","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

