-- Question 147: What are the advantages of React over Vue.js?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('9bae6748-361a-485e-b744-6500b95259ca', 'What are the advantages of React over Vue.js?', 'React offers more flexibility in large apps, easier testing, better mobile support (React Native), and a larger ecosystem.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Vue has better mobile support","isCorrect":false},{"id":"b","text":"React offers more flexibility, easier testing, React Native, and larger ecosystem","isCorrect":true},{"id":"c","text":"Vue is harder to learn","isCorrect":false},{"id":"d","text":"React has no advantages","isCorrect":false}]', NULL, 'These are common perceived advantages, though both frameworks are capable.', NULL, ARRAY[]::text[], ARRAY['react','vue','comparison','ecosystem'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q147","original_type":"multiple-choice","topic":"React Ecosystem","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 148: What is the difference between React and Angular?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('ee9780f3-bda4-44f9-93ac-ca93662f6fd3', 'What is the difference between React and Angular?', 'React is a UI library with JSX and unidirectional data flow; Angular is a full framework with templates and two-way binding.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"React is a full framework like Angular","isCorrect":false},{"id":"b","text":"React is a library (View only); Angular is a full framework (MVC)","isCorrect":true},{"id":"c","text":"Angular uses JSX","isCorrect":false},{"id":"d","text":"React has two-way data binding","isCorrect":false}]', NULL, 'React is view-only, uses JavaScript syntax (JSX), and has one-way data flow. Angular is a full MVC framework with HTML templates and two-way binding.', NULL, ARRAY[]::text[], ARRAY['react','angular','comparison','frameworks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q148","original_type":"multiple-choice","topic":"Framework Comparison","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 149: Why React tab is not showing up in DevTools?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('c2a58f63-a1b3-4e0c-883a-3cccd7c5d187', 'Why React tab is not showing up in DevTools?', 'Because the page isn’t using React, or React failed to initialize and communicate with DevTools.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'DevTools detects React via a global hook set during React initialization. If React isn’t loaded or is in production mode, the tab won’t appear.', NULL, ARRAY[]::text[], ARRAY['react-devtools','debugging','setup'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q149","original_type":"open-ended","topic":"React Developer Tools","subcategory":"React Basics","sample_answers":["The React DevTools tab appears only if React is loaded on the page and not in production mode. Check that you’re using a development build of React.","If using a bundler, ensure React is included and the app is running in development mode."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 150: What are Styled Components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('3e04bd6b-d20c-4dfd-ba9d-ccdd4b31bb92', 'What are Styled Components?', '`styled-components` is a CSS-in-JS library that lets you write actual CSS in JavaScript to style React components.', 'multiple-choice', 'beginner', 6, NULL, NULL, 'It removes the mapping between styles and components by generating unique class names and injecting styles automatically.', NULL, ARRAY[]::text[], ARRAY['styled-components','css-in-js','react','styling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q150","original_type":"open-ended","topic":"Styled Components","subcategory":"CSS-in-JS","sample_answers":["`styled-components` allows you to write CSS inside JavaScript template literals, creating styled React components with automatic scoping and vendor prefixing.","Example: `const Title = styled.h1\\\\`color: red;\\\\`;` creates a component that renders an `<h1>` with red text."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 151: Give an example of Styled Components
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('e3f1932c-7c2d-45c5-8616-bcaaea0d0a77', 'Give an example of Styled Components', 'Create styled `Title` and `Wrapper` components using `styled.h1` and `styled.section` with CSS inside backticks.', 'multiple-choice', 'beginner', 6, NULL, NULL, 'Styled Components uses tagged template literals to define styles, creating components that apply those styles when rendered.', NULL, ARRAY[]::text[], ARRAY['styled-components','example','react','css'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q151","original_type":"open-ended","topic":"Styled Components","subcategory":"CSS-in-JS","sample_answers":["```javascript\\nimport styled from ''styled-components'';\\nconst Title = styled.h1`\\n  font-size: 1.5em;\\n  color: palevioletred;\\n`;\\nconst Wrapper = styled.section`\\n  padding: 4em;\\n  background: papayawhip;\\n`;\\n// Usage: <Wrapper><Title>Hello</Title></Wrapper>\\n```","The `styled` function takes a tag name and returns a component that renders that tag with the specified styles."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 152: Give an example of Styled Components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('6182dad3-81ef-4f10-a5f8-249103b2b8c3', 'Give an example of Styled Components?', 'Create styled `<Title>` and `<Wrapper>` components using `styled.h1` and `styled.section` with CSS inside backticks.

```javascript
import React from "react";
import styled from "styled-components";

const Title = styled.h1`
  font-size: 1.5em;
  text-align: center;
  color: palevioletred;
`;

const Wrapper = styled.section`
  padding: 4em;
  background: papayawhip;
`;
```

Usage:
```jsx
<Wrapper>
  <Title>{"Lets start first styled component!"}</Title>
</Wrapper>
```', 'multiple-choice', 'beginner', 6, NULL, NULL, 'Styled Components uses tagged template literals to define scoped CSS that is automatically injected and applied to React components.', NULL, ARRAY[]::text[], ARRAY['styled-components','css-in-js','react','styling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q151","original_type":"open-ended","topic":"Styled Components","subcategory":"CSS-in-JS","sample_answers":["The `styled` function creates a new React component with encapsulated styles. Example: `const Title = styled.h1\\\\`color: red;\\\\`;` renders an `<h1>` with red text.","Styled Components generates unique class names to avoid style collisions and supports dynamic styling via props."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 153: What is Relay?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('3b4119e8-4922-44a9-8284-61fa1f7b0f69', 'What is Relay?', 'Relay is a JavaScript framework for providing a data layer and client-server communication to web applications using the React view layer.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'Relay is a GraphQL client that manages server-originated state, handles caching, and optimizes data fetching for React applications.', NULL, ARRAY[]::text[], ARRAY['relay','graphql','react','data-fetching'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q152","original_type":"open-ended","topic":"Relay","subcategory":"State Management","sample_answers":["Relay is a GraphQL-based data-fetching framework for React that automatically manages caching, pagination, and mutations with strong typing.","Unlike Redux, Relay only stores data fetched from a GraphQL server and uses declarative queries to specify component data needs."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 154: What are the main features of Reselect library?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('6ccb33c8-f0af-4944-8168-04c1e7bfdaa6', 'What are the main features of Reselect library?', '1. Selectors compute derived data
2. Selectors are efficient (memoized)
3. Selectors are composable', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"It replaces reducers","isCorrect":false},{"id":"b","text":"It provides memoized, composable selectors for derived data","isCorrect":true},{"id":"c","text":"It handles async actions","isCorrect":false},{"id":"d","text":"It manages global state outside Redux","isCorrect":false}]', NULL, 'Reselect enables performant derived data computation in Redux apps through memoized, composable selector functions.', NULL, ARRAY[]::text[], ARRAY['reselect','selectors','redux','memoization'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q153","original_type":"multiple-choice","topic":"Reselect","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 155: Give an example of Reselect usage?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('73a3507e-891e-484c-9b64-2fc1ef97090c', 'Give an example of Reselect usage?', 'Use `createSelector` to build memoized selectors for subtotal, tax, and total from shop state.', 'multiple-choice', 'intermediate', 8, NULL, NULL, 'Reselect’s `createSelector` takes input selectors and a result function, caching output until inputs change.', NULL, ARRAY[]::text[], ARRAY['reselect','redux','selectors','example'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q154","original_type":"open-ended","topic":"Reselect","subcategory":"State Management","sample_answers":["```javascript\\nimport { createSelector } from ''reselect'';\\nconst items = state => state.shop.items;\\nconst subtotal = createSelector(items, i => i.reduce((a, b) => a + b.value, 0));\\n```","Selectors can be composed: tax depends on subtotal, total depends on both—each recomputes only when its dependencies change."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 156: Can Redux only be used with React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('69921912-44ff-4204-8577-47caf0b46ef0', 'Can Redux only be used with React?', 'No. Redux is a standalone state container that can be used with any UI layer (Angular, Vue, etc.) or even without a UI.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Redux provides a subscription mechanism that any library or framework can use to listen to state changes.', NULL, ARRAY[]::text[], ARRAY['redux','framework-agnostic','state-management'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q155","original_type":"true-false","topic":"Redux","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 157: Do you need a particular build tool to use Redux?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('2ee9f91a-0afb-4ffd-a580-d2721716a8ea', 'Do you need a particular build tool to use Redux?', 'No. Redux offers ES5 UMD builds that work without any build process, though it’s commonly used with modern tooling.', 'multiple-choice', 'beginner', 3, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Redux provides multiple builds (ESM, CJS, UMD) and works with or without Webpack, Babel, etc.', NULL, ARRAY[]::text[], ARRAY['redux','build-tools','umd','bundling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q156","original_type":"true-false","topic":"Redux","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 158: How does Redux Form `initialValues` get updated from state?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('ef8ae3c9-4bce-4193-8cb4-d294f67d0ad2', 'How does Redux Form `initialValues` get updated from state?', 'Set `enableReinitialize: true` in the `reduxForm` config to allow `initialValues` prop updates to reset the form.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'By default, Redux Form initializes only once. `enableReinitialize: true` makes it responsive to `initialValues` prop changes.', NULL, ARRAY[]::text[], ARRAY['redux-form','initialvalues','react','forms'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q157","original_type":"open-ended","topic":"Redux Form","subcategory":"State Management","sample_answers":["Pass `enableReinitialize: true` to the `reduxForm()` HOC to allow the form to reinitialize when the `initialValues` prop changes.","Example: `const Form = reduxForm({ form: ''user'', enableReinitialize: true })(UserForm);`"],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 159: How do React PropTypes allow different types for one prop?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('59680835-e447-4ce0-85d6-6f7a671f6364', 'How do React PropTypes allow different types for one prop?', 'Use `PropTypes.oneOfType([PropTypes.string, PropTypes.number])` to accept multiple types for a single prop.', 'multiple-choice', 'beginner', 5, NULL, NULL, '`PropTypes.oneOfType` accepts an array of type validators, allowing flexible prop definitions.', NULL, ARRAY[]::text[], ARRAY['react','proptypes','validation','typing'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q158","original_type":"open-ended","topic":"PropTypes","subcategory":"React Basics","sample_answers":["Use `PropTypes.oneOfType([PropTypes.string, PropTypes.number])` to allow a prop to be either a string or a number.","This is useful for props like `size` that might accept ''large'' (string) or 24 (number)."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 160: Can I import an SVG file as a React component?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('e1e26993-5d39-42ec-b0f8-10d546e5a5b9', 'Can I import an SVG file as a React component?', 'Yes, with `react-scripts@2.0.0+`, use `import { ReactComponent as Logo } from ''./logo.svg'';` to treat SVGs as components.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'CRA supports importing SVGs as React components, enabling props, styling, and tree-shaking.', NULL, ARRAY[]::text[], ARRAY['react','svg','assets','create-react-app'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q159","original_type":"true-false","topic":"Asset Handling","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 161: What is render hijacking in React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('93c538d2-8179-444a-a8c4-e9734422676f', 'What is render hijacking in React?', 'Render hijacking is controlling a component’s output by wrapping it in a Higher-Order Component (HOC) to inject props or logic.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'It’s not true hijacking—just composition via HOCs to alter rendering behavior conditionally.', NULL, ARRAY[]::text[], ARRAY['react','hoc','render-hijacking','composition'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q160","original_type":"open-ended","topic":"Higher-Order Components","subcategory":"React Basics","sample_answers":["Render hijacking uses HOCs to wrap a component and modify its output by injecting props or conditional logic.","Example: A HOC that wraps a component and only renders it if the user is authenticated."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 162: How to pass numbers to React component?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('5c3b33c8-eef6-4908-9a3b-b26125fd8664', 'How to pass numbers to React component?', 'Pass numbers using curly braces: `<Child age={30} />`. Strings use quotes: `<Child name="Chetan" />`.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'JSX expressions inside `{}` are evaluated as JavaScript, so numbers, booleans, and objects must be wrapped in braces.', NULL, ARRAY[]::text[], ARRAY['react','props','javascript','jsx'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q161","original_type":"open-ended","topic":"Props","subcategory":"React Basics","sample_answers":["Use `<Component count={42} />` to pass the number 42. The braces tell JSX to evaluate the expression as JavaScript.","Never use quotes for numbers: `<Component count=\\"42\\" />` passes the string ''42'', not the number."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 163: Do I need to keep all my state in Redux? Should I use React internal state?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('b99f5b14-7d74-4ae8-ae52-8f0f7256cebc', 'Do I need to keep all my state in Redux? Should I use React internal state?', 'No. Use Redux for global/shared state; use React component state for local/UI state (e.g., dropdown open/closed).', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Ask: Is this state shared? Needed for debugging? Used in multiple components? If not, keep it local.', NULL, ARRAY[]::text[], ARRAY['redux','react-state','best-practices','state-management'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q162","original_type":"open-ended","topic":"Redux Best Practices","subcategory":"State Management","sample_answers":["Keep global, shared, or persistent state in Redux. Keep transient UI state (e.g., form input, modal open) in component state.","Rules of thumb: if only one component cares, or it’s ephemeral, use useState. If multiple components or time-travel matters, use Redux."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 164: What is the purpose of registerServiceWorker in React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('aaa97644-3f3c-4628-9c6d-0aad53570d0a', 'What is the purpose of registerServiceWorker in React?', 'It registers a service worker to cache assets, enabling offline functionality and improved performance.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'The service worker caches static assets so the app works offline or on slow networks.', NULL, ARRAY[]::text[], ARRAY['react','service-worker','pwa','offline'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q163","original_type":"open-ended","topic":"PWAs (Progressive Web Apps)","subcategory":"Performance & Monitoring","sample_answers":["`registerServiceWorker()` sets up a service worker that caches the app shell and assets, enabling offline use and faster repeat visits.","It’s part of Create React App’s PWA support—users can install the app and use it without internet."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 165: What is React memo function?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('5f2f6a73-99e5-499d-9ae9-3311514cd0bb', 'What is React memo function?', '`React.memo` is a higher-order component that prevents re-renders of function components when props haven’t changed.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'It’s the functional component equivalent of `PureComponent` for class components.', NULL, ARRAY[]::text[], ARRAY['react','memo','performance','optimization'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q164","original_type":"open-ended","topic":"Performance Optimization","subcategory":"React Basics","sample_answers":["`React.memo(MyComponent)` returns a memoized version that only re-renders if props change (shallow comparison by default).","Use it to optimize expensive components that receive the same props frequently."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 166: What is React lazy function?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('a3979873-a7b5-46ae-be8b-fff20b46acd7', 'What is React lazy function?', '`React.lazy` lets you load components dynamically via `import()` for code-splitting.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It returns a promise that resolves to a component, which must be rendered inside `Suspense`.', NULL, ARRAY[]::text[], ARRAY['react','lazy','code-splitting','dynamic-import'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q165","original_type":"open-ended","topic":"Code Splitting","subcategory":"React Basics","sample_answers":["`const LazyComp = React.lazy(() => import(''./LazyComp''));` loads the component only when needed, reducing initial bundle size.","Must be used with `<Suspense fallback={...}>` to show a loading state while the chunk loads."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 167: How to prevent unnecessary updates using setState?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('ea320d8f-c15e-401b-ac00-69cf96c875be', 'How to prevent unnecessary updates using setState?', 'In functional `setState`, return `null` if the new state equals the current state to skip re-rendering.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Returning `null` from functional `setState` tells React to skip the update and re-render.', NULL, ARRAY[]::text[], ARRAY['react','setstate','optimization','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q166","original_type":"open-ended","topic":"State Management","subcategory":"React Basics","sample_answers":["In `setState((state) => { if (state.x === newX) return null; else return { x: newX }; })`, returning `null` prevents re-render.","This is useful for avoiding redundant updates when new data matches current state."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 168: How do you render Arrays, Strings, and Numbers in React 16+?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('c7c82ddb-92d1-4a8d-b2dc-76b1ddf18fcd', 'How do you render Arrays, Strings, and Numbers in React 16+?', 'React 16+ allows returning arrays, strings, and numbers directly from `render()` without a wrapper element.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"You must always wrap in a `<div>`","isCorrect":false},{"id":"b","text":"You can return arrays, strings, and numbers directly","isCorrect":true},{"id":"c","text":"Only objects are allowed","isCorrect":false},{"id":"d","text":"Strings cause hydration errors","isCorrect":false}]', NULL, 'Before React 16, `render()` had to return a single root element. Now it can return arrays, strings, or numbers.', NULL, ARRAY[]::text[], ARRAY['react','fragments','rendering','arrays'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q167","original_type":"multiple-choice","topic":"Fragments & Rendering","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 169: What are hooks?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('752a4d6c-eb1b-4e3e-ab62-ef3f3e25de42', 'What are hooks?', 'Hooks are functions that let you use state and other React features in function components without classes.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'Hooks like `useState` and `useEffect` enable stateful logic in functional components.', NULL, ARRAY[]::text[], ARRAY['react','hooks','useState','function-components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q168","original_type":"open-ended","topic":"React Hooks","subcategory":"React Hooks","sample_answers":["Hooks are functions like `useState` and `useEffect` that let function components manage state and side effects.","They allow extracting stateful logic into reusable custom hooks, improving code organization."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 170: What rules need to be followed for hooks?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('0e64a936-eaaa-4dd1-ae51-b65bcff651ee', 'What rules need to be followed for hooks?', '1. Call hooks only at the top level
2. Call hooks only from React functions (components or custom hooks)', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"Call hooks inside loops and conditions","isCorrect":false},{"id":"b","text":"Call hooks only at top level and from React functions","isCorrect":true},{"id":"c","text":"Hooks can be called from any JavaScript function","isCorrect":false},{"id":"d","text":"Order of hook calls doesn’t matter","isCorrect":false}]', NULL, 'These rules ensure hooks are called in the same order every render, preserving state consistency.', NULL, ARRAY[]::text[], ARRAY['react','hooks','rules-of-hooks','eslint'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q169","original_type":"multiple-choice","topic":"Rules of Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 171: How to ensure hooks follow the rules in your project?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('1cabc8c2-dd4e-49e4-82ad-f3d713daceac', 'How to ensure hooks follow the rules in your project?', 'Use the `eslint-plugin-react-hooks` ESLint plugin with the `react-hooks/rules-of-hooks` rule enabled.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This plugin enforces the two rules of hooks and is included by default in Create React App.', NULL, ARRAY[]::text[], ARRAY['react','hooks','eslint','linting'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q170","original_type":"open-ended","topic":"Rules of Hooks","subcategory":"React Hooks","sample_answers":["Install `eslint-plugin-react-hooks` and enable `react-hooks/rules-of-hooks` in ESLint config to catch violations at build time.","The plugin also includes `exhaustive-deps` to validate effect dependencies."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 172: What are the differences between Flux and Redux?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('c664b082-969a-4e69-9aac-ea753e05c3d0', 'What are the differences between Flux and Redux?', 'Flux has multiple mutable stores and a dispatcher; Redux has a single immutable store and no dispatcher.', 'multiple-choice', 'intermediate', 8, '[{"id":"a","text":"Flux uses immutable state; Redux uses mutable state","isCorrect":false},{"id":"b","text":"Flux: multiple mutable stores, dispatcher; Redux: single immutable store, no dispatcher","isCorrect":true},{"id":"c","text":"Redux has multiple dispatchers","isCorrect":false},{"id":"d","text":"They are identical","isCorrect":false}]', NULL, 'Redux simplifies Flux by using a single store, pure reducers, and no dispatcher.', NULL, ARRAY[]::text[], ARRAY['flux','redux','comparison','architecture'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q171","original_type":"multiple-choice","topic":"Flux vs Redux","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 173: What are the benefits of React Router V4?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('a8d27780-3d66-4cc6-a5c9-3d8b1fb5c043', 'What are the benefits of React Router V4?', '1. Component-based API
2. Automatic history management
3. Smaller bundle size via modular imports', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Requires manual history setup","isCorrect":false},{"id":"b","text":"Component-based, auto history, smaller bundles","isCorrect":true},{"id":"c","text":"Only works with class components","isCorrect":false},{"id":"d","text":"No support for dynamic routes","isCorrect":false}]', NULL, 'React Router v4 treats routes as components, simplifying composition and reducing boilerplate.', NULL, ARRAY[]::text[], ARRAY['react-router','routing','v4','components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q172","original_type":"multiple-choice","topic":"React Router","subcategory":"React Router","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 174: Describe the componentDidCatch lifecycle method signature
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('bd0ccc6a-0e1e-4319-8d4e-95b2741844a2', 'Describe the componentDidCatch lifecycle method signature', '`componentDidCatch(error, info)` receives the error object and an info object with a componentStack.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This method is called after an error is thrown in a child component tree.', NULL, ARRAY[]::text[], ARRAY['react','error-boundaries','componentdidcatch','lifecycle'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q173","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["`componentDidCatch(error, info)` where `error` is the thrown error and `info.componentStack` shows the component hierarchy.","Used in error boundary components to log errors and display fallback UI."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 175: In which scenarios do error boundaries not catch errors?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('b639cbbb-c8d1-4cd4-aec9-7baa53f770a6', 'In which scenarios do error boundaries not catch errors?', '1. Event handlers
2. Async code (setTimeout)
3. Server-side rendering
4. Errors in the error boundary itself', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"They catch all errors everywhere","isCorrect":false},{"id":"b","text":"Not in event handlers, async code, SSR, or self-errors","isCorrect":true},{"id":"c","text":"They work in setTimeout callbacks","isCorrect":false},{"id":"d","text":"They catch syntax errors in modules","isCorrect":false}]', NULL, 'Error boundaries only catch errors during rendering, in lifecycle methods, and in constructors.', NULL, ARRAY[]::text[], ARRAY['react','error-boundaries','limitations','debugging'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q174","original_type":"multiple-choice","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 176: What is the behavior of uncaught errors in React 16?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('27efe3bb-6af1-4b7b-84fc-702e93a6f0f5', 'What is the behavior of uncaught errors in React 16?', 'Uncaught errors cause React to unmount the entire component tree to prevent corrupted UI.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This prevents displaying inconsistent or dangerous UI (e.g., wrong payment amounts).', NULL, ARRAY[]::text[], ARRAY['react','error-handling','react-16','unmount'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q175","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["React 16 unmounts the whole app on uncaught errors to avoid showing corrupted state—better to show nothing than wrong data.","This behavior emphasizes the need for error boundaries to gracefully handle failures."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 177: What is the proper placement for error boundaries?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('3bd98579-08c6-48ec-b908-cac6ad63108e', 'What is the proper placement for error boundaries?', 'Place them at the top level for app-wide errors, or around individual components to isolate failures.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Granularity depends on UX needs—protect critical sections or provide global fallbacks.', NULL, ARRAY[]::text[], ARRAY['react','error-boundaries','best-practices','ui'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q176","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["Wrap top-level routes for a generic error page, or wrap individual widgets (e.g., comment section) to keep the rest of the app functional.","Balance between user experience and implementation complexity when deciding placement."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 178: What is the benefit of component stack trace from error boundary?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('da0784d2-ca16-42b2-8006-631e6f73eb98', 'What is the benefit of component stack trace from error boundary?', 'It shows the component hierarchy with file names and line numbers, making debugging easier.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'Unlike generic JS stack traces, React’s component stack shows which component failed.', NULL, ARRAY[]::text[], ARRAY['react','debugging','stack-trace','error-boundaries'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q177","original_type":"open-ended","topic":"Error Boundaries","subcategory":"Error Handling","sample_answers":["Error boundaries provide a component stack trace showing which component threw the error, including file and line number in development.","This makes it much easier to locate the source of rendering errors compared to raw JavaScript stack traces."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 179: What are default props?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('2178ed54-addb-4bdb-8897-df1f0c79bd2e', 'What are default props?', '`defaultProps` sets fallback values for props when they are `undefined` (but not `null` or `0`).', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Default props only apply when a prop is not passed or is explicitly `undefined`.', NULL, ARRAY[]::text[], ARRAY['react','defaultprops','props','components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q178","original_type":"open-ended","topic":"Props","subcategory":"React Basics","sample_answers":["`MyButton.defaultProps = { color: ''red'' };` means if `<MyButton />` is used without a `color` prop, it defaults to ''red''.","Note: `null` and `0` are valid values and will not trigger defaultProps."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 180: What is the purpose of displayName class property?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('30ae33e8-a146-4f10-81aa-1dcf894633b1', 'What is the purpose of displayName class property?', '`displayName` sets a custom name for a component in React DevTools and error messages.', 'multiple-choice', 'intermediate', 5, NULL, NULL, 'It’s especially useful for HOCs to show meaningful names like `withAuth(Button)`.', NULL, ARRAY[]::text[], ARRAY['react','displayname','devtools','hoc'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q179","original_type":"open-ended","topic":"Debugging","subcategory":"React Basics","sample_answers":["`displayName` helps identify components in React DevTools—critical for HOCs that would otherwise appear as ''Anonymous''.","Example: `WithSubscription.displayName = ''WithSubscription(UserList)'';`"],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 181: What is the browser support for React applications?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('7dc537ec-0835-4fdc-a068-dff7bf62de4a', 'What is the browser support for React applications?', 'React supports IE9+ with polyfills like `es5-shim` for older browsers.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"Only Chrome and Firefox","isCorrect":false},{"id":"b","text":"IE9+ with polyfills","isCorrect":true},{"id":"c","text":"No IE support at all","isCorrect":false},{"id":"d","text":"Requires ES2020","isCorrect":false}]', NULL, 'Modern React apps require ES5+; polyfills bridge gaps in older browsers.', NULL, ARRAY[]::text[], ARRAY['react','browser-support','polyfills','ie9'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q180","original_type":"multiple-choice","topic":"Browser Compatibility","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 182: What is code-splitting?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('bc40a227-a8a3-4cad-891e-45e14fe21992', 'What is code-splitting?', 'Code-splitting divides your app into smaller bundles loaded on demand, improving initial load time.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It reduces the initial bundle size by lazy-loading features only when needed.', NULL, ARRAY[]::text[], ARRAY['code-splitting','webpack','performance','bundling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q181","original_type":"open-ended","topic":"Code Splitting","subcategory":"Performance Optimization","sample_answers":["Code-splitting uses dynamic `import()` to create separate bundles that load at runtime, reducing initial payload.","In React, it’s commonly used with `React.lazy` and `Suspense` for route or component-based splitting."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 183: What are Keyed Fragments?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('ae77757c-261e-415e-9ec0-e17b2dc25b63', 'What are Keyed Fragments?', '`<React.Fragment key={...}>` allows fragments to be used in lists by providing a unique key.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'When mapping to multiple elements, each must have a key—even if wrapped in a fragment.', NULL, ARRAY[]::text[], ARRAY['react','fragments','keys','lists'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q182","original_type":"open-ended","topic":"Fragments","subcategory":"React Basics","sample_answers":["When returning multiple elements from a `.map()`, wrap them in `<React.Fragment key={item.id}>` to satisfy React’s key requirement.","Keyed fragments avoid unnecessary wrapper divs while enabling stable reconciliation in lists."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 184: Does React support all HTML attributes?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('515683d5-0556-407a-9b00-66634bb0d027', 'Does React support all HTML attributes?', 'Yes, React 16+ supports all standard and custom DOM attributes using camelCase (e.g., `className`, `tabIndex`).', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'React uses camelCase to match DOM API property names, not HTML attribute names.', NULL, ARRAY[]::text[], ARRAY['react','jsx','attributes','dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q183","original_type":"true-false","topic":"JSX","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 185: When do component props default to true?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('e2887003-03a1-4954-98c8-c8d1257836c3', 'When do component props default to true?', 'When a prop is passed without a value (e.g., `<MyInput autocomplete />`), it defaults to `true`.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'This mimics HTML behavior (e.g., `<button disabled>`), but can be confusing—explicit `={true}` is clearer.', NULL, ARRAY[]::text[], ARRAY['react','jsx','props','booleans'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q184","original_type":"open-ended","topic":"JSX","subcategory":"React Basics","sample_answers":["In JSX, `<Component hidden />` is equivalent to `<Component hidden={true} />`—the prop becomes `true`.","This matches HTML’s boolean attribute behavior but is discouraged in favor of explicit values."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 186: What is Next.js and its major features?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('983eb685-4502-44ed-bb9f-6c76bd38859f', 'What is Next.js and its major features?', 'Next.js is a React framework for SSR, SSG, and static apps with features like automatic code splitting and file-based routing.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"A state management library","isCorrect":false},{"id":"b","text":"A React framework for SSR/SSG with file-based routing and code splitting","isCorrect":true},{"id":"c","text":"A testing framework","isCorrect":false},{"id":"d","text":"Only for static sites","isCorrect":false}]', NULL, 'Next.js simplifies building production-ready React apps with built-in SSR, routing, and optimization.', NULL, ARRAY[]::text[], ARRAY['nextjs','ssr','react','framework'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q185","original_type":"multiple-choice","topic":"Next.js Basics","subcategory":"Next.js Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 187: How do you pass an event handler to a component?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('094f01ea-6a80-4b31-8b1a-6780f9199cf3', 'How do you pass an event handler to a component?', 'Pass the function as a prop: `<Button onClick={handleClick} />`.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Event handlers are functions passed as props and called in response to user interactions.', NULL, ARRAY[]::text[], ARRAY['react','events','handlers','props'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q186","original_type":"open-ended","topic":"Event Handling","subcategory":"React Basics","sample_answers":["Define a function in the parent and pass it as a prop: `<Child onClick={this.handleClick} />`.","In the child, call it via `onClick={props.onClick}` on a DOM element."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"2a5901fd-e2ca-4fe3-a914-447622008d02"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 188: How to prevent a function from being called multiple times?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('978f2bad-5773-453e-9afa-454cc3a9d430', 'How to prevent a function from being called multiple times?', 'Use throttling, debouncing, or `requestAnimationFrame` to limit execution rate.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Call it inside a `setTimeout` with 0 delay","isCorrect":false},{"id":"b","text":"Use throttling, debouncing, or requestAnimationFrame","isCorrect":true},{"id":"c","text":"Disable the button permanently","isCorrect":false},{"id":"d","text":"Use `preventDefault()`","isCorrect":false}]', NULL, 'These techniques control how often a function runs in response to rapid events like scrolling or typing.', NULL, ARRAY[]::text[], ARRAY['throttle','debounce','performance','events'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q187","original_type":"multiple-choice","topic":"Debounce & Throttle","subcategory":"Debounce & Throttle","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 189: How does JSX prevent Injection Attacks?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('a1a64992-28a5-4bf7-b2c8-bcf650c2b99b', 'How does JSX prevent Injection Attacks?', 'React escapes all values embedded in JSX, converting them to strings before rendering.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'This automatic escaping prevents XSS by ensuring user input is never executed as code.', NULL, ARRAY[]::text[], ARRAY['react','jsx','xss','security'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q188","original_type":"open-ended","topic":"XSS Prevention","subcategory":"XSS (Cross-Site Scripting)","sample_answers":["JSX automatically escapes `{userInput}` by converting it to a string, so `<h1>{name}</h1>` can’t inject scripts.","This built-in protection makes React apps resistant to XSS without manual sanitization."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 190: How do you update rendered elements?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('77cad797-8394-46f4-b469-95e3451ac7c9', 'How do you update rendered elements?', 'Call `ReactDOM.render()` again with the new element to update the UI.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'In modern React, state-driven re-renders replace manual `render()` calls, but this is how it worked in early examples.', NULL, ARRAY[]::text[], ARRAY['react','render','dom','updates'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q189","original_type":"open-ended","topic":"Rendering","subcategory":"React Basics","sample_answers":["In legacy React (pre-hooks), you could update the UI by calling `ReactDOM.render(newElement, container)` repeatedly.","Today, we use state and let React handle re-renders automatically via the reconciliation process."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 191: How do you say that props are readonly?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('ae5e0d54-46f4-426f-a62a-0f4a0ffce11d', 'How do you say that props are readonly?', 'React components must treat props as immutable—never modify them directly.', 'multiple-choice', 'beginner', 3, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'Components should act like pure functions with respect to props—same input, same output.', NULL, ARRAY[]::text[], ARRAY['react','props','immutable','pure-functions'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q190","original_type":"true-false","topic":"Props","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 192: What are the conditions to safely use the index as a key?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('a0f9aef3-a0c6-46f1-ad67-e89fe8e0c56e', 'What are the conditions to safely use the index as a key?', '1. List is static
2. Items have no IDs
3. List is never reordered or filtered', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Always safe to use index","isCorrect":false},{"id":"b","text":"Only if list is static, no IDs, and never reordered","isCorrect":true},{"id":"c","text":"Index is preferred over IDs","isCorrect":false},{"id":"d","text":"Keys are optional in static lists","isCorrect":false}]', NULL, 'Using index as key can cause bugs if the list changes order or items are added/removed.', NULL, ARRAY[]::text[], ARRAY['react','keys','lists','index'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q191","original_type":"multiple-choice","topic":"Lists and Keys","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"540c20e9-c800-48de-9185-e611c10d37d5"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 193: Should keys be globally unique?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('e4d09773-646a-4dd9-bba0-9694f4cf013a', 'Should keys be globally unique?', 'No. Keys only need to be unique among siblings, not globally.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Keys help React identify which items have changed within the same parent—global uniqueness isn’t required.', NULL, ARRAY[]::text[], ARRAY['react','keys','lists','uniqueness'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q192","original_type":"true-false","topic":"Lists and Keys","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"540c20e9-c800-48de-9185-e611c10d37d5"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 194: What is the popular choice for form handling?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('63fee047-6677-4820-ad25-1eb0e0762080', 'What is the popular choice for form handling?', '`Formik` is a popular React form library for validation, state management, and submission handling.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Redux Form is the only option","isCorrect":false},{"id":"b","text":"Formik is a popular lightweight form library","isCorrect":true},{"id":"c","text":"HTML forms are sufficient","isCorrect":false},{"id":"d","text":"Forms don’t need libraries","isCorrect":false}]', NULL, 'Formik provides a minimal API to handle common form tasks without Redux.', NULL, ARRAY[]::text[], ARRAY['formik','forms','react','validation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q193","original_type":"multiple-choice","topic":"Formik","subcategory":"Form Libraries","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 195: What are the advantages of Formik over Redux Form?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('042eee85-a021-4ead-a19f-16fcd147c42a', 'What are the advantages of Formik over Redux Form?', '1. Form state is local, not global
2. Better performance (no Redux reducer calls per keystroke)
3. Smaller bundle size', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"Redux Form is faster","isCorrect":false},{"id":"b","text":"Formik: local state, better perf, smaller size","isCorrect":true},{"id":"c","text":"Redux Form has fewer features","isCorrect":false},{"id":"d","text":"Formik requires Redux","isCorrect":false}]', NULL, 'Formik keeps form state local, avoiding unnecessary Redux overhead.', NULL, ARRAY[]::text[], ARRAY['formik','redux-form','comparison','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q194","original_type":"multiple-choice","topic":"Formik","subcategory":"Form Libraries","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 196: Why are you not required to use inheritance in React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('662ac011-f46f-4e7d-9ae9-7e3b6829a1f2', 'Why are you not required to use inheritance in React?', 'React recommends composition over inheritance for code reuse—via props and custom hooks.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Composition is more flexible and explicit—components receive behavior via props or hooks.', NULL, ARRAY[]::text[], ARRAY['react','composition','inheritance','design'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q195","original_type":"open-ended","topic":"Composition vs Inheritance","subcategory":"React Basics","sample_answers":["React uses composition: components receive functionality via props, children, or custom hooks—not class inheritance.","For non-UI logic, extract functions/modules instead of extending classes."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 197: Can I use web components in a React application?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('3e207524-ac1c-434a-bbb4-d1824d13d018', 'Can I use web components in a React application?', 'Yes. React can render web components by using their custom element tags in JSX.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'React treats web components as regular DOM elements—just use `<my-element />` in JSX.', NULL, ARRAY[]::text[], ARRAY['react','web-components','custom-elements','integration'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q196","original_type":"true-false","topic":"Web Components","subcategory":"Integration","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 198: What is dynamic import?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('1d4726e3-9655-4b54-825c-3cf4cfeb85b9', 'What is dynamic import?', 'Dynamic `import()` loads modules at runtime, enabling code-splitting and lazy loading.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Unlike static `import`, dynamic `import()` returns a promise and loads code on demand.', NULL, ARRAY[]::text[], ARRAY['dynamic-import','code-splitting','esm','webpack'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q197","original_type":"open-ended","topic":"Dynamic Imports","subcategory":"Code Splitting","sample_answers":["`import(''./module'')` returns a Promise that resolves to the module, allowing conditional or lazy loading.","It’s the foundation for route-based or feature-based code splitting in modern apps."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 199: What are loadable components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('135f4560-95e3-44e0-ab62-899f1af1977a', 'What are loadable components?', '`@loadable/component` is a library for code-splitting with SSR support, used before React 18’s lazy/Suspense SSR.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'It provided SSR-compatible dynamic imports before React.lazy supported server rendering.', NULL, ARRAY[]::text[], ARRAY['loadable','code-splitting','ssr','react'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q198","original_type":"open-ended","topic":"Loadable Components","subcategory":"Code Splitting","sample_answers":["`loadable(() => import(''./Component''))` creates a component that loads its bundle dynamically, with SSR support.","Now largely superseded by React 18’s built-in SSR support for `lazy` and `Suspense`."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 200: What is Suspense component?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('f17280d6-1cf9-4ed3-a0f1-ac20bb8e7c1f', 'What is Suspense component?', '`Suspense` lets you show a fallback UI while waiting for asynchronous operations (code or data) to complete.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It’s used with `React.lazy` for code-splitting and with data-fetching libraries that integrate with Suspense.', NULL, ARRAY[]::text[], ARRAY['react','suspense','lazy','loading'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q199","original_type":"open-ended","topic":"Suspense","subcategory":"React Basics","sample_answers":["`<Suspense fallback={<Spinner />}><LazyComponent /></Suspense>` shows a spinner while LazyComponent loads.","Suspense provides a declarative way to handle loading states without manual boolean flags."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 201: What is route-based code splitting?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('cdff064d-c2ae-49e1-beb7-6ca98ab7e7be', 'What is route-based code splitting?', 'Splitting code by route—each page loads only its required bundle—improves initial load performance.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Since users navigate to one route at a time, splitting by route ensures they only download what they need.', NULL, ARRAY[]::text[], ARRAY['code-splitting','routing','performance','react-router'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q200","original_type":"open-ended","topic":"Code Splitting","subcategory":"Code Splitting","sample_answers":["Use `React.lazy` with React Router to load each route’s component only when visited: `const Home = lazy(() => import(''./Home''));`","This reduces the main bundle size and speeds up time-to-interactive for the initial route."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 202: What is the purpose of default value in context?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('d97f5166-64a8-4d20-b877-78bdfca61c1f', 'What is the purpose of default value in context?', 'The `defaultValue` argument in `React.createContext(defaultValue)` is only used when a component does not have a matching Provider above it in the tree. This is helpful for testing components in isolation.', 'multiple-choice', 'intermediate', 5, NULL, NULL, 'The default value allows components to work without a Provider, which is useful in unit tests or when the Provider is conditionally rendered.', NULL, ARRAY[]::text[], ARRAY['react','context','defaultvalue','testing'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q201","original_type":"open-ended","topic":"Context API","subcategory":"Context API","sample_answers":["The default value in `createContext()` is used only when no Provider is present in the ancestry—ideal for testing or fallback behavior.","Example: `const ThemeContext = React.createContext(''light'');` ensures components render with ''light'' theme if no Provider wraps them."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 203: What is diffing algorithm?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('a3a92b04-2d3d-43c1-9c25-4c3a0fee36ab', 'What is diffing algorithm?', 'React uses a heuristic O(n) diffing algorithm to efficiently update the UI by minimizing DOM operations, based on two assumptions: (1) different element types produce different trees, (2) keys help identify stable children.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'Instead of O(n³) tree comparison, React assumes stable identity via keys and type changes imply full subtree replacement.', NULL, ARRAY[]::text[], ARRAY['react','diffing','reconciliation','virtual-dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q202","original_type":"open-ended","topic":"Diffing Algorithm","subcategory":"Virtual DOM","sample_answers":["React’s diffing algorithm is an O(n) heuristic that avoids expensive full-tree comparisons by assuming element type changes mean new subtrees and using keys to track child identity.","This optimization enables fast UI updates even with large component trees."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 204: What are the rules covered by diffing algorithm?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('f5ffa032-62dd-4039-9873-5e1f9e5db6a7', 'What are the rules covered by diffing algorithm?', 'Rules: (1) Different root types → full rebuild, (2) Same DOM type → update attributes only, (3) Same component type → update props and recurse, (4) Children diffed by index unless keys are used.', 'multiple-choice', 'advanced', 9, '[{"id":"a","text":"React compares every possible node pair in O(n³) time","isCorrect":false},{"id":"b","text":"Different types rebuild tree; same DOM updates attrs; same component updates props; keys optimize children diffing","isCorrect":true},{"id":"c","text":"Keys are optional and have no performance impact","isCorrect":false},{"id":"d","text":"Component instances are recreated on every render","isCorrect":false}]', NULL, 'These rules ensure minimal DOM mutations while maintaining correctness through key-based child reconciliation.', NULL, ARRAY[]::text[], ARRAY['react','reconciliation','diffing','keys'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q203","original_type":"multiple-choice","topic":"Diffing Algorithm","subcategory":"Virtual DOM","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 205: When do you need to use refs?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('8842e5db-95a6-4160-a86d-270d0c4597b1', 'When do you need to use refs?', 'Use refs for: (1) managing focus/text selection/media playback, (2) triggering imperative animations, (3) integrating with third-party DOM libraries.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"For all state management","isCorrect":false},{"id":"b","text":"For focus, animations, and third-party DOM integrations","isCorrect":true},{"id":"c","text":"To replace useState","isCorrect":false},{"id":"d","text":"Only in class components","isCorrect":false}]', NULL, 'Refs provide a way to access DOM nodes or React elements created in the render method, bypassing typical data flow.', NULL, ARRAY[]::text[], ARRAY['react','refs','imperative','dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q204","original_type":"multiple-choice","topic":"Refs","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 206: Must prop be named as render for render props?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('c37a3803-893a-4424-998c-c75c820c6e2e', 'Must prop be named as render for render props?', 'No. Any prop that is a function used to determine what to render is a render prop—including the `children` prop.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'The pattern is defined by behavior (a function that returns JSX), not the prop name. `children` as a function is common.', NULL, ARRAY[]::text[], ARRAY['react','render-props','children','patterns'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q205","original_type":"true-false","topic":"Render Props","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 207: What are the problems of using render props with pure components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('0246e20b-2828-475e-8e1f-4abc82b8d79f', 'What are the problems of using render props with pure components?', 'Creating a new function inside render negates PureComponent’s shallow comparison, causing unnecessary re-renders.', 'multiple-choice', 'advanced', 7, NULL, NULL, 'Each render creates a new function instance, so shallow prop comparison fails, and the component re-renders every time.', NULL, ARRAY[]::text[], ARRAY['react','purecomponent','render-props','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q206","original_type":"open-ended","topic":"Render Props","subcategory":"Performance Optimization","sample_answers":["Defining the render function inline in render creates a new reference each time, breaking PureComponent’s optimization. Define it as an instance method or use useCallback.","Solution: Move the render function outside render or memoize it to preserve referential identity."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 208: What is windowing technique?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('407f9169-1ae9-4e25-a87d-3903881020bc', 'What is windowing technique?', 'Windowing renders only the visible subset of a large list, reducing DOM nodes and improving performance. Libraries: react-window, react-virtualized.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Instead of rendering 10,000 list items, windowing renders only the 20 currently visible, drastically improving memory and speed.', NULL, ARRAY[]::text[], ARRAY['react','windowing','virtualization','performance','lists'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q207","original_type":"open-ended","topic":"Windowing","subcategory":"Performance Optimization","sample_answers":["Windowing (or virtualization) renders only the items in the viewport, recycling DOM nodes as the user scrolls—ideal for long lists or tables.","Libraries like `react-window` provide `FixedSizeList` and `VariableSizeList` components to implement this pattern easily."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 209: How do you print falsy values in JSX?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('aa62f937-2065-45d1-9cbd-d7f5d3db2fde', 'How do you print falsy values in JSX?', 'Falsy values (false, null, undefined) don’t render in JSX. Convert them to strings using `String()` or template literals.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'React ignores falsy children for convenience (e.g., `{condition && <Component />}`), but explicit conversion shows the value.', NULL, ARRAY[]::text[], ARRAY['react','jsx','falsy','rendering'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q208","original_type":"open-ended","topic":"JSX","subcategory":"JSX","sample_answers":["Use `{String(myVar)}` or `{`${myVar}`}` to display falsy values like `null` or `undefined` in JSX.","Example: `<div>Value: {String(user.age)}</div>` will show ''Value: null'' if age is null."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 210: What is the typical use case of portals?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('2d9fa844-0138-40f1-893f-50733f95961c', 'What is the typical use case of portals?', 'Portals render modals, tooltips, dropdowns, and notifications outside the parent DOM tree to avoid CSS issues like `overflow: hidden` or `z-index` stacking contexts.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"To improve component reusability","isCorrect":false},{"id":"b","text":"To render modals/tooltips outside parent DOM to avoid CSS clipping and z-index issues","isCorrect":true},{"id":"c","text":"To replace Redux","isCorrect":false},{"id":"d","text":"Only for server-side rendering","isCorrect":false}]', NULL, 'By rendering into a separate DOM node (e.g., `document.body`), portals escape parent styling constraints.', NULL, ARRAY[]::text[], ARRAY['react','portals','modals','tooltips','css'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q209","original_type":"multiple-choice","topic":"React Portals","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 211: How do you set default value for uncontrolled component?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('d04a7e1b-e708-43ac-96cd-210558a2cbd0', 'How do you set default value for uncontrolled component?', 'Use `defaultValue` for text inputs and `defaultChecked` for checkboxes/radios to set initial values without controlling subsequent updates.', 'multiple-choice', 'beginner', 5, NULL, NULL, '`defaultValue` sets the initial DOM value but doesn’t override user changes, unlike `value` which fully controls the input.', NULL, ARRAY[]::text[], ARRAY['react','forms','uncontrolled','defaultvalue'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q210","original_type":"open-ended","topic":"Uncontrolled Components","subcategory":"Forms","sample_answers":["Use `<input defaultValue=\\"John\\" />` to set the initial value of an uncontrolled input. For checkboxes, use `defaultChecked={true}`.","This is useful when you need to initialize a form but don’t want to manage every keystroke with state."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 212: What is your favorite React stack?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('3bf2e722-39f5-4b89-a650-5a5b2aef2b09', 'What is your favorite React stack?', 'A popular stack includes Redux/redux-saga for state, react-router for routing, styled-components for styling, axios for API calls, and webpack/Babel for tooling.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This stack balances maturity, performance, and developer experience for large-scale applications.', NULL, ARRAY[]::text[], ARRAY['react','boilerplate','redux','saga','tooling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q211","original_type":"open-ended","topic":"React Ecosystem","subcategory":"React Ecosystem","sample_answers":["A robust React stack: Redux + redux-saga for state and side effects, React Router for navigation, styled-components for styling, axios for HTTP, and Webpack/Babel for builds.","Projects like react-boilerplate provide this setup out of the box for production-ready apps."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 213: What is the difference between Real DOM and Virtual DOM?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('687555a0-325d-461d-a041-fe449f7a1471', 'What is the difference between Real DOM and Virtual DOM?', 'Real DOM updates are slow and expensive; Virtual DOM is a lightweight JS representation that enables fast diffing and minimal real DOM mutations.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"Real DOM is faster than Virtual DOM","isCorrect":false},{"id":"b","text":"Real DOM updates are slow/expensive; Virtual DOM enables fast, minimal updates","isCorrect":true},{"id":"c","text":"Virtual DOM directly manipulates HTML","isCorrect":false},{"id":"d","text":"They are the same","isCorrect":false}]', NULL, 'Virtual DOM allows React to compute the minimal set of changes before touching the real DOM, optimizing performance.', NULL, ARRAY[]::text[], ARRAY['react','virtual-dom','real-dom','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q212","original_type":"multiple-choice","topic":"Virtual DOM","subcategory":"Virtual DOM","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"0ee220a5-c50b-4c32-afc5-a654931c9fcd"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 214: How to add Bootstrap to a React application?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('b007dab8-c8bc-431c-b458-b589dbf5084b', 'How to add Bootstrap to a React application?', 'Three ways: (1) CDN, (2) npm install bootstrap + import CSS, (3) React-specific libraries like react-bootstrap or reactstrap.', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"Only via CDN","isCorrect":false},{"id":"b","text":"CDN, npm install, or React wrappers like react-bootstrap","isCorrect":true},{"id":"c","text":"Bootstrap is built into React","isCorrect":false},{"id":"d","text":"Only with Sass","isCorrect":false}]', NULL, 'React-specific libraries (react-bootstrap) wrap Bootstrap components as React components, avoiding jQuery dependency.', NULL, ARRAY[]::text[], ARRAY['react','bootstrap','styling','css'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q213","original_type":"multiple-choice","topic":"Third-Party Libraries","subcategory":"Styling","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 215: Can you list top websites using React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('47c1574a-4d8d-40a0-abb4-39b729eed483', 'Can you list top websites using React?', 'Top sites: Facebook, Instagram, WhatsApp, Uber, Airbnb, Netflix, PayPal, Khan Academy, Dropbox, Flipboard.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'React’s component model and performance make it ideal for large, dynamic user interfaces.', NULL, ARRAY[]::text[], ARRAY['react','companies','adoption','real-world'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q214","original_type":"open-ended","topic":"React Adoption","subcategory":"React Ecosystem","sample_answers":["Major companies using React include Facebook (creator), Instagram, WhatsApp, Airbnb, Netflix, Uber, and PayPal.","These companies rely on React for its declarative UI, performance, and rich ecosystem."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 216: Is it recommended to use CSS In JS in React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('558b29f3-7561-475c-80e4-93795dfbb346', 'Is it recommended to use CSS In JS in React?', 'React has no opinion, but beginners should start with CSS files and className. CSS-in-JS (e.g., styled-components) is a valid alternative for scoped styles.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'CSS-in-JS offers benefits like dynamic styling and scoping, but traditional CSS is simpler for beginners.', NULL, ARRAY[]::text[], ARRAY['react','css-in-js','styled-components','styling'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q215","original_type":"true-false","topic":"CSS-in-JS","subcategory":"CSS-in-JS","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 217: Do I need to rewrite all my class components with hooks?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('293e89a6-c870-4fe2-86e1-542bade59b92', 'Do I need to rewrite all my class components with hooks?', 'No. Hooks are additive; you can use them in new components without rewriting existing class components.', 'multiple-choice', 'beginner', 3, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'React has no plans to remove class components. Hooks and classes can coexist in the same app.', NULL, ARRAY[]::text[], ARRAY['react','hooks','class-components','migration'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q216","original_type":"true-false","topic":"React Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 218: What is useEffect hook? How to fetch data with React Hooks?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('9a2d8cf8-486e-4d6a-94fb-5a025ea27b24', 'What is useEffect hook? How to fetch data with React Hooks?', '`useEffect` handles side effects in function components. Use it with `useState` to fetch data on mount by passing an empty dependency array.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It replaces componentDidMount, componentDidUpdate, and componentWillUnmount with a unified API.', NULL, ARRAY[]::text[], ARRAY['react','useeffect','data-fetching','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q217","original_type":"open-ended","topic":"useEffect","subcategory":"React Hooks","sample_answers":["`useEffect(() => { fetch(url).then(setData); }, []);` fetches data once on mount. The empty array ensures it doesn’t re-run on updates.","Always include cleanup for subscriptions or async operations to avoid memory leaks or state updates on unmounted components."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 219: Is Hooks cover all use cases for classes?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('058809d2-5fac-4044-8e5e-453e398be807', 'Is Hooks cover all use cases for classes?', 'Not yet. Hooks don’t cover `getSnapshotBeforeUpdate` and `componentDidCatch` (error boundaries).', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Error boundaries and snapshot reading are still class-only features, though the React team plans to add Hook equivalents.', NULL, ARRAY[]::text[], ARRAY['react','hooks','class-lifecycle','limitations'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q218","original_type":"true-false","topic":"React Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 220: What is the stable release for hooks support?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('a4b6c529-bb63-479e-81f8-d9675e5f45a5', 'What is the stable release for hooks support?', 'React 16.8 introduced stable Hooks support for React DOM, React DOM Server, React Test Renderer, and React Shallow Renderer.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'React 16.8 (February 2019) was the first version to include Hooks as a stable feature.', NULL, ARRAY[]::text[], ARRAY['react','hooks','16.8','release'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q219","original_type":"open-ended","topic":"React Hooks","subcategory":"React Hooks","sample_answers":["React 16.8, released in February 2019, was the first stable version to include Hooks across all official React packages.","This allowed function components to use state and side effects without classes."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 221: Why do we use array destructuring in `useState`?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('7b88f7f1-d82a-4416-ab8d-5caf62f55794', 'Why do we use array destructuring in `useState`?', '`useState` returns an array `[value, setValue]`. Destructuring makes it clear and concise: `const [count, setCount] = useState(0)`.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Array destructuring provides semantic names for the state value and updater function, improving readability.', NULL, ARRAY[]::text[], ARRAY['react','hooks','destructuring','useState'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q220","original_type":"open-ended","topic":"useState","subcategory":"React Hooks","sample_answers":["Instead of `const state = useState(0); const count = state[0]; const setCount = state[1];`, we write `const [count, setCount] = useState(0);` for clarity.","This pattern is idiomatic in React and makes state usage self-documenting."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 222: What are the sources used for introducing hooks?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('5037ade3-3fff-45a1-8a7b-0bbc451249d6', 'What are the sources used for introducing hooks?', 'Hooks drew inspiration from: react-future experiments, render props (Reactions Component), DisplayScript state cells, Rx subscriptions, and ReasonReact reducer components.', 'multiple-choice', 'advanced', 7, '[{"id":"a","text":"Only from Angular","isCorrect":false},{"id":"b","text":"From react-future, render props, DisplayScript, Rx, and ReasonReact","isCorrect":true},{"id":"c","text":"Invented from scratch with no prior art","isCorrect":false},{"id":"d","text":"Based on Vue composition API","isCorrect":false}]', NULL, 'Hooks unified patterns from the React ecosystem into a single, composable API.', NULL, ARRAY[]::text[], ARRAY['react','hooks','history','design'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q221","original_type":"multiple-choice","topic":"React Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 223: How do you access imperative API of web components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('a07944a1-9c5f-41a3-97ee-0157582c8489', 'How do you access imperative API of web components?', 'Use a `ref` to get the DOM node and call its methods directly, or wrap the web component in a React component for better integration.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Web components often expose methods like `focus()` or `play()` that must be called imperatively via refs.', NULL, ARRAY[]::text[], ARRAY['react','web-components','imperative','refs'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q222","original_type":"open-ended","topic":"Web Components","subcategory":"Integration","sample_answers":["Use `const wcRef = useRef();` and `<my-element ref={wcRef} />`, then call `wcRef.current.myMethod()`.","For better React integration, create a wrapper component that manages the web component’s state and props."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 224: What is Formik?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('f229d747-9256-4ecc-abf6-cc8e747ca580', 'What is Formik?', 'Formik is a small React form library that handles form state, validation, and submission.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'It solves the three main form problems: getting values in/out of state, validation, and submission handling.', NULL, ARRAY[]::text[], ARRAY['formik','forms','react','validation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q223","original_type":"open-ended","topic":"Formik","subcategory":"Form Libraries","sample_answers":["Formik provides a minimal API to manage form state, validate inputs, and handle submission without Redux.","It uses render props or hooks to give you access to values, errors, and helpers like `handleChange` and `handleSubmit`."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 225: What are typical middleware choices for async calls in Redux?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('d56d1ab4-6ccf-405e-8d9a-a68b49c2b62d', 'What are typical middleware choices for async calls in Redux?', 'Popular choices: Redux Thunk, Redux Saga, and Redux Promise.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Only Redux Thunk","isCorrect":false},{"id":"b","text":"Redux Thunk, Redux Saga, Redux Promise","isCorrect":true},{"id":"c","text":"Middleware is not needed for async","isCorrect":false},{"id":"d","text":"Only Redux Saga","isCorrect":false}]', NULL, 'These middleware enable side effects like API calls in Redux’s otherwise pure action-reducer flow.', NULL, ARRAY[]::text[], ARRAY['redux','middleware','thunk','saga','async'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q224","original_type":"multiple-choice","topic":"Redux Middleware","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 226: Do browsers understand JSX code?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('2af9e072-60ea-4a36-9347-8af596bc0820', 'Do browsers understand JSX code?', 'No. JSX must be transpiled to `React.createElement()` calls using Babel or similar tools.', 'multiple-choice', 'beginner', 3, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Browsers only understand standard JavaScript; JSX is syntactic sugar transformed at build time.', NULL, ARRAY[]::text[], ARRAY['react','jsx','babel','transpilation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q225","original_type":"true-false","topic":"JSX","subcategory":"JSX","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 227: Describe data flow in React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('96e07f12-31a3-41e5-8094-41d3e04f3bc4', 'Describe data flow in React?', 'React uses one-way data flow: data flows down from parent to child via props, making the data flow predictable and easier to debug.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Parents pass data to children via props; children communicate back via callback functions.', NULL, ARRAY[]::text[], ARRAY['react','props','one-way-data-flow','architecture'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q226","original_type":"open-ended","topic":"Data Flow","subcategory":"React Basics","sample_answers":["Data flows unidirectionally: parent components pass data to children via props, and children trigger updates by calling parent-provided callbacks.","This contrasts with two-way binding (e.g., AngularJS), where child changes automatically update parent state."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 228: What is MobX?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('06f46936-f99d-4521-857e-270c97a33a74', 'What is MobX?', 'MobX is a state management library that uses observable state and reactive programming to automatically update UI when state changes.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'It tracks which components depend on which state and re-renders them automatically when that state changes.', NULL, ARRAY[]::text[], ARRAY['mobx','state-management','react','observable'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q227","original_type":"open-ended","topic":"MobX","subcategory":"State Management","sample_answers":["MobX makes state observable and automatically re-renders components that use that state—no need for explicit selectors or reducers.","It’s often described as ''transparent reactivity''—you mutate state directly, and the UI updates magically."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 229: What are the differences between Redux and MobX?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('5e480af2-2581-4747-a407-e3edbb0ead48', 'What are the differences between Redux and MobX?', 'Redux: single store, immutable, explicit updates. MobX: multiple stores, mutable, automatic updates via observables.', 'multiple-choice', 'advanced', 8, '[{"id":"a","text":"Redux uses observables; MobX uses reducers","isCorrect":false},{"id":"b","text":"Redux: single immutable store, explicit; MobX: multiple mutable stores, automatic","isCorrect":true},{"id":"c","text":"They are identical","isCorrect":false},{"id":"d","text":"MobX requires more boilerplate","isCorrect":false}]', NULL, 'Redux is explicit and predictable; MobX is implicit and automatic. Redux scales better for large teams; MobX is simpler for small apps.', NULL, ARRAY[]::text[], ARRAY['redux','mobx','comparison','state-management'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q228","original_type":"multiple-choice","topic":"Redux vs MobX","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 230: Should I learn ES6 before learning ReactJS?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('b271bcfb-88a5-458e-bb48-5e69dbd4ccdc', 'Should I learn ES6 before learning ReactJS?', 'Not required, but helpful. React ecosystem heavily uses ES6 features like destructuring, spread operator, and arrow functions.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'You can learn React without ES6, but most tutorials and codebases assume ES6 knowledge.', NULL, ARRAY[]::text[], ARRAY['react','es6','javascript','learning'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q229","original_type":"true-false","topic":"JavaScript Fundamentals","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 231: What is Concurrent Rendering?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('38fec334-b230-4ffc-8458-a4c4ac3006ca', 'What is Concurrent Rendering?', 'Concurrent Rendering allows React to interrupt long renders to handle high-priority updates (e.g., user input), improving responsiveness.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'It enables interruptible rendering, so the UI stays responsive during heavy updates.', NULL, ARRAY[]::text[], ARRAY['react','concurrent','rendering','suspense'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q230","original_type":"open-ended","topic":"Concurrent Rendering","subcategory":"Concurrent React","sample_answers":["Concurrent Rendering lets React pause and resume work based on priority—e.g., user clicks are handled before finishing a low-priority background render.","It’s the foundation for features like Suspense and automatic batching."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 232: What is the difference between async mode and concurrent mode?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('ec656186-2c17-4850-aa20-7944bce885f0', 'What is the difference between async mode and concurrent mode?', 'They are the same. ''Async Mode'' was the old name; ''Concurrent Mode'' is the current name to emphasize priority-based rendering.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'The name changed to avoid confusion with other async patterns and to highlight the priority-based scheduling.', NULL, ARRAY[]::text[], ARRAY['react','concurrent-mode','async-mode','history'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q231","original_type":"true-false","topic":"Concurrent Rendering","subcategory":"Concurrent React","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 233: Can I use javascript URLs in React 16.9?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('6c565e8c-f77e-490d-92c4-448022009172', 'Can I use javascript URLs in React 16.9?', 'Yes, but it logs a warning because `javascript:` URLs are a security risk (XSS). Future versions will throw an error.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'React warns against `javascript:` URLs to prevent accidental injection of malicious code.', NULL, ARRAY[]::text[], ARRAY['react','security','xss','javascript-urls'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q232","original_type":"true-false","topic":"XSS Prevention","subcategory":"XSS (Cross-Site Scripting)","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 234: What is the purpose of ESLint plugin for hooks?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('c14db56f-f2e5-42d4-ba10-83a94dc68b97', 'What is the purpose of ESLint plugin for hooks?', 'It enforces the Rules of Hooks: (1) call hooks at top level, (2) call hooks only from React functions.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'The plugin prevents bugs by ensuring hooks are called consistently across renders.', NULL, ARRAY[]::text[], ARRAY['react','hooks','eslint','linting'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q233","original_type":"open-ended","topic":"Rules of Hooks","subcategory":"React Hooks","sample_answers":["The `eslint-plugin-react-hooks` enforces that hooks are called in the same order every render and only from function components or custom hooks.","It also includes `exhaustive-deps` to validate effect dependencies."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 235: What is the difference between Imperative and Declarative in React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('18a4f4f3-f988-45e7-8a71-b491f979e342', 'What is the difference between Imperative and Declarative in React?', 'Imperative: how to do it (step-by-step DOM manipulation). Declarative: what the UI should look like (describe state, React handles updates).', 'multiple-choice', 'beginner', 5, NULL, NULL, 'React is declarative: you describe the UI for a given state, and React handles the DOM updates.', NULL, ARRAY[]::text[], ARRAY['react','declarative','imperative','paradigm'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q234","original_type":"open-ended","topic":"Declarative UI","subcategory":"React Basics","sample_answers":["Imperative: ''If liked, remove grey class and add blue class.'' Declarative: ''If liked, render <BlueLike />; else render <GreyLike />.''","Declarative code is easier to reason about because it separates ''what'' from ''how''."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 236: What are the benefits of using TypeScript with ReactJS?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('b327c74d-2673-4720-9af2-92e4a328933b', 'What are the benefits of using TypeScript with ReactJS?', 'Benefits: type safety, better IDE support, easier refactoring, interfaces for props/state, and fewer runtime errors.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Slower development","isCorrect":false},{"id":"b","text":"Type safety, IDE support, fewer bugs, better refactoring","isCorrect":true},{"id":"c","text":"Only for backend","isCorrect":false},{"id":"d","text":"Increases bundle size significantly","isCorrect":false}]', NULL, 'TypeScript catches errors at compile time and provides excellent autocomplete and navigation in editors like VS Code.', NULL, ARRAY[]::text[], ARRAY['typescript','react','types','ide'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q235","original_type":"multiple-choice","topic":"TypeScript with React","subcategory":"TypeScript with React","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 237: How to keep user authenticated on page refresh with Context API?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('f0a208fb-7dba-47d9-83d7-c821dd152271', 'How to keep user authenticated on page refresh with Context API?', 'Call `loadUser()` in `useEffect` in App.js, ensuring the AuthProvider wraps the entire app in index.js to persist auth state across refreshes.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'On page load, check for a token in storage and validate it to restore the user session.', NULL, ARRAY[]::text[], ARRAY['react','context-api','authentication','persistence'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q236","original_type":"open-ended","topic":"Context API","subcategory":"Authentication","sample_answers":["Wrap your app in an AuthProvider in index.js. In App.js, use `useEffect` to call `loadUser()`, which checks localStorage/sessionStorage for a token and validates it with the backend.","This ensures the user remains logged in after refresh, similar to how Redux would handle it with a root-level dispatch."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 238: What are the benefits of new JSX transform?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('4abf8ff4-7183-418f-957b-d58fde0fd994', 'What are the benefits of new JSX transform?', 'Benefits: (1) no need to import React, (2) slightly smaller bundles, (3) simpler mental model for beginners.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Requires explicit React import","isCorrect":false},{"id":"b","text":"No React import needed, smaller bundles, simpler learning","isCorrect":true},{"id":"c","text":"Only works with class components","isCorrect":false},{"id":"d","text":"Increases bundle size","isCorrect":false}]', NULL, 'The new transform compiles JSX to `_jsx()` calls from `react/jsx-runtime`, removing the need for React in scope.', NULL, ARRAY[]::text[], ARRAY['react','jsx','transform','babel'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q237","original_type":"multiple-choice","topic":"JSX","subcategory":"JSX","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 239: How is the new JSX transform different from old transform?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('1d29010c-d03b-4faa-af39-57b6324bf4b0', 'How is the new JSX transform different from old transform?', 'Old: compiles to `React.createElement()`, requires `import React`. New: compiles to `_jsx()` from `react/jsx-runtime`, no React import needed.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'The new transform reduces boilerplate and bundle size by using a dedicated runtime.', NULL, ARRAY[]::text[], ARRAY['react','jsx','babel','compilation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q238","original_type":"open-ended","topic":"JSX","subcategory":"JSX","sample_answers":["Old: `import React from ''react'';` and `<div />` → `React.createElement(''div'')`. New: no import needed; `<div />` → `_jsx(''div'', {})`.","You still need to import React to use Hooks, but not for JSX alone."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 240: What are React Server Components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('1461a2c2-61ab-45aa-9a8d-8522c4f690a9', 'What are React Server Components?', 'React Server Components render on the server, allowing direct data fetching and reducing client bundle size. Still experimental.', 'multiple-choice', 'advanced', 9, NULL, NULL, 'They enable zero-bundle-size components that run only on the server, with automatic client hydration for interactivity.', NULL, ARRAY[]::text[], ARRAY['react','server-components','rsc','nextjs'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q239","original_type":"open-ended","topic":"React Server Components","subcategory":"React Server Components","sample_answers":["React Server Components run on the server, can access databases directly, and send only JSON to the client—reducing JavaScript bundle size.","They’re a key part of Next.js App Router and the future of React full-stack architecture."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"759ee497-71fd-4347-9018-c9d62ba7470f"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 241: What is prop drilling?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('1af3c739-8045-4321-9ba6-501bd48b2c60', 'What is prop drilling?', 'Prop drilling is passing data through multiple levels of components that don’t need it, just to get it to a deeply nested child.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'It makes components tightly coupled and hard to refactor. Solutions: Context API or state management libraries.', NULL, ARRAY[]::text[], ARRAY['react','prop-drilling','context','anti-pattern'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q240","original_type":"open-ended","topic":"Props","subcategory":"React Basics","sample_answers":["Prop drilling occurs when you pass a prop through many intermediate components that don’t use it, just to reach a deep child.","This creates unnecessary dependencies. Use Context or Redux to avoid it."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"accaeeab-e45f-4999-a647-4e5126bcd487"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 242: What is the difference between useState and useRef hook?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('cdfc6323-6308-4480-b7bc-23cd8b321788', 'What is the difference between useState and useRef hook?', '`useState` triggers re-renders on update; `useRef` holds a mutable value without causing re-renders.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Both cause re-renders","isCorrect":false},{"id":"b","text":"useState causes re-renders; useRef does not","isCorrect":true},{"id":"c","text":"useRef is only for DOM elements","isCorrect":false},{"id":"d","text":"useState cannot hold objects","isCorrect":false}]', NULL, '`useRef` is for persisting values across renders without triggering updates—ideal for timers, intervals, or DOM references.', NULL, ARRAY[]::text[], ARRAY['react','hooks','usestate','useref'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q241","original_type":"multiple-choice","topic":"useState vs useRef","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 243: What is a wrapper component?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('7c108901-9507-4305-ae59-8757301747f5', 'What is a wrapper component?', 'A wrapper component surrounds other components to add functionality, styling, or layout without modifying the wrapped components.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'It’s a form of composition that promotes reuse and separation of concerns.', NULL, ARRAY[]::text[], ARRAY['react','wrapper','hoc','composition'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q242","original_type":"open-ended","topic":"Component Composition","subcategory":"React Basics","sample_answers":["A wrapper component adds behavior or UI around children—e.g., a `Card` component that adds padding and a border.","Example: `<AuthWrapper><Profile /></AuthWrapper>` could check authentication before rendering the child."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 244: What are the differences between useEffect and useLayoutEffect?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('2f353e7d-5a89-425e-a440-bc0895905867', 'What are the differences between useEffect and useLayoutEffect?', '`useEffect` runs after browser paint; `useLayoutEffect` runs synchronously before paint, avoiding visual flicker for layout measurements.', 'multiple-choice', 'advanced', 8, '[{"id":"a","text":"useEffect runs before paint; useLayoutEffect after","isCorrect":false},{"id":"b","text":"useEffect runs after paint; useLayoutEffect runs before paint synchronously","isCorrect":true},{"id":"c","text":"They are identical","isCorrect":false},{"id":"d","text":"useLayoutEffect is deprecated","isCorrect":false}]', NULL, 'Use `useLayoutEffect` only when you need to measure or adjust layout before the user sees it.', NULL, ARRAY[]::text[], ARRAY['react','useeffect','uselayouteffect','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q243","original_type":"multiple-choice","topic":"useLayoutEffect","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 245: What are the differences between Functional and Class Components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('1697ea53-8a6b-4e21-babf-5f133085dae6', 'What are the differences between Functional and Class Components?', 'Functional: simpler syntax, use Hooks for state/effects. Class: use lifecycle methods, `this.state`, and `this.props`. Both can use state and props now.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"Functional components can''t use state","isCorrect":false},{"id":"b","text":"Functional use Hooks; Class use lifecycle methods and this","isCorrect":true},{"id":"c","text":"Class components are faster","isCorrect":false},{"id":"d","text":"Functional components can''t receive props","isCorrect":false}]', NULL, 'With Hooks, functional components can do everything class components can, with less boilerplate.', NULL, ARRAY[]::text[], ARRAY['react','functional-components','class-components','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q244","original_type":"multiple-choice","topic":"Component Types","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 246: What is strict mode in React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('08a25492-9ab7-40d7-bb74-321724d188f5', 'What is strict mode in React?', '`React.StrictMode` enables additional development-only checks for potential problems like unsafe lifecycles, legacy APIs, and side effects.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'It helps catch bugs early by identifying unsafe patterns and legacy code.', NULL, ARRAY[]::text[], ARRAY['react','strict-mode','development','warnings'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q245","original_type":"open-ended","topic":"Strict Mode","subcategory":"React Basics","sample_answers":["`StrictMode` is a development tool that warns about deprecated APIs, unsafe lifecycle methods, and side effects in render.","It only runs in development and has no effect in production builds."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 247: What is the benefit of strict mode?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('7cdcfc98-9db5-4930-a7d5-6581af80ef11', 'What is the benefit of strict mode?', 'Benefits: detects impure renders, missing effect cleanup, unsafe lifecycles, legacy refs/context, and findDOMNode usage.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"Improves production performance","isCorrect":false},{"id":"b","text":"Detects side effects, unsafe lifecycles, legacy APIs, and missing cleanup","isCorrect":true},{"id":"c","text":"Reduces bundle size","isCorrect":false},{"id":"d","text":"Only for class components","isCorrect":false}]', NULL, 'It prepares your code for future React features like concurrent rendering by enforcing purity.', NULL, ARRAY[]::text[], ARRAY['react','strict-mode','debugging','best-practices'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q246","original_type":"multiple-choice","topic":"Strict Mode","subcategory":"React Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 248: Why does strict mode render twice in React?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('0991b885-45c9-4bb7-a1db-2c2e0d94659a', 'Why does strict mode render twice in React?', 'In development, StrictMode renders components twice to detect accidental side effects in the render phase by exposing non-idempotent code.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'This double-invocation helps ensure components are pure and safe for concurrent features.', NULL, ARRAY[]::text[], ARRAY['react','strict-mode','side-effects','development'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q247","original_type":"open-ended","topic":"Strict Mode","subcategory":"React Basics","sample_answers":["StrictMode calls render and effect functions twice in development to reveal side effects that rely on single execution.","This prepares apps for concurrent rendering, where React may interrupt or re-run renders."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 249: What are the rules of JSX?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('813ca5e4-2d38-41c7-8562-2f4564d05020', 'What are the rules of JSX?', 'Rules: (1) return single root element, (2) all tags must be closed, (3) use camelCase for attributes (e.g., `className`).', 'multiple-choice', 'beginner', 5, '[{"id":"a","text":"Tags don''t need to be closed","isCorrect":false},{"id":"b","text":"Single root, closed tags, camelCase attributes","isCorrect":true},{"id":"c","text":"Use class instead of className","isCorrect":false},{"id":"d","text":"Multiple roots are allowed","isCorrect":false}]', NULL, 'These rules ensure JSX can be reliably transformed to JavaScript function calls.', NULL, ARRAY[]::text[], ARRAY['react','jsx','syntax','rules'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q248","original_type":"multiple-choice","topic":"JSX","subcategory":"JSX","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 250: What is the reason behind multiple JSX tags to be wrapped?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('7654d094-b93e-4169-ab3d-a3137e0cf117', 'What is the reason behind multiple JSX tags to be wrapped?', 'JSX compiles to JavaScript function calls, and a function can only return one value—so multiple elements must be wrapped in a parent or Fragment.', 'multiple-choice', 'beginner', 4, NULL, NULL, 'Without a wrapper, the transpiler would produce invalid JavaScript (multiple return values).', NULL, ARRAY[]::text[], ARRAY['react','jsx','fragments','compilation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q249","original_type":"open-ended","topic":"JSX","subcategory":"JSX","sample_answers":["JSX is compiled to `React.createElement()` calls, and a function can only return one value—so multiple elements need a common parent.","React 16+ introduced Fragments (`<>...</>`) to avoid unnecessary DOM wrappers."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"6a7eecc1-7cea-4bfd-84d3-e5ddff1d6624"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 251: How do you prevent mutating array variables?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('60463d30-ee98-4b49-8d33-b912c5251af6', 'How do you prevent mutating array variables?', 'Avoid mutation methods (`push`, `pop`, `sort`, `reverse`). Use non-mutating methods (`map`, `filter`, `slice`) or spread syntax to create new arrays.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'React and Redux rely on immutability to detect changes via reference comparison.', NULL, ARRAY[]::text[], ARRAY['javascript','immutability','arrays','react'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q250","original_type":"open-ended","topic":"Immutable Data Structures","subcategory":"Shallow vs Deep Copy","sample_answers":["Instead of `arr.push(item)`, use `[...arr, item]`. Instead of `arr.sort()`, use `[...arr].sort()`.","Non-mutating methods like `map` and `filter` return new arrays, preserving immutability for state updates."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 252: What are capture phase events?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('bc057166-4c4e-40ce-b1d8-911e2b0d7c9f', 'What are capture phase events?', 'Capture phase events (e.g., `onClickCapture`) fire during the downward phase of event propagation, before the target and bubbling phases.', 'multiple-choice', 'advanced', 7, NULL, NULL, 'They’re useful for analytics or global handlers that need to run before child event handlers.', NULL, ARRAY[]::text[], ARRAY['react','events','capture-phase','dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q251","original_type":"open-ended","topic":"Event Bubbling & Capturing","subcategory":"DOM Events","sample_answers":["`onClickCapture` fires from root to target during the capture phase, even if child events call `stopPropagation()`.","Order: capture handlers (top-down) → target handler → bubble handlers (bottom-up)."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 253: How does React update the screen in an application?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('63e15ab7-8a6a-42f1-a4b8-501abc964466', 'How does React update the screen in an application?', 'React updates the UI in three steps: (1) Triggering a render (initial or state update), (2) Rendering components recursively, (3) Committing minimal DOM changes.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'React uses a render-commit cycle: it first computes what to render, then applies only necessary changes to the DOM for performance.', NULL, ARRAY[]::text[], ARRAY['react','rendering','dom','reconciliation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q252","original_type":"open-ended","topic":"Rendering","subcategory":"React Basics","sample_answers":["React triggers a render on mount or state change, recursively calls component functions to build a virtual DOM, then commits minimal real DOM updates.","This three-step process ensures efficient UI updates without unnecessary repaints or reflows."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 254: How does React batch multiple state updates?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('ec0bc802-2675-42f8-92f8-004f3fa53b70', 'How does React batch multiple state updates?', 'React groups multiple state updates in event handlers into a single re-render (batching). React 18 extends this to async code (automatic batching).', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Automatic batching in React 18 ensures that even async updates (e.g., in fetch callbacks) trigger only one re-render, improving performance.', NULL, ARRAY[]::text[], ARRAY['react','batching','state','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q253","original_type":"open-ended","topic":"State Updates","subcategory":"State Management","sample_answers":["React batches multiple `setState` calls in the same event handler into one re-render. In React 18, this works even in promises and timeouts.","Example: calling `setA()` and `setB()` in a fetch callback causes only one re-render, not two."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 255: Is it possible to prevent automatic batching?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('87c32eda-eb7e-4731-b68f-8bb2866af29b', 'Is it possible to prevent automatic batching?', 'Yes, using `flushSync` from `react-dom` to force immediate DOM updates and disable batching for specific state changes.', 'multiple-choice', 'advanced', 8, NULL, NULL, '`flushSync` forces React to process state updates immediately, useful when you need DOM to reflect state before next line executes.', NULL, ARRAY[]::text[], ARRAY['react','flushsync','batching','dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q254","original_type":"open-ended","topic":"State Updates","subcategory":"State Management","sample_answers":["`flushSync(() => { setA(a+1); }); setB(b+1);` causes two re-renders: one after `setA`, another after `setB`.","Use sparingly—it defeats performance benefits of batching and should only be used when DOM measurement is required mid-update."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 256: What is React hydration?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('86c194fa-c70e-49db-9f27-902e0bed232b', 'What is React hydration?', 'Hydration attaches event handlers and state to server-rendered static HTML, making it interactive on the client.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'After server sends pre-rendered HTML, `hydrateRoot` makes it interactive by attaching React event system and state.', NULL, ARRAY[]::text[], ARRAY['react','hydration','ssr','seo'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q255","original_type":"open-ended","topic":"Hydration","subcategory":"Server-Side Rendering","sample_answers":["Hydration is the process where React takes static HTML from the server and ''activates'' it by attaching event listeners and state.","It enables fast initial load (SEO-friendly) with full interactivity, bridging server and client rendering."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 257: How do you update objects inside state?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('7ca7195f-d2ac-445b-bb49-0f2bfe691d04', 'How do you update objects inside state?', 'Never mutate state objects directly. Create a new object using spread syntax: `setUser({ ...user, name: ''New'' })`.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'React relies on object reference comparison; mutating state directly won’t trigger re-renders.', NULL, ARRAY[]::text[], ARRAY['react','state','immutability','objects'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q256","original_type":"open-ended","topic":"Immutable State","subcategory":"State Management","sample_answers":["Use spread syntax to create a new object: `setUser({ ...user, firstName: e.target.value })` instead of `user.firstName = ...`.","Direct mutation bypasses React’s change detection, so the UI won’t update even if state changes."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 258: How do you update nested objects inside state?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('1f5c3397-479c-47da-be23-9cda765b3407', 'How do you update nested objects inside state?', 'Use nested spread syntax: `setUser({ ...user, address: { ...user.address, country: ''Germany'' } })`.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Spread is shallow, so nested objects require spreading at each level to preserve immutability.', NULL, ARRAY[]::text[], ARRAY['react','nested-state','immutability','objects'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q257","original_type":"open-ended","topic":"Immutable State","subcategory":"State Management","sample_answers":["For `user.address.country`, update with: `setUser({ ...user, address: { ...user.address, country: ''Germany'' } })`.","This ensures a new reference for both the top-level object and the nested object, triggering re-renders correctly."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 259: How do you update arrays inside state?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('36d74fc7-9923-42b2-b9db-129a5db6c022', 'How do you update arrays inside state?', 'Avoid mutating methods (`push`, `pop`). Use non-mutating methods (`filter`, `map`, spread) to create new arrays.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'React compares array references; mutating the original array won’t trigger a re-render.', NULL, ARRAY[]::text[], ARRAY['react','arrays','immutability','state'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q258","original_type":"open-ended","topic":"Immutable State","subcategory":"State Management","sample_answers":["To add an item: `setTodos([...todos, newItem])`. To remove: `setTodos(todos.filter((_, i) => i !== index))`.","Never use `todos.push()` directly—always create a new array instance."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 260: How do you use immer library for state updates?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('951714d8-db47-4bee-a117-9f280c912999', 'How do you use immer library for state updates?', 'Use `useImmer` to write mutable-style code that produces immutable updates via proxies.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'Immer lets you write `draft.address.country = ''Germany''` while ensuring the final state is immutable.', NULL, ARRAY[]::text[], ARRAY['react','immer','immutability','state'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q259","original_type":"open-ended","topic":"Immer","subcategory":"State Management","sample_answers":["Install `use-immer`, then `const [user, setUser] = useImmer(initialState); setUser(draft => { draft.address.country = ''Germany''; });`","Immer uses proxies to track mutations and produce a new immutable state object automatically."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 261: What are the benefits of preventing direct state mutations?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('dc8092d6-05fc-4fc9-b8bf-784f4948941b', 'What are the benefits of preventing direct state mutations?', 'Prevents bugs, enables predictable re-renders, supports time-travel debugging, and aligns with React’s declarative model.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Allows faster direct DOM manipulation","isCorrect":false},{"id":"b","text":"Enables predictable re-renders, debugging, and performance optimizations","isCorrect":true},{"id":"c","text":"Reduces bundle size","isCorrect":false},{"id":"d","text":"Makes code more verbose","isCorrect":false}]', NULL, 'Immutability ensures React can detect changes via reference comparison, enabling performance optimizations and debugging tools.', NULL, ARRAY[]::text[], ARRAY['react','immutability','state','debugging'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q260","original_type":"multiple-choice","topic":"Immutable State","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 262: What are preferred and non-preferred array operations for updating state?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('bb5eddeb-dc02-45e8-bfc2-afc0c0451935', 'What are preferred and non-preferred array operations for updating state?', 'Preferred: `concat`, `...`, `filter`, `map`, `slice`. Non-preferred: `push`, `pop`, `splice`, direct assignment, `sort`, `reverse`.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Use `push` and `splice` for performance","isCorrect":false},{"id":"b","text":"Preferred: concat/spread/filter/map; Non-preferred: push/pop/splice/sort","isCorrect":true},{"id":"c","text":"All array methods are safe","isCorrect":false},{"id":"d","text":"Only `slice` is safe","isCorrect":false}]', NULL, 'Non-mutating methods return new arrays, preserving immutability; mutating methods change the original array.', NULL, ARRAY[]::text[], ARRAY['react','arrays','immutability','state'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q261","original_type":"multiple-choice","topic":"Immutable State","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 263: What happens by defining nested function components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('8b313f76-fd81-4bd3-8b5a-7462023f9c7c', 'What happens by defining nested function components?', 'Defining components inside other components causes unnecessary re-renders and performance issues due to new identity on every render.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Nested components get a new identity each render, breaking memoization and causing child re-renders even when props haven’t changed.', NULL, ARRAY[]::text[], ARRAY['react','nested-components','performance','optimization'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q262","original_type":"open-ended","topic":"Component Definition","subcategory":"Performance Optimization","sample_answers":["Defining a component inside another creates a new function on every render, so `React.memo` won’t work and children re-render unnecessarily.","Always define components at the top level or outside the parent component to preserve referential identity."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 264: Can I use keys for non-list items?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('9471bd1e-ee4d-4d2a-8753-e6fe0cb0b162', 'Can I use keys for non-list items?', 'Yes. Keys help React identify components beyond lists, such as toggling between different component types.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'Keys are used whenever React needs to distinguish between elements of the same type, not just in lists.', NULL, ARRAY[]::text[], ARRAY['react','keys','components','reconciliation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q263","original_type":"true-false","topic":"Keys","subcategory":"Lists and Keys","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"540c20e9-c800-48de-9185-e611c10d37d5"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 265: What are the guidelines for writing reducers?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('26539725-5fc9-49ee-82dd-387fe4a8e481', 'What are the guidelines for writing reducers?', '1. Reducers must be pure (no side effects). 2. Each action should represent a single user intent, even if it updates multiple state fields.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"Reducers can make API calls","isCorrect":false},{"id":"b","text":"Reducers must be pure; actions should represent single user intents","isCorrect":true},{"id":"c","text":"Each state field needs its own action","isCorrect":false},{"id":"d","text":"Reducers should mutate state for performance","isCorrect":false}]', NULL, 'Pure reducers ensure predictability and testability; single-intent actions improve debugging and time-travel.', NULL, ARRAY[]::text[], ARRAY['react','reducers','redux','pure-functions'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q264","original_type":"multiple-choice","topic":"Reducers","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 266: How does ReactJS work behind the scenes?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('a90c914b-1369-4231-8b9c-c18c0f2a0ee3', 'How does ReactJS work behind the scenes?', 'React uses Virtual DOM, Fiber architecture, reconciliation, and commit phases to efficiently update UI with minimal DOM changes.', 'multiple-choice', 'advanced', 9, NULL, NULL, 'Fiber breaks work into units, enables interruptible rendering, and uses scheduler for priority-based updates.', NULL, ARRAY[]::text[], ARRAY['react','fiber','virtual-dom','reconciliation'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q265","original_type":"open-ended","topic":"Fiber Architecture","subcategory":"Virtual DOM","sample_answers":["React builds a Virtual DOM tree, reconciles it with the previous tree using a diffing algorithm, then commits minimal real DOM changes in the commit phase.","Fiber architecture allows React to pause, resume, and prioritize work, enabling concurrent features like Suspense and time-slicing."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 267: How is `useReducer` different from `useState`?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('1df3a622-dcc4-43d9-b235-8cbc9e421f76', 'How is `useReducer` different from `useState`?', 'useState: simple state, direct updates. useReducer: complex state, action-based updates, logic in reducer, more testable.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"useReducer is only for Redux","isCorrect":false},{"id":"b","text":"useState: simple state; useReducer: complex state with centralized logic","isCorrect":true},{"id":"c","text":"useState cannot hold objects","isCorrect":false},{"id":"d","text":"useReducer causes more re-renders","isCorrect":false}]', NULL, '`useReducer` centralizes update logic, making it ideal for complex state with multiple sub-values or dependencies.', NULL, ARRAY[]::text[], ARRAY['react','usereducer','usestate','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q266","original_type":"multiple-choice","topic":"useReducer","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 268: What is useContext? What are the steps to follow for useContext?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('887d9de5-010d-4585-871a-c86863a3d1b6', 'What is useContext? What are the steps to follow for useContext?', 'Steps: (1) Create context with `createContext()`, (2) Wrap with `Provider`, (3) Consume with `useContext()` in child components.', 'multiple-choice', 'intermediate', 6, NULL, NULL, '`useContext` eliminates prop drilling by allowing components to access shared state from a Provider.', NULL, ARRAY[]::text[], ARRAY['react','context-api','usecontext','state-management'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q267","original_type":"open-ended","topic":"Context API","subcategory":"Context API","sample_answers":["1. `const ThemeContext = createContext();` 2. `<ThemeContext.Provider value=\\"dark\\"><App /></ThemeContext.Provider>` 3. `const theme = useContext(ThemeContext);`","This pattern is ideal for themes, auth, or any global state that many components need."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 269: What are the use cases of useContext hook?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('c01ac667-64dd-4060-a9cd-6692233326c8', 'What are the use cases of useContext hook?', 'Theme switching, auth state, localization, modal visibility, and global state when combined with useReducer.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Only for passing props to immediate children","isCorrect":false},{"id":"b","text":"Theme, auth, i18n, modals, and global state with useReducer","isCorrect":true},{"id":"c","text":"Replacing all useState calls","isCorrect":false},{"id":"d","text":"Only for class components","isCorrect":false}]', NULL, 'Context is perfect for data that needs to be accessed by many components at different nesting levels.', NULL, ARRAY[]::text[], ARRAY['react','usecontext','global-state','theming'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q268","original_type":"multiple-choice","topic":"Context API","subcategory":"Context API","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 270: When to use client and server components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('5d130e24-3fa6-46a1-82dd-ae3b7a30dbf1', 'When to use client and server components?', 'Client: interactivity, state, effects, browser APIs. Server: data fetching, backend access, sensitive info, reducing JS bundle.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"Use client components for all data fetching","isCorrect":false},{"id":"b","text":"Client: interactivity/state; Server: data fetching/sensitive logic","isCorrect":true},{"id":"c","text":"Server components can use useState","isCorrect":false},{"id":"d","text":"Client components are always faster","isCorrect":false}]', NULL, 'Server Components run only on the server, have no interactivity, but can access databases directly and reduce client JS.', NULL, ARRAY[]::text[], ARRAY['nextjs','server-components','client-components','rsc'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q269","original_type":"multiple-choice","topic":"App Router","subcategory":"Next.js Basics","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 271: What are the differences between page router and app router in Next.js?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('60509bd6-7fe7-4160-8f80-b88be8caf9f8', 'What are the differences between page router and app router in Next.js?', 'Pages Router: file-based, client-side navigation. App Router: React Server Components, nested layouts, streaming, and built-in Suspense.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'App Router is the modern Next.js architecture with server-first rendering, layouts, and better data fetching.', NULL, ARRAY[]::text[], ARRAY['nextjs','app-router','pages-router','routing'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q270","original_type":"open-ended","topic":"App Router vs Pages Router","subcategory":"Next.js Basics","sample_answers":["Pages Router uses `pages/` directory with client-side routing. App Router uses `app/` with server components, nested layouts, and streaming.","App Router supports React Server Components, Suspense for data, and automatic code splitting by route."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 272: Can you describe the useMemo() Hook?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('e6c6199b-b8aa-4928-8bd4-e447b5124822', 'Can you describe the useMemo() Hook?', '`useMemo` caches expensive calculations and only recomputes when dependencies change.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It prevents unnecessary recalculations on every render, improving performance for derived data.', NULL, ARRAY[]::text[], ARRAY['react','usememo','performance','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q271","original_type":"open-ended","topic":"useMemo","subcategory":"React Hooks","sample_answers":["`const filteredUsers = useMemo(() => users.filter(u => u.name.includes(search)), [search]);` only filters when `search` changes.","Use `useMemo` for expensive operations like sorting, filtering, or complex computations that depend on props or state."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 273: Can Hooks be used in class components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('9ad36b65-e5cc-4c45-9794-366f7f0e6628', 'Can Hooks be used in class components?', 'No. Hooks are only for function components. Class and function components can coexist in the same app.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, 'Hooks rely on call order, which is only guaranteed in function components.', NULL, ARRAY[]::text[], ARRAY['react','hooks','class-components','function-components'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q272","original_type":"true-false","topic":"React Hooks","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 274: What is an updater function? Should it be used in all cases?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('e3b2ebfb-1191-429d-963e-5f4f6b372d30', 'What is an updater function? Should it be used in all cases?', 'An updater function is `setState(prev => newValue)`. Use it when state depends on previous state; not needed for static values.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Updater functions ensure you always have the latest state, avoiding stale closures in async or batched updates.', NULL, ARRAY[]::text[], ARRAY['react','setstate','updater-function','state'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q273","original_type":"open-ended","topic":"State Updates","subcategory":"State Management","sample_answers":["Use `setCount(c => c + 1)` when incrementing; use `setName(''John'')` for static values. The former avoids stale state issues.","In event handlers with multiple updates, updater functions guarantee correct sequencing: `setC(c=>c+1); setC(c=>c+1)` adds 2."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 275: Can useState take a function as an initial value?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('09bc28da-648f-4e7f-881c-813732eb8be1', 'Can useState take a function as an initial value?', 'Yes. `useState(() => expensiveComputation())` for lazy initialization—runs only once on mount.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'The initializer function runs only once, avoiding expensive computation on every render.', NULL, ARRAY[]::text[], ARRAY['react','usestate','lazy-initialization','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q274","original_type":"true-false","topic":"useState","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 276: What types of values can `useState` hold?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('bb53709b-e9d5-4e59-ae39-ba7ffa3e1769', 'What types of values can `useState` hold?', 'Any type: primitives, objects, arrays, functions, null, undefined. But objects/arrays require immutability for re-renders.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Only strings and numbers","isCorrect":false},{"id":"b","text":"Any type, but objects/arrays must be immutable","isCorrect":true},{"id":"c","text":"Functions are not allowed","isCorrect":false},{"id":"d","text":"Only primitives","isCorrect":false}]', NULL, 'React compares state by reference, so mutating objects/arrays won’t trigger re-renders.', NULL, ARRAY[]::text[], ARRAY['react','usestate','state','immutability'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q275","original_type":"multiple-choice","topic":"useState","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 277: What happens if you call `useState` conditionally?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('0d51f67b-f3f5-4feb-99bc-0d91305ec4d3', 'What happens if you call `useState` conditionally?', 'React throws a runtime error because hooks must be called in the same order every render.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Conditional hook calls break React’s internal hook order tracking, causing bugs.', NULL, ARRAY[]::text[], ARRAY['react','hooks','conditional','rules-of-hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q276","original_type":"open-ended","topic":"Rules of Hooks","subcategory":"React Hooks","sample_answers":["Calling `useState` inside an `if` statement violates the Rules of Hooks—React can’t match hooks to their state across renders.","Always call hooks at the top level of your component, before any early returns or conditionals."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"756586f8-23f1-4a1b-a75f-ad7914b047c3"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 278: Is useState synchronous or asynchronous?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('919939ae-6853-49b5-9ea2-f88077b80e9c', 'Is useState synchronous or asynchronous?', '`useState` call is sync; state updates are async (batched). Use `useEffect` to observe new state.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'State updates are queued and applied before the next render, so logging state right after `setState` shows the old value.', NULL, ARRAY[]::text[], ARRAY['react','usestate','asynchronous','batching'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q277","original_type":"open-ended","topic":"State Updates","subcategory":"State Management","sample_answers":["`setCount(count+1); console.log(count);` logs the old value. Use `useEffect(() => { console.log(count); }, [count])` to see the new value.","React batches updates for performance, so state changes are not immediate but are applied before the next render."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 279: Can you explain how useState works internally?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('6bb596fe-c4ad-4222-8317-86cbeed28279', 'Can you explain how useState works internally?', 'React maintains a hook list per component. Each `useState` call corresponds to a slot storing state and update queue.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'Hooks rely on a linked list of state slots; order of calls must be consistent across renders.', NULL, ARRAY[]::text[], ARRAY['react','hooks','internal','useState'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q278","original_type":"open-ended","topic":"useState","subcategory":"React Hooks","sample_answers":["React keeps an array of hook states for each component. On first render, `useState` initializes a slot. On updates, it processes the update queue.","The setter function enqueues an update and schedules a re-render. On next render, React applies queued updates in order."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":"ed11a4a3-b4e1-4d70-b69e-4b63e01576c2"}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 280: What is `useReducer`? Why do you use it?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('d1e87f92-9e16-47dc-8c74-8ec277d92156', 'What is `useReducer`? Why do you use it?', '`useReducer` manages complex state logic via actions and a reducer function, similar to Redux.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'It’s ideal for state with multiple sub-values, when next state depends on previous, or for complex update logic.', NULL, ARRAY[]::text[], ARRAY['react','usereducer','state-management','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q279","original_type":"open-ended","topic":"useReducer","subcategory":"React Hooks","sample_answers":["`useReducer` centralizes state update logic in a reducer function, making it easier to test and debug complex state transitions.","Use it for forms, shopping carts, or any state that requires multiple related updates in response to user actions."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 281: How does `useReducer` work? Explain with an example
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('888ab87f-7cf5-40d4-94c9-dccb945931ae', 'How does `useReducer` work? Explain with an example', 'Define a reducer function, call `useReducer(reducer, initialState)`, dispatch actions to update state.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'The reducer takes current state and an action, returns new state. Dispatch sends actions to the reducer.', NULL, ARRAY[]::text[], ARRAY['react','usereducer','example','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q280","original_type":"open-ended","topic":"useReducer","subcategory":"React Hooks","sample_answers":["```javascript\\nfunction reducer(state, action) {\\n  switch(action.type) {\\n    case ''increment'': return { count: state.count + 1 };\\n    default: return state;\\n  }\\n}\\nconst [state, dispatch] = useReducer(reducer, { count: 0 });\\n```","Dispatch with `dispatch({ type: ''increment'' })` to trigger state updates through the reducer."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 282: Can you combine `useReducer` with `useContext`?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('e1549148-5644-435d-a582-ab09b6e60a6f', 'Can you combine `useReducer` with `useContext`?', 'Yes. Wrap `useReducer` in a Context Provider to create a lightweight global state management system.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'This pattern mimics Redux without external libraries—ideal for medium-sized apps.', NULL, ARRAY[]::text[], ARRAY['react','usereducer','usecontext','global-state'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q281","original_type":"true-false","topic":"Context API","subcategory":"State Management","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 283: Can you dispatch multiple actions in a row with useReducer?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('caac0de5-734d-4232-9abe-eeebee6cefaf', 'Can you dispatch multiple actions in a row with useReducer?', 'Yes. Call `dispatch()` multiple times, or create a custom action that performs multiple state changes.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Each `dispatch` queues an update; React processes them in order during the next render.', NULL, ARRAY[]::text[], ARRAY['react','usereducer','dispatch','actions'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q282","original_type":"open-ended","topic":"useReducer","subcategory":"React Hooks","sample_answers":["`dispatch({type:''inc''}); dispatch({type:''inc''});` increments twice. Or define `case ''inc_twice'': return { count: state.count + 2 };`","Multiple dispatches are batched into a single re-render, ensuring consistent state transitions."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 284: How does useContext work? Explain with an example
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('bffd87cd-a56d-4e55-9275-86b6856ab1c6', 'How does useContext work? Explain with an example', 'Create context, provide value via Provider, consume with `useContext` in child components.', 'multiple-choice', 'intermediate', 7, NULL, NULL, 'The example shows auth state shared across components without prop drilling.', NULL, ARRAY[]::text[], ARRAY['react','usecontext','example','authentication'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q283","original_type":"open-ended","topic":"Context API","subcategory":"Context API","sample_answers":["```javascript\\nconst AuthContext = createContext();\\nfunction AuthProvider({children}) {\\n  const [user, setUser] = useState(null);\\n  return <AuthContext.Provider value={{user, login, logout}}>{children}</AuthContext.Provider>;\\n}\\n// In child: const {user, login} = useContext(AuthContext);\\n```","This pattern allows any component to access auth state and methods without passing props through every level."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 285: Can you use multiple contexts in one component?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('9b3b9822-d590-4f07-8519-01ee48827208', 'Can you use multiple contexts in one component?', 'Yes. Call `useContext` once for each context in the same component.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'Components can consume as many contexts as needed by calling `useContext` multiple times.', NULL, ARRAY[]::text[], ARRAY['react','multiple-contexts','usecontext','composition'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q284","original_type":"true-false","topic":"Context API","subcategory":"Context API","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 286: What's a common pitfall when using useContext with objects?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('e24ec306-0a20-4109-86bb-be966b683b27', 'What''s a common pitfall when using useContext with objects?', 'Providing a new object literal as context value causes unnecessary re-renders of all consumers.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'React compares context value by reference; a new object on every render triggers all consumers to re-render.', NULL, ARRAY[]::text[], ARRAY['react','context-pitfall','re-renders','usememo'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q285","original_type":"open-ended","topic":"Context API","subcategory":"Context API","sample_answers":["Avoid `<Provider value={{user, theme}}>`—instead, lift to state or memoize with `useMemo(() => ({user, theme}), [user, theme])`.","Split contexts by concern (e.g., `UserContext`, `ThemeContext`) to isolate re-renders to only affected components."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 287: What would the context value be for no matching provider?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('884ec053-217c-42f7-93e5-2565a026052d', 'What would the context value be for no matching provider?', 'The default value passed to `createContext(defaultValue)` is returned if no Provider is found.', 'multiple-choice', 'intermediate', 5, NULL, NULL, 'This is useful for testing components in isolation without wrapping them in a Provider.', NULL, ARRAY[]::text[], ARRAY['react','context','default-value','provider'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q286","original_type":"open-ended","topic":"Context API","subcategory":"Context API","sample_answers":["`const ThemeContext = createContext(''light'');` means `useContext(ThemeContext)` returns ''light'' if no Provider is present.","If no default is provided, the context value is `undefined` when no Provider exists."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 288: How do reactive dependencies in useEffect affect execution?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('e5066ec6-968b-4d6c-a484-ca210b836e2d', 'How do reactive dependencies in useEffect affect execution?', 'Empty array: runs once. With deps: runs on mount and when deps change. No array: runs every render.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Effect always runs once regardless of deps","isCorrect":false},{"id":"b","text":"[]: once; [deps]: on change; none: every render","isCorrect":true},{"id":"c","text":"Deps are deep-comparison","isCorrect":false},{"id":"d","text":"Effect runs before render","isCorrect":false}]', NULL, 'The dependency array controls when the effect re-runs, based on shallow equality of values.', NULL, ARRAY[]::text[], ARRAY['react','useeffect','dependencies','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q287","original_type":"multiple-choice","topic":"useEffect","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 289: When does React invoke setup and cleanup in useEffect?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('1af99705-b6f6-4894-b445-a84c51236a74', 'When does React invoke setup and cleanup in useEffect?', 'Setup: after mount or when deps change. Cleanup: before re-running effect or on unmount.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Cleanup ensures side effects like subscriptions or timers are properly disposed of.', NULL, ARRAY[]::text[], ARRAY['react','useeffect','cleanup','lifecycle'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q288","original_type":"open-ended","topic":"useEffect","subcategory":"React Hooks","sample_answers":["Setup runs after the component mounts or when dependencies change. Cleanup runs before the next setup or when the component unmounts.","Example: `useEffect(() => { const id = setInterval(...); return () => clearInterval(id); }, [])` cleans up on unmount."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 290: What happens if you return a Promise from useEffect?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('a5b63401-e2de-4fc4-9753-21983b5ad50d', 'What happens if you return a Promise from useEffect?', 'React ignores it. useEffect must return a cleanup function or nothing—not a Promise.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Async functions return Promises, which useEffect doesn’t handle. Do async work inside the effect, not as the effect itself.', NULL, ARRAY[]::text[], ARRAY['react','useeffect','async','promise'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q289","original_type":"open-ended","topic":"useEffect","subcategory":"React Hooks","sample_answers":["Never write `useEffect(async () => { ... })`. Instead, define an async function inside: `useEffect(() => { const fn = async () => { ... }; fn(); }, []);`","Returning a Promise confuses React’s cleanup mechanism and may cause memory leaks or warnings."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 291: Can you have multiple useEffect hooks in a single component?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('11d20ccc-5329-4c8a-8d83-7894e604a2e7', 'Can you have multiple useEffect hooks in a single component?', 'Yes. Multiple useEffect hooks are encouraged to separate unrelated side effects.', 'multiple-choice', 'beginner', 4, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'Separating effects by concern improves readability and maintainability.', NULL, ARRAY[]::text[], ARRAY['react','useeffect','multiple','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q290","original_type":"true-false","topic":"useEffect","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 292: How to prevent infinite loops with useEffect?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('6dabbd63-16b2-484b-bd7d-c44ecf47cd13', 'How to prevent infinite loops with useEffect?', 'Ensure state updates in effect don’t depend on values that trigger the effect, or add guards.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Infinite loops occur when an effect updates state that’s in its dependency array, causing re-runs.', NULL, ARRAY[]::text[], ARRAY['react','useeffect','infinite-loop','dependencies'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q291","original_type":"open-ended","topic":"useEffect","subcategory":"React Hooks","sample_answers":["If `useEffect(() => { setCount(count + 1); }, [count]);`, it loops. Fix by removing `count` from deps if update is unconditional, or use updater function.","Add a condition: `if (count < 5) setCount(count + 1);` to break the cycle."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 293: What are the use cases of useLayoutEffect?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('11fbe50a-ff06-4e65-b51c-8e15dc01b070', 'What are the use cases of useLayoutEffect?', 'Reading layout (size/position), preventing visual flicker, synchronously updating DOM before paint.', 'multiple-choice', 'advanced', 7, '[{"id":"a","text":"Data fetching and logging","isCorrect":false},{"id":"b","text":"Layout measurements, preventing flicker, synchronous DOM updates","isCorrect":true},{"id":"c","text":"Replacing all useEffect calls","isCorrect":false},{"id":"d","text":"Only for server-side rendering","isCorrect":false}]', NULL, 'It runs synchronously before paint, so use only when necessary to avoid blocking visual updates.', NULL, ARRAY[]::text[], ARRAY['react','uselayouteffect','layout','flicker'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q292","original_type":"multiple-choice","topic":"useLayoutEffect","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 294: How does useLayoutEffect work during SSR?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('022c7429-0ddb-4217-9b29-54765a5f4e39', 'How does useLayoutEffect work during SSR?', 'It doesn’t run on the server. Use a polyfill like `useIsomorphicLayoutEffect` for SSR compatibility.', 'multiple-choice', 'advanced', 7, NULL, NULL, 'Since there’s no DOM on the server, `useLayoutEffect` is skipped, but React warns in SSR environments.', NULL, ARRAY[]::text[], ARRAY['react','ssr','uselayouteffect','nextjs'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q293","original_type":"open-ended","topic":"useLayoutEffect","subcategory":"Server-Side Rendering","sample_answers":["`const useIsomorphicLayoutEffect = typeof window !== ''undefined'' ? useLayoutEffect : useEffect;` avoids SSR warnings.","In Next.js, always use this pattern for components that render on both server and client."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 295: What happens if you use useLayoutEffect for non-layout logic?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('22345063-ce21-4da3-bcda-1db832b4824f', 'What happens if you use useLayoutEffect for non-layout logic?', 'It degrades performance by blocking the browser paint for non-visual work like data fetching or logging.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Reserve `useLayoutEffect` for layout-critical code; use `useEffect` for everything else.', NULL, ARRAY[]::text[], ARRAY['react','uselayouteffect','performance','anti-pattern'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q294","original_type":"open-ended","topic":"useLayoutEffect","subcategory":"React Hooks","sample_answers":["Using `useLayoutEffect` for analytics or API calls delays visual updates unnecessarily. Use `useEffect` instead.","It should only be used when you need to measure or adjust the DOM before the user sees it."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 296: How does useLayoutEffect cause layout thrashing?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('5a0a899d-9fde-4952-a159-6f0fcb720264', 'How does useLayoutEffect cause layout thrashing?', 'Repeated read-write DOM operations in `useLayoutEffect` force synchronous reflows, blocking rendering.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'Each DOM read after a write triggers a reflow; doing this in `useLayoutEffect` blocks the main thread.', NULL, ARRAY[]::text[], ARRAY['react','layout-thrashing','uselayouteffect','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q295","original_type":"open-ended","topic":"Layout Thrashing","subcategory":"Performance Optimization","sample_answers":["Avoid `const h = el.offsetHeight; el.style.height = h + 10; const newH = el.offsetHeight;` in `useLayoutEffect`—batch reads and writes.","Read all layout values first, compute changes, then apply all writes in one go to minimize reflows."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 297: How to use useRef to access a DOM element?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('5e1639f8-dc9f-448f-ab62-ff658ccf7fd0', 'How to use useRef to access a DOM element?', 'Create a ref with `useRef()`, attach to DOM element via `ref` prop, access via `ref.current`.', 'multiple-choice', 'beginner', 5, NULL, NULL, 'Refs provide a way to imperatively interact with DOM elements, like focusing an input.', NULL, ARRAY[]::text[], ARRAY['react','useref','dom','focus'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q296","original_type":"open-ended","topic":"useRef","subcategory":"React Hooks","sample_answers":["```javascript\\nconst inputRef = useRef();\\nreturn <input ref={inputRef} />;\\n// Focus: inputRef.current.focus();\\n```","Refs are the standard way to access DOM nodes in React without query selectors."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 298: Can you use useRef to persist values across renders?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('9b111435-ed50-454d-a7a9-238fb1998287', 'Can you use useRef to persist values across renders?', 'Yes. `useRef` holds a mutable `.current` value that persists across renders without causing re-renders.', 'multiple-choice', 'intermediate', 5, '[{"id":"a","text":"True","isCorrect":true},{"id":"b","text":"False","isCorrect":false}]', NULL, 'Unlike `useState`, updating `ref.current` doesn’t trigger a re-render, making it ideal for counters or timers.', NULL, ARRAY[]::text[], ARRAY['react','useref','persistence','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q297","original_type":"true-false","topic":"useRef","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 299: Can useRef be used to store previous values?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('4ab6262f-ccd2-4419-9113-e7236bb715f0', 'Can useRef be used to store previous values?', 'Yes. Update ref in `useEffect` to store previous props or state for comparison.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'This pattern is common for detecting changes between renders without causing extra re-renders.', NULL, ARRAY[]::text[], ARRAY['react','useref','previous-value','hooks'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q298","original_type":"open-ended","topic":"useRef","subcategory":"React Hooks","sample_answers":["```javascript\\nconst prevCountRef = useRef();\\nuseEffect(() => { prevCountRef.current = count; }, [count]);\\nconst prevCount = prevCountRef.current;\\n```","This lets you compare current and previous values in render without storing them in state."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 300: Is it possible to access a ref in the render method?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('70c80b89-3f9d-4907-8727-d14056977d02', 'Is it possible to access a ref in the render method?', 'Yes, but `ref.current` is `null` during initial render until after the component mounts.', 'multiple-choice', 'intermediate', 5, NULL, NULL, 'Refs are populated after the component mounts, so accessing them in render is usually not useful.', NULL, ARRAY[]::text[], ARRAY['react','useref','render','dom'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q299","original_type":"open-ended","topic":"useRef","subcategory":"React Hooks","sample_answers":["`console.log(ref.current)` in render logs `null` on first render. Use `useEffect` to access the DOM node after mount.","Refs should be used in event handlers or effects, not in the render method itself."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 301: What are common use cases of useRef hook?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('70f778cb-f483-4315-b60e-ac5ac9a2cf51', 'What are common use cases of useRef hook?', 'Focus management, scroll control, DOM measurements, media playback, and integrating with non-React libraries.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"Managing application state","isCorrect":false},{"id":"b","text":"Focus, scroll, measurements, media, and third-party libraries","isCorrect":true},{"id":"c","text":"Replacing useState","isCorrect":false},{"id":"d","text":"Only for class components","isCorrect":false}]', NULL, 'Refs enable imperative interactions with DOM elements that aren’t possible with declarative React alone.', NULL, ARRAY[]::text[], ARRAY['react','useref','dom','imperative'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q300","original_type":"multiple-choice","topic":"useRef","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 302: What is useImperativeHandle Hook? Give an example.
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('61a379c6-447d-4f93-a547-041fd4ffeee1', 'What is useImperativeHandle Hook? Give an example.', 'Exposes custom methods from child to parent via ref, used with `forwardRef` for modals, inputs, etc.', 'multiple-choice', 'advanced', 8, NULL, NULL, 'It lets parents control child behavior imperatively while hiding internal implementation.', NULL, ARRAY[]::text[], ARRAY['react','useimperativehandle','forwardref','imperative'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q301","original_type":"open-ended","topic":"useImperativeHandle","subcategory":"React Hooks","sample_answers":["```javascript\\nconst Dialog = forwardRef((props, ref) => {\\n  const [open, setOpen] = useState(false);\\n  useImperativeHandle(ref, () => ({ open: () => setOpen(true) }));\\n  return open ? <div>Dialog</div> : null;\\n});\\n// Parent: dialogRef.current.open();\\n```","This pattern is essential for reusable component libraries that need to expose controlled APIs."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 303: When should you use useImperativeHandle?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('87f693e3-56a1-4803-a289-00d407cd06e7', 'When should you use useImperativeHandle?', 'For reusable components that need to expose imperative methods (modals, inputs, scroll containers).', 'multiple-choice', 'advanced', 7, '[{"id":"a","text":"For all state management","isCorrect":false},{"id":"b","text":"For modals, inputs, scroll containers in reusable libraries","isCorrect":true},{"id":"c","text":"To replace useEffect","isCorrect":false},{"id":"d","text":"Only in class components","isCorrect":false}]', NULL, 'It’s for when you need to give parents controlled access to child behavior without exposing internals.', NULL, ARRAY[]::text[], ARRAY['react','useimperativehandle','component-libraries','api'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q302","original_type":"multiple-choice","topic":"useImperativeHandle","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 304: Is it possible to use useImperativeHandle without forwardRef?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('d8d41acd-7f47-4257-91dc-15852db443b5', 'Is it possible to use useImperativeHandle without forwardRef?', 'No. `useImperativeHandle` only works when the component is wrapped in `forwardRef`.', 'multiple-choice', 'intermediate', 6, '[{"id":"a","text":"True","isCorrect":false},{"id":"b","text":"False","isCorrect":true}]', NULL, '`forwardRef` is required to pass the ref from parent to child so `useImperativeHandle` can attach methods to it.', NULL, ARRAY[]::text[], ARRAY['react','useimperativehandle','forwardref','refs'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q303","original_type":"true-false","topic":"useImperativeHandle","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 305: How is useMemo different from useCallback?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('19d45c25-ec37-4e08-88fb-6092bdf87412', 'How is useMemo different from useCallback?', '`useMemo` memoizes values; `useCallback` memoizes functions.', 'multiple-choice', 'intermediate', 7, '[{"id":"a","text":"useMemo is for functions; useCallback for values","isCorrect":false},{"id":"b","text":"useMemo memoizes values; useCallback memoizes functions","isCorrect":true},{"id":"c","text":"They are identical","isCorrect":false},{"id":"d","text":"useCallback prevents re-renders of parent","isCorrect":false}]', NULL, '`useCallback(fn, deps)` is equivalent to `useMemo(() => fn, deps)`.', NULL, ARRAY[]::text[], ARRAY['react','usememo','usecallback','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q304","original_type":"multiple-choice","topic":"useMemo vs useCallback","subcategory":"React Hooks","sample_answers":[],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 306: Does useMemo prevent re-rendering of child components?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('ec5f276a-3519-45ed-9f78-b5d001d59ed8', 'Does useMemo prevent re-rendering of child components?', 'Not directly. But when combined with `React.memo`, it prevents re-renders by preserving prop reference equality.', 'multiple-choice', 'advanced', 7, NULL, NULL, '`useMemo` ensures the value doesn’t change between renders, so `React.memo` can skip child re-renders.', NULL, ARRAY[]::text[], ARRAY['react','usememo','react-memo','performance'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q305","original_type":"open-ended","topic":"useMemo","subcategory":"Performance Optimization","sample_answers":["`const data = useMemo(() => compute(data), [data]);` passed to `<Child data={data} />` where `Child = React.memo(...)` prevents re-renders if `data` hasn’t changed.","Alone, `useMemo` only optimizes the parent’s computation; with `React.memo`, it optimizes child rendering too."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');

-- Question 307: What is `useCallback` and why is it used?
INSERT INTO questions (id, title, content, type, difficulty, points, options, correct_answer, explanation, test_cases, hints, tags, stats, metadata, category_id, is_active, created_at, updated_at) VALUES ('977f01c9-5d0d-43a0-8556-58cdaf9ac23e', 'What is `useCallback` and why is it used?', '`useCallback` memoizes function references to prevent unnecessary re-renders of child components.', 'multiple-choice', 'intermediate', 6, NULL, NULL, 'Without it, new function instances on every render cause `React.memo` children to re-render unnecessarily.', NULL, ARRAY[]::text[], ARRAY['react','usecallback','performance','react-memo'], '{"views":0,"attempts":0,"correct_attempts":0,"average_time":0}', '{"original_id":"q306","original_type":"open-ended","topic":"useCallback","subcategory":"React Hooks","sample_answers":["`const handleClick = useCallback(() => { ... }, []);` ensures the function reference stays the same across renders.","Pass `handleClick` to a `React.memo` child to prevent it from re-rendering when parent state changes but the callback hasn’t."],"time_limit":120,"related_links":[],"created_by":"admin","updated_by":"system","topic_id":null}', 'ca612436-af48-4e3f-a7fd-f9a9b2c52374', true, '2024-01-01T00:00:00.000Z', '2024-01-01T00:00:00.000Z');
